        generateData: ($container, schemaKey) => {
            const schema = FormSchemas[schemaKey];
            let requestText = schema.templateHeader;
            let outputTemplateParts = [];
            let hasContent = false;

            $container.find('.divine-form-group').each(function() {
                const label = $(this).data('label');
                const desc = $(this).data('desc');
                const type = $(this).data('type');
                const format = $(this).data('format');
                let blockContent = '';

                if (type === 'text') blockContent = $(this).find('input').val().trim();
                else if (type === 'textarea') blockContent = $(this).find('textarea').val().trim();
                else if (type === 'array') {
                    const $items = $(this).find('.divine-array-item');
                    if ($items.length > 0) {
                        const itemLabel = $(this).find('.divine-array-container').data('item-label');
                        $items.each(function(i) {
                            let itemStr = `\n  â€¢ ${itemLabel} ${i + 1}: `;
                            let subParts = [];
                            $(this).find('input').each(function() {
                                const val = $(this).val().trim();
                                const subLabel = $(this).prev().text();
                                if (val) subParts.push(`[${subLabel}: ${val}]`);
                            });
                            if (subParts.length > 0) itemStr += subParts.join(' | ');
                            blockContent += itemStr;
                        });
                    }
                }

                if (blockContent) {
                    hasContent = true;
                    requestText += `\nã€${label}ã€‘\nè¯´æ˜ï¼š${desc}\nç¥è°•ï¼š${blockContent}\n`;
                    if (format) outputTemplateParts.push(format);
                }
