                (window.SillyTavern?.callGenericPopup || window.callGenericPopup)($wrap, 1, "", {okButton:false});
            }
            Logic.showPage('main', $wrap.find('.divine-scroll-content'), popup);
        } catch(e) { console.error(e); alert('Error: '+e.message); }
    };

    const register = () => {
        if (typeof appendInexistentScriptButtons === 'function') {
            appendInexistentScriptButtons([{ name: BTN_NAME, visible: true }]);
            eventOn(getButtonEvent(BTN_NAME), initUI);
        } else {
            const $qr = $('#quick-reply-container, .quickReplyContainer');
            if ($qr.length && !$('#divine-backup-btn').length) {
                $qr.prepend(`<div id="divine-backup-btn" style="cursor:pointer;display:inline-block;padding:5px;border:1px solid #9370DB;margin:2px;color:#d4af37;">${BTN_NAME}</div>`);
                $('#divine-backup-btn').on('click', initUI);
            }
        }
    };
    setTimeout(register, 1200);
})();

