<script>
    (function() {
        const script = document.currentScript;
        const root = script.previousElementSibling;
        const raw = script.previousElementSibling.previousElementSibling;

        const DATA = {
            html: raw.innerHTML,
            dateStr: raw.dataset.date,
            day: raw.dataset.day,
            timeStr: raw.dataset.time,
            loc: raw.dataset.location,
            season: raw.dataset.season
        };

        // éŸ³æ•ˆåº“
        const AMBIENCE = {
            snow: 'https://files.catbox.moe/k1pfd0.mp3',
            beach: 'https://files.catbox.moe/huj46s.mp3',
            rain: 'https://files.catbox.moe/joid3d.mp3',
            forest: 'https://files.catbox.moe/a2f7ka.mp3',
            home: 'https://files.catbox.moe/t45sd6.mp3',
            public: 'https://files.catbox.moe/hfplj1.mp3'
        };

        let audioEl;
        let audioBtn;

        function init() {
            renderTimeAndScene();
            renderContent();

            // è·å–ä¸Šä¸‹æ–‡æ–‡æœ¬ç”¨äºæ£€æµ‹å¤©æ°”å’ŒéŸ³æ•ˆ
            let contextText = "";
            const parentMsg = root.closest('.mes_text');
            if (parentMsg) contextText = parentMsg.innerText;
            else contextText = root.parentElement ? root.parentElement.innerText : "";

            detectWeather(contextText);
            detectAudio(contextText);
        }

        // --- ç»Ÿä¸€ç¬”è®°æ ·å¼ ---
        function renderContent() {
            const target = root.querySelector('#content-area');
            let text = DATA.html.replace(/<[^>]*>/g, '').trim();
            const lines = text.split('\n');
            let html = '';

            let currentCard = null;

            function flushCard() {
                if (currentCard) {
                    html += `<div class="char-card">
                                <div class="char-card-header"><div class="char-name">${currentCard.name}</div></div>
                                ${currentCard.stats ? `<div class="stats-list">${currentCard.stats}</div>` : ''}
                                ${currentCard.notes ? `<div style="padding:5px 15px 15px;">${currentCard.notes}</div>` : ''}
                             </div>`;
                    currentCard = null;
                }
            }

            lines.forEach(line => {
                line = line.trim();
                if(!line) return;

                // 1. ç« èŠ‚æ ‡é¢˜
                if (line.includes('â€”') && line.includes('â–«ï¸')) {
                    flushCard();
                    html += `<div class="section-title">${line.replace(/[â€”â–«ï¸]/g, '')}</div>`;
                }
                // 2. è§’è‰²å
                else if (line.startsWith('**') && line.includes('**')) {
                    flushCard();
                    const name = line.replace(/\*\*/g, '').replace(/[:ï¼š]/g, '');
                    currentCard = { name: name, stats: '', notes: '' };
                }
                // 3. åˆ—è¡¨é¡¹
                else if ((line.startsWith('-') || line.startsWith('â€¢'))) {
                    const content = line.substring(1).trim();

                    let isStat = false;
                    let key = '', val = '';

                    if (!content.startsWith('[') && content.match(/[:ï¼š]/)) {
                        const parts = content.split(/[:ï¼š]/);
                        const potentialKey = parts[0].trim();
                        if (potentialKey.length < 15) {
                            isStat = true;
                            key = potentialKey;
                            val = parts.slice(1).join(':').trim();
                        }
                    }

                    if (isStat) {
                        const statHtml = `<div class="stat-item"><div class="stat-key">${key}</div><div class="stat-val">${val}</div></div>`;
                        if (currentCard) currentCard.stats += statHtml;
                        else html += `<div class="char-card"><div class="stats-list">${statHtml}</div></div>`;
                    } else {
                        // ç»Ÿä¸€ä½¿ç”¨é‡‘è‰²å›¾æ ‡
                        const noteHtml = `<div class="note-block"><span class="bullet-point">â–</span>${content}</div>`;
                        if (currentCard) currentCard.notes += noteHtml;
                        else html += noteHtml;
                    }
                }
                // 4. æ™®é€šæ–‡æœ¬
                else {
                    if (!line.includes('details') && !line.includes('summary')) {
                        const noteHtml = `<div class="note-block"><span class="bullet-point">â–</span>${line}</div>`;
                        if (currentCard) currentCard.notes += noteHtml;
                        else html += noteHtml;
                    }
                }
            });
            flushCard();
            target.innerHTML = html;
        }

        // --- éŸ³æ•ˆæ£€æµ‹ä¸æ’­æ”¾ ---
        function detectAudio(contextText) {
            audioEl = root.querySelector('#bg-audio');
            audioBtn = root.querySelector('#audio-btn');
            const searchText = (DATA.loc + " " + DATA.season + " " + contextText).toLowerCase();

            let src = null;

            // ä¼˜å…ˆçº§åˆ¤å®š
            if (searchText.match(/é›ª|æš´é›ª|å†°|éœœ|winter|snow/)) {
                src = AMBIENCE.snow;
            } else if (searchText.match(/é›¨|æš´é›¨|æ·‹|wet|rain/)) {
                src = AMBIENCE.rain;
            } else if (searchText.match(/æµ·|æ»©|å²¸|æ¸¯|èˆ¹|beach|sea|ocean/)) {
                src = AMBIENCE.beach;
            } else if (searchText.match(/é¤|é¦†|è¡—|å¸‚|å•†|å§|åº—|public|restaurant|mall|bar/)) {
                src = AMBIENCE.public;
            } else if (searchText.match(/æ£®|æ—|å›­|å…¬å›­|é‡å¤–|forest|park|garden/)) {
                src = AMBIENCE.forest;
            } else if (searchText.match(/å®¶|å®…|å¯“|å…|ç‚‰|æˆ¿|å®¤|home|house|room|fireplace/)) {
                src = AMBIENCE.home;
            }

            if (src) {
                audioEl.src = src;
                audioEl.volume = 0.5;
                audioEl.play().then(() => {
                    audioBtn.classList.add('is-playing');
                    audioBtn.innerText = 'ğŸ”Š';
                }).catch(e => {
                    console.log("Auto-play prevented");
                    audioBtn.innerText = 'ğŸ”ˆ';
                });
            } else {
                audioBtn.style.display = 'none';
            }
        }

        window.toggleAudio = function(e) {
            e.stopPropagation();
            const btn = e.target;
            const audio = btn.closest('.hud-header').parentElement.parentElement.querySelector('#bg-audio');

            if (audio.paused) {
                audio.play();
                btn.classList.add('is-playing');
                btn.innerText = 'ğŸ”Š';
            } else {
                audio.pause();
                btn.classList.remove('is-playing');
                btn.innerText = 'ğŸ”ˆ';
            }
        };

        // --- åœºæ™¯æ¸²æŸ“ï¼ˆä¿æŒåŸæœ‰é€»è¾‘ä¸å˜ï¼‰---
        function renderTimeAndScene() {
            const tm = DATA.timeStr.match(/(\d{1,2})[:ï¼š](\d{2})/);
            let h = 12, m = 0;
            if (tm) { h = parseInt(tm[1]); m = parseInt(tm[2]); }

            root.querySelector('#disp-time').innerText = `${h.toString().padStart(2,'0')}:${m.toString().padStart(2,'0')}`;

            const isDay = (h >= 6 && h < 18);
            const celestial = root.querySelector('#celestial-body');
            const sky = root.querySelector('#sky');
            const stars = root.querySelector('#stars');
            const clouds = root.querySelector('#clouds');

            let bg = '', border = '';
            if (h >= 5 && h < 7) { 
                bg = 'linear-gradient(180deg, #4B2C45 0%, #6A4C63 100%)'; 
                border = 'rgba(255, 200, 150, 0.3)';
            } else if (h >= 7 && h < 17) { 
                bg = 'linear-gradient(180deg, #4B79A1 0%, #283E51 100%)'; 
                border = 'rgba(255, 255, 255, 0.15)';
            } else if (h >= 17 && h < 19) { 
                bg = 'linear-gradient(180deg, #2C3E50 0%, #FD746C 100%)'; 
                border = 'rgba(255, 160, 122, 0.4)';
            } else { 
                bg = 'linear-gradient(180deg, #0F2027 0%, #203A43 100%)'; 
                border = 'rgba(100, 149, 237, 0.2)';
            }
            sky.style.background = bg;
            root.style.borderColor = border;

            celestial.innerHTML = '';
            if (isDay) {
                celestial.className = 'the-sun';
                stars.style.opacity = 0;
                clouds.style.opacity = 1; clouds.style.filter = 'brightness(1)';
            } else {
                celestial.className = 'the-moon';
                stars.style.opacity = 1;
                createStars(stars);
                const moonAge = calculateMoonAge(DATA.dateStr);
                renderMoonPhase(celestial, moonAge);
                clouds.style.opacity = 0.4; clouds.style.filter = 'brightness(0.7)';
            }

            let percentage = 0;
            if (isDay) {
                const totalMinutes = 12 * 60; 
                const currentMinutes = (h - 6) * 60 + m;
                percentage = currentMinutes / totalMinutes;
            } else {
                let currentMinutes = 0;
                if (h >= 18) currentMinutes = (h - 18) * 60 + m;
                else currentMinutes = (h + 6) * 60 + m;
                percentage = currentMinutes / (12 * 60);
            }
            percentage = Math.max(0, Math.min(1, percentage));

            const posX = percentage * 100;
            const parabola = 4 * Math.pow(percentage - 0.5, 2); 
            const topPos = 10 + (parabola * 90); 
            celestial.style.left = `${posX}%`;
            celestial.style.top = `${topPos}%`;
        }

        function calculateMoonAge(dateString) {
            const match = dateString.match(/(\d{4}).*?(\d{1,2}).*?(\d{1,2})/);
            if (!match) return 15;
            const y = parseInt(match[1]), m = parseInt(match[2]), d = parseInt(match[3]);
            const date = new Date(y, m - 1, d);
            const baseDate = new Date(2000, 0, 6);
            const diffDays = (date.getTime() - baseDate.getTime()) / (1000 * 3600 * 24);
            const synodicMonth = 29.53058867;
            let age = diffDays % synodicMonth;
            if (age < 0) age += synodicMonth;
            return age;
        }

        function renderMoonPhase(moonEl, age) {
            let color = 'rgba(22, 34, 42, 0.95)'; 
            if (age < 3 || age > 27) moonEl.style.boxShadow = `inset 0 0 0 50px ${color}`;
            else if (age >= 3 && age < 12) {
                let offset = 36 - ((age - 3) * 4); 
                moonEl.style.boxShadow = `inset ${offset}px 0 8px 0 ${color}`;
            }
            else if (age >= 12 && age < 18) {
                moonEl.style.boxShadow = `inset 0 0 0 0 ${color}, 0 0 15px rgba(255,255,255,0.5)`;
                moonEl.style.background = '#fff';
            }
            else {
                let offset = -1 * (36 - ((27 - age) * 4));
                moonEl.style.boxShadow = `inset ${offset}px 0 8px 0 ${color}`;
            }
        }

        function detectWeather(contextText) {
            const weatherLayer = root.querySelector('#weather-layer');
            const fog = root.querySelector('#fog');
            const season = DATA.season;

            let type = 'clear';
            if (contextText.match(/é›ª|æš´é›ª|å†°|éœœ/)) type = 'snow';
            else if (contextText.match(/é›¨|æš´é›¨|æ·‹|æ¹¿/)) type = 'rain';
            else if (contextText.match(/é£|ç‹‚é£|å•¸/)) type = 'wind';
            else if (season.includes('å†¬') && !contextText.match(/æ™´|é˜³/)) type = 'snow';

            if (contextText.match(/é›¾|é˜´|éœ¾|æš—/)) fog.style.opacity = 0.6;
            else fog.style.opacity = 0;

            if (type === 'clear') return;

            if (type === 'wind') createParticles(weatherLayer, 'p-wind', 15);
            else if (type === 'snow') createParticles(weatherLayer, 'p-snow', 40);
            else if (type === 'rain') {
                createParticles(weatherLayer, 'p-rain', 50);
                root.querySelector('#sky').style.filter = 'grayscale(0.8) brightness(0.6)';
            }
        }

        function createParticles(container, cssClass, count) {
            for(let i=0; i<count; i++) {
                const p = document.createElement('div');
                p.className = `particle ${cssClass}`;
                p.style.left = Math.random() * 100 + '%';
                if (cssClass === 'p-wind') {
                    p.style.top = Math.random() * 100 + '%';
                    p.style.width = (Math.random() * 100 + 50) + 'px';
                    p.style.animation = `drift ${Math.random()+0.5}s linear infinite`;
                } else {
                    p.style.top = -10 + '%';
                    if (cssClass === 'p-snow') {
                        const size = Math.random() * 3 + 2;
                        p.style.width = size + 'px'; p.style.height = size + 'px';
                        p.style.animation = `fall ${Math.random()*3 + 3}s linear infinite`;
                    } else {
                         p.style.animation = `fall ${Math.random()*0.5 + 0.3}s linear infinite`;
                    }
                }
                p.style.animationDelay = -Math.random() * 5 + 's';
                container.appendChild(p);
            }
        }

        function createStars(container) {
            if(container.querySelectorAll('.star').length > 0) return; 
            for(let i=0; i<40; i++) {
                const s = document.createElement('div');
                s.className = 'star';
                s.style.left = Math.random() * 100 + '%';
                s.style.top = Math.random() * 85 + '%';
                const sz = Math.random() * 2 + 1;
                s.style.width = sz + 'px'; s.style.height = sz + 'px';
                s.style.setProperty('--dur', (Math.random()*2 + 1) + 's');
                container.appendChild(s);
            }
        }

        window.toggleHUD = function(el) {
            el.closest('.prism-hud').classList.toggle('expanded');
        };

        setTimeout(init, 50);
    })();
