        display: block; font-size: 2em; line-height: 0.5;
        color: rgba(255,255,255,0.3); margin-bottom: 20px;
    }
    .style-postcard .front-caption {
        height: 50px;
        display: flex; justify-content: center; align-items: center;
        font-family: 'JetBrains Mono', monospace; font-size: 0.85em;
        color: #666; letter-spacing: 1px; text-transform: uppercase;
        margin-top: 5px;
    }

    /* 背面样式 */
    .style-postcard .card-back {
        transform: rotateY(180deg);
        background-color: #f4f1ea;
        background-image: radial-gradient(#d0c8b0 1px, transparent 1px);
        background-size: 20px 20px;
        padding: 30px 25px 40px 25px;
        display: flex; flex-direction: column; align-items: center;
    }
    .style-postcard .stamp-box {
        align-self: flex-end; width: 60px; height: 70px; background: #fff;
        border: 1px solid #ddd; padding: 4px; box-shadow: 1px 1px 3px rgba(0,0,0,0.1);
        background-image: radial-gradient(transparent 50%, #f4f1ea 50%);
        background-size: 10px 10px; background-position: -5px -5px;
        display: flex; justify-content: center; align-items: center; margin-bottom: 10px;
    }
    .style-postcard .stamp-inner {
        width: 100%; height: 100%; background: #2c3e50; color: #fff;
        display: flex; justify-content: center; align-items: center;
        font-size: 0.6em; font-family: 'Cinzel', serif;
    }
    .style-postcard .postmark-circle {
        position: absolute; top: 30px; right: 75px;
        width: 60px; height: 60px; border: 2px solid rgba(50,50,50,0.3);
        border-radius: 50%; color: rgba(50,50,50,0.3);
        display: flex; justify-content: center; align-items: center; text-align: center;
        font-family: monospace; font-size: 0.6em; line-height: 1;
        transform: rotate(-15deg); mix-blend-mode: multiply; pointer-events: none;
    }
    .style-postcard .writing-lines {
        flex: 1; 
        width: 100%; 
        margin: 20px 0 10px 0;
        background-image: linear-gradient(to bottom, transparent 29px, #a3b8cc 30px);
        background-size: 100% 30px; 
        display: flex;
        flex-direction: column;
        justify-content: flex-start; 
        align-items: center;      
        overflow: hidden;         
    }

    .style-postcard .sender-signature {
        font-family: 'Dancing Script', cursive;
        font-size: 1.1em; 
        color: #2c3e50;
        line-height: 30px; 
        width: 88%;      
        text-align: left; 
        white-space: pre-wrap; 
        margin-top: 5px;   
        opacity: 0.9;
    }
    .style-postcard .sender-signature {
        font-family: 'Dancing Script', cursive; font-size: 1.2em;
        color: #2c3e50; transform: none; line-height: 30px;
        margin-bottom: 2px; opacity: 0.8;
    }
    .style-postcard .air-mail-strip {
        position: absolute; bottom: 0; left: 0; width: 100%; height: 8px;
        background: repeating-linear-gradient(45deg, #e74c3c, #e74c3c 10px, #fff 10px, #fff 20px, #3498db 20px, #3498db 30px, #fff 30px, #fff 40px);
    }


    /* =================================================================== */
    /* --- 模式四：神圣拱门 (Fortune - Final Version) --- */
    /* =================================================================== */

    .style-fortune .card-content-wrapper {
        position: relative;
        width: 100%;
        max-width: 320px;
        background: transparent;
        filter: drop-shadow(0 0 3px rgba(255, 215, 0, 0.25)) drop-shadow(0 6px 10px rgba(0,0,0,0.2));
        display: flex; justify-content: center; align-items: center;
        animation: hoverFloat 6s ease-in-out infinite;
        padding-top: 10px;
    }

    .style-fortune .arch-frame {
        position: relative; width: 100%; min-height: 200px; 
        border-radius: 160px 160px 12px 12px;
        background: radial-gradient(circle at 50% 30%, #203a6e, #101b3b 60%, #080c18 100%);
        overflow: hidden;
        border: 4px solid #b8860b;
        box-shadow: inset 0 0 0 3px #080c18, inset 0 0 0 5px #ffd700;
    }

    .style-fortune .arch-frame::after {
        content: ''; position: absolute; top: 0; left: 0; width: 100%; height: 100%;
        border-radius: 160px 160px 12px 12px;
        border: 4px solid rgba(255, 215, 0, 0.4);
        mask-image: linear-gradient(45deg, transparent 40%, black 50%, transparent 60%);
        -webkit-mask-image: linear-gradient(45deg, transparent 40%, black 50%, transparent 60%);
        mask-size: 200%; -webkit-mask-size: 200%;
        animation: shineBorder 4s infinite linear; pointer-events: none; z-index: 5;
    }

    .style-fortune .stars-bg { position: absolute; top: 0; left: 0; width: 100%; height: 100%; z-index: 0; pointer-events: none; }
    .style-fortune .zodiac-ring {
        position: absolute; top: 35%; left: 50%; width: 400px; height: 400px;
        background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 100 100' xmlns='http://www.w3.org/2000/svg'%3E%3Ccircle cx='50' cy='50' r='49' fill='none' stroke='rgba(255,215,0,0.08)' stroke-width='0.5' stroke-dasharray='2'/%3E%3Ccircle cx='50' cy='50' r='35' fill='none' stroke='rgba(255,215,0,0.05)' stroke-width='2'/%3E%3Cpath d='M50 0 L50 100 M0 50 L100 50' stroke='rgba(255,215,0,0.05)' stroke-width='0.5'/%3E%3C/svg%3E");
        transform: translate(-50%, -50%);
        animation: rotateZodiac 120s linear infinite;
    }
    .style-fortune .stardust {
        position: absolute; background: #fff; border-radius: 50%;
        box-shadow: 0 0 4px #fff, 0 0 8px rgba(255, 215, 0, 0.5);
        opacity: 0; animation: twinkle 3s infinite ease-in-out;
    }

    .style-fortune .content-layout {
        position: relative; z-index: 2;
        display: flex; flex-direction: column; align-items: center; text-align: center;
        padding: 60px 30px 40px 30px; height: 100%; color: #e0d2b4;
    }

    .style-fortune .top-icon {
        font-size: 2em; color: #ffd700; margin-bottom: 20px; opacity: 0.9;
        filter: drop-shadow(0 0 10px rgba(255, 215, 0, 0.6));
    }

    .style-fortune .arch-num {
        font-family: 'Cinzel', serif; font-size: 0.9em; letter-spacing: 2px; color: #8a9bbd; margin-bottom: 5px;
    }

    .style-fortune .arch-title {
        font-family: 'Cinzel', serif; font-size: 1.6em; font-weight: 700; line-height: 1.2; text-transform: uppercase; margin-bottom: 25px;
        background: linear-gradient(to bottom, #fff, #ffd700); -webkit-background-clip: text; -webkit-text-fill-color: transparent;
        filter: drop-shadow(0 2px 4px rgba(0,0,0,0.5));
    }

    .style-fortune .arch-text-area {
        margin-top: auto; width: 100%;
        background: rgba(0, 0, 0, 0.4);
        border-top: 1px solid rgba(255, 215, 0, 0.3);
        padding: 20px 15px; border-radius: 8px;
        backdrop-filter: blur(4px);
        display: flex; flex-direction: column; gap: 12px;
    }

    .style-fortune .keywords {
        font-size: 0.8em; color: #81cdf8; letter-spacing: 1px; text-transform: uppercase; font-weight: 700;
    }

    .style-fortune .description {
        font-family: 'Noto Serif SC', serif; font-size: 0.95em; line-height: 1.6; color: #dce5f2;
        text-align: justify;
    }

    /* 新增：角色评论样式 */
    .style-fortune .char-comment {
        font-family: 'Noto Serif SC', serif;
        font-size: 0.85em;
        line-height: 1.5;
        color: #e6c288; 
        border-top: 1px dashed rgba(255, 215, 0, 0.3); 
        padding-top: 10px;
        margin-top: 5px;
        font-style: italic;
        opacity: 0.9;
        text-align: left;
    }

    @keyframes hoverFloat { 0%, 100% { transform: translateY(0); } 50% { transform: translateY(-10px); } }
    @keyframes rotateZodiac { from { transform: translate(-50%, -50%) rotate(0deg); } to { transform: translate(-50%, -50%) rotate(360deg); } }
    @keyframes shineBorder { 0% { -webkit-mask-position: 200%; } 100% { -webkit-mask-position: -200%; } }
    @keyframes twinkle { 0%, 100% { opacity: 0; transform: scale(0.2); } 50% { opacity: 1; transform: scale(1.2); } }
</style>
</head>
<body>

<div class="reverie-container" id="reverieCard"></div>

<div id="raw-data" style="display: none;">
$1
</div>

<script>
    document.addEventListener('DOMContentLoaded', () => {
        // --- 1. 数据获取与预处理 ---
        const cardContainer = document.getElementById('reverieCard');
        const rawData = document.getElementById('raw-data');
        if (!cardContainer || !rawData) return;

        const rawText = rawData.textContent.trim();

        const activeModeMatch = rawText.match(/^(?:模式[:：])?(.*)\n([\s\S]*)$/);

        let mode = 'excerpt'; 
        let content = rawText;

        if (activeModeMatch) {
            mode = activeModeMatch[1].trim(); 
            content = activeModeMatch[2];
        } else {
            content = rawText.trim();
        }

        const cleanSymbols = (str) => {
            if (!str) return '';
            // 链式调用：先去Markdown符号，再去括号
            return str.replace(/\*\*|__|\*|_|~~/g, '').replace(/[()（）]/g, '').trim();
        };

        const lines = content.split('\n').map(l => l.trim()).filter(Boolean).map(cleanSymbols);

        // --- 2. 容器初始化 ---
        cardContainer.innerHTML = '';
        cardContainer.className = 'reverie-container';
        cardContainer.classList.add(`style-${mode}`, 'active');

        // --- 3. 模式构建逻辑 ---

         if (mode === 'postcard') {

            const photoText = lines[0] || '...';

            const caption = lines[1] || 'UNTITLED MEMORY';

            const senderMessage = lines.slice(2).join('<br>') || 'Anonymous';

            const dateStr = new Date().toLocaleDateString('en-GB', { day: '2-digit', month: 'short', year: '2-digit' }).toUpperCase();

            const flipper = document.createElement('div');
            flipper.className = 'card-flipper';

            flipper.innerHTML = `
                <div class="card-face card-front">
                    <div class="photo-area">
                        <div class="grain-overlay"></div>
                        <div class="poster-text">
                            <span class="poster-quote-mark">“</span>
                            ${photoText}
                        </div>
                    </div>
                    <div class="front-caption">
                        ${caption}
                    </div>
                </div>

                <div class="card-face card-back">
                    <div class="stamp-box">
                        <div class="stamp-inner">ECHO</div>
                    </div>
                    <div class="postmark-circle">
                        SENT<br>${dateStr}
                    </div>

                    <div class="writing-lines">
                        <div class="sender-signature">${senderMessage}</div>
                    </div>

                    <div class="air-mail-strip"></div>
                </div>
            `;

            cardContainer.appendChild(flipper);
            cardContainer.addEventListener('click', () => {
                cardContainer.classList.toggle('is-flipped');
            });

        } else if (mode === 'recommendation') {
            const wrapper = document.createElement('div');
            wrapper.className = 'card-content-wrapper';

            const flightCode = 'RV-' + Math.floor(100 + Math.random() * 899);
            const gate = 'G' + Math.ceil(Math.random() * 20);
            const seat = Math.ceil(Math.random() * 30) + ['A','B','C','D'][Math.floor(Math.random()*4)];
            const time = new Date().toLocaleTimeString('en-GB', {hour: '2-digit', minute:'2-digit'});

            const sharer = lines[0] || 'Traveler';
            let title = lines[1] || 'Untitled';
            let artist = '';

            const separators = [' by ', ' - ', '——', ' / '];
            for (const sep of separators) {
                if (title.includes(sep)) {
                    const parts = title.split(sep);
                    title = parts[0].trim();
                    artist = parts[1].trim();
                    break;
                }
            }
            const comment = lines[2] || 'Ready for takeoff.';

            wrapper.innerHTML = `
                <div class="ticket-main">
                    <div class="texture-overlay"></div>
                    <div class="perforation-line"></div>
                    <div class="ticket-header">
                        <div class="airline-brand"><i class="fa-solid fa-plane-up"></i><span>REVERIE AIR</span></div>
                        <span class="priority-tag">PRIORITY</span>
                    </div>
                    <div class="content-body">
                        <div class="rec-label">BOARDING PASS</div>
                        <div class="rec-title">${title}</div>
                        ${artist ? `<div class="rec-artist">${artist}</div>` : ''}
                        <div class="rec-quote-box">"${comment}"</div>
                    </div>
                    <div class="info-grid">
                        <div class="info-unit"><label>PASSENGER</label><span>${sharer}</span></div>
                        <div class="info-unit"><label>FLIGHT</label><span>${flightCode}</span></div>
                        <div class="info-unit"><label>TIME</label><span>${time}</span></div>
                    </div>
                </div>
                <div class="ticket-stub">
                    <div class="texture-overlay"></div>
                    <div class="stub-content">
                        <div class="stub-dest-group"><div class="stub-label">DEST</div><div class="dest-code">SOUL</div></div>
                        <div class="stub-meta">
                            <div><div class="stub-label">SEAT</div><div class="stub-val">${seat}</div></div>
                            <div><div class="stub-label">GATE</div><div class="stub-val">${gate}</div></div>
                        </div>
                    </div>
                    <div class="barcode-container"><div class="barcode-font">${flightCode}</div></div>
                </div>
            `;
            cardContainer.appendChild(wrapper);

        } else if (mode === 'memory') {
            const wrapper = document.createElement('div');
            wrapper.className = 'card-content-wrapper';

            document.body.style.backgroundColor = 'transparent';

            const tapeLabel = lines[0] || 'Unlabeled Tape';
            const memoryText = lines[1] || '...';
            const currentThought = lines[2] || 'End of tape.';

            const min = Math.floor(Math.random() * 5);
            const sec = Math.floor(Math.random() * 60).toString().padStart(2, '0');
            const timeStr = `0${min}:${sec}`;

            wrapper.innerHTML = `
                <div class="cassette-shell">
                    <div class="screw tl"></div><div class="screw tr"></div>
                    <div class="screw bl"></div><div class="screw br"></div>

                    <div class="tape-label">
                        <div class="handwritten-title">${tapeLabel}</div>
                    </div>

                    <div class="tape-window">
                        <div class="reel left"></div>
                        <div class="reel right"></div>
                    </div>
                </div>

                <div class="lcd-panel">
                    <div class="meta-row">
                        <span>PLAYING ►</span>
                        <span>${timeStr}</span>
                    </div>

                    <div class="scrolling-text">${memoryText}</div>

                    <div class="thought-line">
                        <span class="thought-label">NOTE:</span> ${currentThought}
                    </div>

                    <div class="visualizer-row">
                        <div class="play-status">
                            <span class="led"></span> REC
                        </div>
                        <div class="visualizer">
                            <div class="bar"></div><div class="bar"></div>
                            <div class="bar"></div><div class="bar"></div><div class="bar"></div>
                        </div>
                    </div>
                </div>

                <div class="controls-deco">
                    <div class="btn-deco"></div>
                    <div class="btn-deco"></div>
                    <div class="btn-deco active"></div>
                    <div class="btn-deco"></div>
                </div>
            `;
            cardContainer.appendChild(wrapper);

        } else if (mode === 'fortune') {
            // ==========================================
            // 模式四：神圣拱门 (Fortune - 4 Lines Support)
            // ==========================================
            const wrapper = document.createElement('div');
            wrapper.className = 'card-content-wrapper';

            document.body.style.backgroundColor = 'transparent';

            // --- 数据解析 ---
            // Line 1: 牌名/状态 (e.g., 圣杯侍从 - 正位)
            // Line 2: 关键词
            // Line 3: 解读
            // Line 4: 角色评论 (需去除 *)

            let cardNum = 'ARCANA';
            let cardName = 'DESTINY';

            const firstLine = lines[0] || '';
            if (firstLine.includes('-')) {
                const parts = firstLine.split('-');
                cardNum = parts[0].trim();
                cardName = parts[1].trim();
            } else {
                cardName = firstLine;
            }

            const keywords = lines[1] || 'Unknown';
            const description = lines[2] || 'The stars are silent.';

            // 处理第四行：角色评论
            let charComment = '';
            if (lines[3]) {
                // 自动消除 * 号，并去除首尾空格
                charComment = lines[3].replace(/\*/g, '').trim();
            }

            // 随机图标
            const mysticIcons = [
                'fa-regular fa-sun', 'fa-regular fa-moon', 'fa-regular fa-star',
                'fa-solid fa-eye', 'fa-solid fa-infinity', 'fa-solid fa-meteor',
                'fa-solid fa-hand-sparkles'
            ];
            const randomIconClass = mysticIcons[Math.floor(Math.random() * mysticIcons.length)];

            // 随机星尘
            let starsHtml = '';
            for(let i=0; i<25; i++) {
                const top = Math.random() * 100;
                const left = Math.random() * 100;
                const size = 1 + Math.random() * 2;
                const delay = Math.random() * 3;
                starsHtml += `<div class="stardust" style="top:${top}%; left:${left}%; width:${size}px; height:${size}px; animation-delay:${delay}s"></div>`;
            }

            wrapper.innerHTML = `
                <div class="arch-frame">
                    <div class="stars-bg">
                        <div class="zodiac-ring"></div>
                        ${starsHtml}
                    </div>

                    <div class="content-layout">
                        <div class="top-icon">
                            <i class="${randomIconClass}"></i>
                        </div>

                        <div class="arch-num">${cardNum}</div>
                        <div class="arch-title">${cardName}</div>

                        <div class="arch-text-area">
                            <div class="keywords">✦ ${keywords} ✦</div>
                            <div class="description">${description}</div>

                            ${charComment ? `<div class="char-comment">${charComment}</div>` : ''}
                        </div>
                    </div>
                </div>
            `;
            cardContainer.appendChild(wrapper);

        } else {
            const wrapper = document.createElement('div');
            wrapper.className = 'card-content-wrapper';

            const now = new Date();
            const dateTimeStr = `${now.getFullYear().toString().slice(-2)}.${String(now.getMonth()+1).padStart(2,'0')}.${String(now.getDate()).padStart(2,'0')} ${String(now.getHours()).padStart(2,'0')}:${String(now.getMinutes()).padStart(2,'0')}`;
            const orderNo = Math.floor(1000 + Math.random() * 9000);

            const mainText = lines[0] || 'Unknown Item';
            const sourceText = lines[1] || '';
            const noteText = lines[2] || '';

            wrapper.innerHTML = `
                <div class="receipt-header">
                    <h1 class="brand-logo">E C H O</h1>
                    <div class="store-info">MEM_FRAGMENT_STORE_01</div>
                </div>
                <div class="meta-line"><span>NO.${orderNo}</span><span>${dateTimeStr}</span></div>
                <div class="item-section">
                    <div class="item-row">
                        <span class="qty">1</span>
                        <div class="desc">
                            <span class="main-content">${mainText}</span>
                            ${sourceText ? `<div class="sub-content">${sourceText}</div>` : ''}
                            ${noteText ? `<div class="simple-note">${noteText}</div>` : ''}
                        </div>
                        <span class="price">∞</span>
                    </div>
                </div>
                <div class="total-section">
                    <div class="total-row"><span>TOTAL</span><span>PRICELESS</span></div>
                    <div class="card-info">PAID WITH SOUL ****8888</div>
                </div>
                <div class="receipt-footer">
                    <div class="barcode-font">${orderNo}ECHO</div>
                    <div style="font-size:0.7em; letter-spacing:1px;">THANK YOU FOR VISITING</div>
                </div>
            `;
            cardContainer.appendChild(wrapper);
        }
    });
</script>
</body>
</html>
