<title>心绪回响 · 浮生卷</title>

<!-- 引入古风书法字体 -->
<link rel="preconnect" href="https://fonts.googleapis.com">
<link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
<link href="https://fonts.googleapis.com/css2?family=Ma+Shan+Zheng&family=Noto+Serif+SC:wght@300;600;900&family=Zhi+Mang+Xing&family=Long+Cang&display=swap" rel="stylesheet">
<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css">

<style>
    @import url('https://static.zeoseven.com/zsft/256/main/result.css');
    @import url("https://fontsapi.zeoseven.com/2/main/result.css");
    @import url("https://fontsapi-storage.zeoseven.com/111/main/result.css");
    @import url("https://fontsapi.zeoseven.com/874/main/result.css");

    :root {
        --ink-color: #2c2c2c;
        --paper-bg: #f4ecd8;
        --red-seal: #b5382e;
        --jade-green: #5c8d89;
        --gold-accent: #c5a059;
        --font-serif: 'LXGW ZhenKai GB';
        --font-calligraphy: 'Zhi Mang Xing', cursive;
        --font-title: 'Huiwen-Mincho';
        --font-hand: 'ChillHuoKai';
    }

    body { 
        display: flex; 
        justify-content: center; 
        align-items: center; 
        margin: 0; 
        padding: 10px; 
        box-sizing: border-box; 
        font-family: var(--font-serif);
    }

    .reverie-container {
        max-width: 600px;
        margin: 0 auto;
        display: none; 
        animation: fadeIn 1s ease-out;
    }
    .reverie-container.active { display: block; }

    @keyframes fadeIn { from { opacity: 0; transform: translateY(10px); } to { opacity: 1; transform: translateY(0); } }

    /* =================================================================== */
    /* 1. 模式一：古籍残卷 (Excerpt) */
    /* =================================================================== */
    .style-excerpt .card-content-wrapper {
        position: relative;
        background: var(--paper-bg);
        width: 100%;
        max-width: 380px;
        margin: 0 auto;
        padding: 40px 30px;
        box-shadow: 0 6px 8px rgba(0,0,0,0.15), inset 0 0 60px rgba(139, 69, 19, 0.1);
        color: var(--ink-color);
        writing-mode: horizontal-tb; /* 网页阅读习惯保留横排，但设计感做竖排意向 */
        border-right: 4px solid #dcdcdc; /* 模拟书卷厚度 */
        overflow: hidden;
    }

    /* 纸张纹理 */
    .style-excerpt .card-content-wrapper::before {
        content: ""; position: absolute; top: 0; left: 0; width: 100%; height: 100%;
        background-image: url("data:image/svg+xml,%3Csvg width='200' height='200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='paper'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.04' numOctaves='5'/%3E%3CfeDiffuseLighting lighting-color='%23fff5e6' surfaceScale='2'%3E%3CfeDistantLight azimuth='45' elevation='60'/%3E%3C/feDiffuseLighting%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23paper)' opacity='0.4'/%3E%3C/svg%3E");
        pointer-events: none; mix-blend-mode: multiply;
    }

    /* 顶部装饰线 */
    .style-excerpt .receipt-header { 
        border-bottom: 2px solid var(--ink-color); 
        border-top: 2px solid var(--ink-color);
        padding: 5px 0; margin-bottom: 25px; text-align: center;
        position: relative; z-index: 2;
    }
    .style-excerpt .brand-logo { font-family: var(--font-title); font-size: 1.8em; margin: 0; color: var(--ink-color); letter-spacing: 5px; }

    /* 内容区 */
    .style-excerpt .item-section { position: relative; z-index: 2; padding: 10px 0; }
    .style-excerpt .main-content { 
        font-family: var(--font-calligraphy); 
        font-size: 1.8em; 
        line-height: 1.4; 
        display: block; 
        text-align: center;
        margin-bottom: 15px;
        text-shadow: 0 0 1px rgba(0,0,0,0.1);
    }
    .style-excerpt .sub-content { 
        font-family: var(--font-serif); 
        font-size: 0.9em; 
        color: #555; 
        text-align: right; 
        margin-top: 10px; 
        font-style: italic;
    }

    /* 眉批 */
    .style-excerpt .simple-note { 
        margin-top: 25px; 
        font-family: var(--font-hand); 
        font-size: 1.1em; 
        color: var(--red-seal); 
        border-left: 2px solid var(--red-seal);
        padding-left: 10px;
        opacity: 0.9;
        transform: rotate(-1deg);
    }

    /* 印章 */
    .style-excerpt .stamp-seal {
        position: absolute; bottom: 20px; right: 20px;
        width: 60px; height: 60px;
        border: 3px solid var(--red-seal);
        color: var(--red-seal);
        font-family: var(--font-title);
        display: flex; justify-content: center; align-items: center;
        border-radius: 4px;
        font-size: 24px;
        opacity: 0.7;
        transform: rotate(-15deg);
        mask-image: url("data:image/svg+xml,%3Csvg width='100%25' height='100%25' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noise'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='1.5' numOctaves='3'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' fill='black'/%3E%3Crect width='100%25' height='100%25' filter='url(%23noise)' opacity='0.5'/%3E%3C/svg%3E");
        -webkit-mask-image: url("data:image/svg+xml,%3Csvg width='100%25' height='100%25' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noise'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='1.5' numOctaves='3'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' fill='black'/%3E%3Crect width='100%25' height='100%25' filter='url(%23noise)' opacity='0.5'/%3E%3C/svg%3E");
    }

    /* =================================================================== */
    /* 3. 模式三：青黛雅帖 (Recommendation - 雅致版) */
    /* =================================================================== */
    .style-recommendation .card-content-wrapper {
        display: flex;
        flex-direction: column;
        width: 100%;
        max-width: 320px; /* 手机竖屏最佳宽度 */
        min-height: 400px;
        /* 核心背景：深青灰 + 噪点质感 */
        background-color: #354a4b; 
        background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noise'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.9' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noise)' opacity='0.05'/%3E%3C/svg%3E");
        border-radius: 8px;
        /* 阴影：柔和的深色投影 */
        box-shadow: 0 6px 8px rgba(0,0,0,0.3);
        position: relative;
        overflow: hidden;
        color: #e6ebeb;
        margin: 0 auto;
        /* 内边框：极细的单线，营造精致感 */
        border: 1px solid rgba(255, 255, 255, 0.15);
    }

    /* 装饰元素：顶部的淡色云纹/山纹 */
    .style-recommendation .card-content-wrapper::before {
        content: '';
        position: absolute;
        top: -50px; left: -50px; width: 200px; height: 200px;
        background: radial-gradient(circle, rgba(255,255,255,0.05) 0%, transparent 70%);
        pointer-events: none;
    }

    /* 1. 顶部：赠礼人 (设计为一枚银质/玉质腰牌) */
    .style-recommendation .giver-tag-container {
        display: flex;
        justify-content: center;
        padding-top: 30px;
        margin-bottom: 10px;
        position: relative;
        z-index: 2;
    }

    .style-recommendation .giver-tag {
        /* 这里的背景半透明，融合底色 */
        background: rgba(255, 255, 255, 0.1);
        border: 1px solid rgba(255, 255, 255, 0.2);
        color: #d1dddd; /* 淡青白文字 */
        font-family: var(--font-serif);
        padding: 4px 16px;
        border-radius: 50px; /* 胶囊形状 */
        font-size: 0.8em;
        letter-spacing: 2px;
        display: flex; align-items: center; gap: 8px;
        backdrop-filter: blur(4px);
    }

    .style-recommendation .giver-tag::before {
        content: '✦'; 
        color: #aebaba;
        font-size: 0.8em;
    }

    /* 2. 中部：宝物展示 (极简，留白) */
    .style-recommendation .item-display {
        flex: 1;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        text-align: center;
        padding: 10px 30px;
        position: relative;
        z-index: 2;
    }

    /* 竖线装饰，连接上下 */
    .style-recommendation .item-display::after {
        content: '';
        position: absolute;
        height: 40px; width: 1px;
        background: linear-gradient(to bottom, transparent, rgba(255,255,255,0.3), transparent);
        bottom: -20px; left: 50%;
    }

    .style-recommendation .item-main {
        font-family: var(--font-title);
        font-size: 2.2em;
        line-height: 1.3;
        margin-bottom: 8px;
        color: #ffffff;
        text-shadow: 0 2px 10px rgba(0,0,0,0.2);
        letter-spacing: 2px;
    }

    .style-recommendation .item-origin {
        font-family: var(--font-serif);
        font-size: 0.8em;
        color: #8fa3a3; /* 灰青色 */
        letter-spacing: 1px;
        opacity: 0.8;
    }

    /* 3. 底部：素笺寄语 (设计为一张插在下方的素纸) */
    .style-recommendation .message-section {
        background: #f0f5f5; /* 极淡的青白色素纸 */
        margin: 0;
        padding: 30px 25px 40px 25px;
        position: relative;
        color: #354a4b; /* 深色墨迹 */
        /* 上方做不规则边缘或简单弧线 */
        clip-path: polygon(0 20px, 100% 0, 100% 100%, 0% 100%);
    }

    /* 纸张纹理 */
    .style-recommendation .message-section::before {
        content: ''; position: absolute; top: 0; left: 0; width: 100%; height: 100%;
        background-image: url("data:image/svg+xml,%3Csvg width='100' height='100' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='n'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.5'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23n)' opacity='0.08'/%3E%3C/svg%3E");
        pointer-events: none; mix-blend-mode: multiply;
    }

    .style-recommendation .message-text {
        font-family: var(--font-hand);
        font-size: 1.15em;
        line-height: 1.8;
        text-align: justify;
        margin-top: 10px;
        opacity: 0.9;
    }

    /* 落款印章 (改为雅致的闲章风格，不用大红) */
    .style-recommendation .seal-mark {
        position: absolute;
        bottom: 15px;
        right: 20px;
        width: 24px; height: 24px;
        border: 1px solid #7a8b8b;
        color: #7a8b8b; /* 石青色印章，而非朱红 */
        font-size: 0.6em;
        display: flex; justify-content: center; align-items: center;
        border-radius: 2px;
        opacity: 0.6;
        font-family: var(--font-serif);
    }

    /* =================================================================== */
    /* 5. 模式五：水月镜花 (Memory - 编年史版) */
    /* =================================================================== */
    .style-memory .card-content-wrapper {
        position: relative;
        width: 100%;
        max-width: 360px;
        min-height: 420px;
        margin: 0 auto;
        /* 背景：深墨色 + 水波纹理 */
        background-color: #1a1c20;
        background-image: 
            radial-gradient(circle at 10% 20%, rgba(255,255,255,0.03) 0%, transparent 20%),
            radial-gradient(circle at 90% 80%, rgba(255,255,255,0.03) 0%, transparent 20%);
        border-radius: 4px;
        box-shadow: 0 6px 8px -10px rgba(0,0,0,0.6);
        color: #d1d5db;
        display: flex;
        flex-direction: row; /* 关键：改为左右布局 */
        overflow: hidden;
        border: 1px solid #2d3748;
    }

    /* 动态水波背景层 */
    .style-memory .card-content-wrapper::before {
        content: '';
        position: absolute;
        top: -50%; left: -50%; width: 200%; height: 200%;
        background: url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noise'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.6' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noise)' opacity='0.03'/%3E%3C/svg%3E");
        animation: drift 20s linear infinite;
        opacity: 0.4;
        pointer-events: none;
    }

    /* === 左侧：时间轴区域 === */
    .style-memory .timeline-col {
        flex: 0 0 50px; /* 固定宽度 */
        border-right: 1px solid rgba(255,255,255,0.1);
        position: relative;
        background: rgba(0,0,0,0.2);
        display: flex;
        flex-direction: column;
        align-items: center;
        padding: 20px 0;
        z-index: 2;
    }

    /* 贯穿的红线/金线 */
    .style-memory .timeline-line {
        position: absolute;
        top: 0; bottom: 0; left: 50%;
        width: 1px;
        background: linear-gradient(to bottom, transparent, #a0aec0, rgba(192, 160, 128, 0.5), transparent);
        transform: translateX(-50%);
        z-index: 0;
    }

    /* 竖排的时间文字 */
    .style-memory .time-text {
        writing-mode: vertical-rl; /* 竖排 */
        text-orientation: upright;
        font-family: var(--font-serif);
        font-size: 0.85em;
        letter-spacing: 4px;
        color: #94a3b8;
        background: #1a1c20; /* 遮挡线条 */
        padding: 10px 0;
        z-index: 1;
        border: 1px solid rgba(255,255,255,0.1);
        border-radius: 4px;
        box-shadow: 0 2px 5px rgba(0,0,0,0.3);
    }

    /* === 右侧：内容区域 === */
    .style-memory .content-col {
        flex: 1;
        padding: 30px 25px;
        display: flex;
        flex-direction: column;
        position: relative;
        z-index: 2;
    }

    /* 装饰：右上角的卦象或符号 */
    .style-memory .hex-decor {
        position: absolute;
        top: 15px; right: 15px;
        font-size: 1.5em;
        color: rgba(255,255,255,0.05);
        font-family: serif;
        pointer-events: none;
    }

    /* 记忆正文 */
    .style-memory .memory-text {
        font-family: var(--font-serif);
        font-size: 1.05em;
        line-height: 1.7;
        text-align: justify;
        color: #e2e8f0;
        margin-bottom: auto; /* 把下方内容推到底部 */
        text-shadow: 0 0 5px rgba(0,0,0,0.5);
    }

    /* 首字下沉效果，增加古籍感 */
    .style-memory .memory-text::first-letter {
        font-size: 1.8em;
        float: left;
        margin-right: 5px;
        line-height: 1;
        color: #c0a080; /* 淡金 */
    }

    /* 分割线 */
    .style-memory .sep-line {
        width: 30px;
        height: 1px;
        background: #555;
        margin: 20px 0;
    }

    /* 当下念头 */
    .style-memory .thought-text {
        font-family: var(--font-hand);
        font-size: 0.95em;
        color: #aebaba; /* 青灰色 */
        font-style: italic;
        position: relative;
        padding-left: 10px;
        border-left: 2px solid #5c6b7f;
    }

    /* 底部水印：模拟印章缺角 */
    .style-memory .watermark {
        position: absolute;
        bottom: -20px; right: -20px;
        width: 100px; height: 100px;
        border: 1px solid rgba(255,255,255,0.05);
        border-radius: 50%;
        opacity: 0.5;
        pointer-events: none;
    }

    @keyframes drift { from { transform: translate(-20%, -20%) rotate(0deg); } to { transform: translate(0%, 0%) rotate(5deg); } }

    /* =================================================================== */
    /* 4. 模式二：水墨信笺 (Postcard) */
    /* =================================================================== */
    .reverie-container.style-postcard { perspective: 1500px; width: 400px; height: 480px; }

    .card-flipper {
        position: relative; width: 100%; height: 100%;
        transform-style: preserve-3d; transition: transform 0.8s cubic-bezier(0.2, 0.8, 0.2, 1); cursor: pointer;
    }
    .style-postcard.is-flipped .card-flipper { transform: rotateY(180deg); }

    .style-postcard .card-face {
        position: absolute; top: 0; left: 0; width: 100%; height: 100%;
        backface-visibility: hidden; border-radius: 4px; overflow: hidden;
        box-shadow: 0 6px 8px rgba(0,0,0,0.2);
        background: #fdfbf5;
    }

    /* 正面 */
    .style-postcard .card-front {
        display: flex; flex-direction: column; padding: 15px;
        background-image: linear-gradient(to bottom, #fdfbf5 0%, #ece5ce 100%);
    }
    .style-postcard .photo-area {
        flex: 1; background: #e0d8c0; position: relative;
        display: flex; justify-content: center; align-items: center;
        overflow: hidden; border: 1px solid #ccc;
    }
    /* 模拟水墨晕染 */
    .style-postcard .photo-area::before {
        content: ''; position: absolute; width: 120%; height: 120%;
        background: radial-gradient(circle, rgba(0,0,0,0.1) 0%, transparent 60%);
        filter: blur(20px);
    }
    .style-postcard .poster-text {
        position: relative; z-index: 2; color: #333;
        font-family: var(--font-title); font-size: 1.4em; writing-mode: vertical-rl;
        letter-spacing: 5px; padding: 20px;
    }
    .style-postcard .front-caption {
        height: 40px; display: flex; align-items: center; justify-content: flex-end;
        font-size: 0.8em; color: #888; letter-spacing: 1px;
    }

    /* 背面 */
    .style-postcard .card-back {
        transform: rotateY(180deg);
        background: #f4ecd8;
        padding: 30px;
        display: flex; flex-direction: column;
    }
    /* 竖排信纸格线 */
    .style-postcard .writing-lines {
        flex: 1; position: relative;
        background-image: linear-gradient(to right, rgba(0,0,0,0.05) 1px, transparent 1px);
        background-size: 40px 100%; /* 竖排间距 */
        display: flex; flex-direction: row-reverse; /* 竖排从右向左 */
        align-items: flex-start;
        padding-right: 10px;
        overflow: hidden;
    }
    .style-postcard .sender-signature {
        writing-mode: vertical-rl;
        font-family: var(--font-hand); font-size: 1.3em; color: #444;
        line-height: 40px; height: 100%; white-space: pre-wrap;
    }

    .style-postcard .stamp-box {
        position: absolute; top: 20px; left: 20px; /* 古代信笺左侧或封口处 */
        width: 40px; height: 40px; border: 2px solid #b5382e;
        display: flex; justify-content: center; align-items: center;
        color: #b5382e; writing-mode: vertical-rl; font-family: var(--font-title);
        line-height: 18px;
    }
    .style-postcard .postmark-circle {
        position: absolute; bottom: 20px; left: 20px;
        font-size: 0.8em; color: #888;
        font-family: var(--font-serif);
    }

    /* =================================================================== */
    /* 5. 模式四：天命灵签 (Fortune - 雅致签文版) */
    /* =================================================================== */
    .style-fortune .card-content-wrapper {
        position: relative;
        /* 签文通常是细长的 */
        width: 100%;
        max-width: 280px; 
        min-height: 360px;
        margin: 0 auto;
        padding: 0;
        background: transparent;
        display: flex;
        justify-content: center;
        /* 微微的浮动动画 */
        animation: floatPaper 6s ease-in-out infinite;
    }

    /* 签纸主体 */
    .style-fortune .fortune-paper {
        position: relative;
        width: 100%;
        background-color: #fdfbf5; /* 宣纸白 */
        background-image: 
            /* 纸张噪点 */
            url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noise'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.8' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noise)' opacity='0.08'/%3E%3C/svg%3E"),
            /* 顶部边框纹样 */
            linear-gradient(to bottom, transparent 98%, rgba(0,0,0,0.05) 100%);
        box-shadow: 
            0 2px 5px rgba(0,0,0,0.05),
            0 6px 8px rgba(0,0,0,0.1);
        display: flex;
        flex-direction: column;
        padding: 30px 25px;
        box-sizing: border-box;
        overflow: hidden;
        border-left: 1px solid rgba(0,0,0,0.05);
        border-right: 1px solid rgba(0,0,0,0.05);
    }

    /* 背景水印 (八卦/太极，极淡) */
    .style-fortune .fortune-paper::before {
        content: '';
        position: absolute;
        top: 50%; left: 50%;
        transform: translate(-50%, -50%);
        width: 200px; height: 200px;
        background-image: url("data:image/svg+xml,%3Csvg width='100' height='100' viewBox='0 0 100 100' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M50 0 L50 10 M50 90 L50 100 M0 50 L10 50 M90 50 L100 50' stroke='%23000' stroke-width='2' opacity='1'/%3E%3Ccircle cx='50' cy='50' r='30' stroke='%23000' stroke-width='1' fill='none' opacity='1'/%3E%3C/svg%3E");
        opacity: 0.03; /* 极淡 */
        pointer-events: none;
        z-index: 0;
    }

    /* 顶部：第几签/方式 */
    .style-fortune .header-row {
        text-align: center;
        margin-bottom: 20px;
        position: relative;
        z-index: 2;
    }

    .style-fortune .method-label {
        font-family: var(--font-serif);
        font-size: 0.8em;
        color: #666;
        letter-spacing: 4px;
        margin-bottom: 8px;
        display: block;
    }

    /* 核心：朱砂红印章样式的“上上签” */
    .style-fortune .level-stamp {
        display: inline-block;
        border: 2px solid #b5382e; /* 朱砂红 */
        color: #b5382e;
        font-family: var(--font-title); /* 书法体 */
        font-size: 2em;
        padding: 5px 15px;
        border-radius: 4px;
        letter-spacing: 5px;
        /* 模拟印章的不完美感 */
        mask-image: url("data:image/svg+xml,%3Csvg width='100%25' height='100%25' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='n'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.8' numOctaves='3'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' fill='black'/%3E%3Crect width='100%25' height='100%25' filter='url(%23n)' opacity='0.3'/%3E%3C/svg%3E");
        -webkit-mask-image: url("data:image/svg+xml,%3Csvg width='100%25' height='100%25' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='n'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.8' numOctaves='3'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' fill='black'/%3E%3Crect width='100%25' height='100%25' filter='url(%23n)' opacity='0.3'/%3E%3C/svg%3E");
    }

    /* 中部：判词 */
    .style-fortune .content-body {
        flex: 1;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: flex-start;
        padding: 20px 0;
        position: relative;
        z-index: 2;
    }

    /* 竖线分割 */
    .style-fortune .divider-v {
        width: 1px; height: 30px;
        background: linear-gradient(to bottom, transparent, #ccc, transparent);
        margin: 10px 0;
    }

    .style-fortune .main-text {
        font-family: var(--font-serif);
        font-size: 1.1em;
        line-height: 1.8;
        color: #2c2c2c;
        text-align: center; /* 保持横排阅读，但在视觉上居中 */
        font-weight: 600;
        margin-bottom: 5px;
    }

    .style-fortune .sub-text {
        font-family: var(--font-serif);
        font-size: 0.95em;
        color: #555;
        text-align: justify;
        line-height: 1.6;
        padding: 0 10px;
    }

    /* 底部：解签人与批注 */
    .style-fortune .footer-section {
        margin-top: auto;
        border-top: 1px dashed rgba(0,0,0,0.1);
        padding-top: 15px;
        position: relative;
        z-index: 2;
    }

    .style-fortune .master-sign {
        text-align: left;
        font-family: var(--font-hand);
        font-size: 0.9em;
        color: #888;
        margin-bottom: 10px;
    }

    /* 角色吐槽：模拟侧边批注 */
    .style-fortune .char-comment {
        font-family: var(--font-hand);
        color: #b5382e; /* 用红色模拟朱批 */
        font-size: 1em;
        line-height: 1.4;
        background: rgba(181, 56, 46, 0.05); /* 极淡红底 */
        padding: 8px;
        border-radius: 4px;
        position: relative;
    }

    /* 批注前面的小红点 */
