                    <div class="stamp-box">鸿雁<br>传书</div>
                    <div class="writing-lines">
                        <div class="sender-signature">${senderMessage}</div>
                    </div>
                    <div class="postmark-circle">平安<br>顺遂</div>
                </div>
            `;

            cardContainer.appendChild(flipper);
            cardContainer.addEventListener('click', () => {
                cardContainer.classList.toggle('is-flipped');
            });

        } else if (mode === 'recommendation') {
            // 输入处理：去除括号
            const sharer = lines[0] ? lines[0].replace(/[()（）]/g, '') : '神秘人';
            const fullTitle = lines[1] ? lines[1].replace(/[()（）]/g, '') : '无名之物';

            let item = fullTitle;
            let source = '';

            const separators = [' 出自 ', ' / ', ' - '];
            for (const sep of separators) {
                if (fullTitle.includes(sep)) {
                    const parts = fullTitle.split(sep);
                    item = parts[0].trim();
                    source = parts[1].trim();
                    break;
                }
            }

            const message = lines[2] ? lines[2].replace(/[()（）]/g, '') : '赠予有缘人。';

            const wrapper = document.createElement('div');
            wrapper.className = 'card-content-wrapper';

            wrapper.innerHTML = `
                <div class="giver-tag-container">
                    <div class="giver-tag">
                        ${sharer} 敬赠
                    </div>
                </div>

