                    <div class="item-main">${item}</div>
                    ${source ? `<div class="item-origin">${source}</div>` : ''}
                </div>

                <div class="message-section">
                    <div class="message-text">
                        ${message}
                    </div>
                    <div class="seal-mark">雅</div>
                </div>
            `;
            cardContainer.appendChild(wrapper);

        // ==========================================
        // Mode: Memory (水月镜花版)
        // ==========================================
        } else if (mode === 'memory') {
            const timeLoc = lines[0] ? lines[0].replace(/[()（）]/g, '') : '往事';
            const memoryText = lines[1] ? lines[1].replace(/[()（）]/g, '') : '...';
            const currentThought = lines[2] ? lines[2].replace(/[()（）]/g, '') : '';

            // 随机生成一个装饰符号 (八卦符号或类似)
            const decoSymbols = ['☷', '☶', '☵', '☴', '☳', '☲', '☱', '☰'];
            const randomSymbol = decoSymbols[Math.floor(Math.random() * decoSymbols.length)];

            const wrapper = document.createElement('div');
            wrapper.className = 'card-content-wrapper';

            // 左右布局结构
            wrapper.innerHTML = `
                <div class="timeline-col">
                    <div class="timeline-line"></div>
                    <div class="time-text">${timeLoc}</div>
                </div>

                <div class="content-col">
                    <div class="hex-decor">${randomSymbol}</div>

                    <div class="memory-text">${memoryText}</div>

                    ${currentThought ? `
                        <div class="sep-line"></div>
                        <div class="thought-text">
                            ${currentThought}
                        </div>
                    ` : ''}

                    <div class="watermark"></div>
                </div>
            `;
            cardContainer.appendChild(wrapper);

        // ==========================================
        // Mode: Fortune (灵签)
        // ==========================================
        } else if (mode === 'fortune') {
            // Line 1: 测字 - 上上签
            const firstLine = lines[0] ? lines[0].replace(/[()（）]/g, '') : '';

            // 修复解析逻辑：使用 ' - ' 或 '-' 分割，取最后一部分作为等级
            let method = '问卜';
            let level = '运势';

            if (firstLine.includes('-')) {
                const parts = firstLine.split('-');
                // 考虑到可能出现 "测字 - 上上签"，parts[0]是测字，parts[1]是上上签
                // 如果没有空格也支持
                method = parts[0].trim();
                level = parts.length > 1 ? parts[1].trim() : '未定';
            } else {
                level = firstLine; // 如果没有横杠，整行都是等级
            }

            // Line 2: 核心判词
            const mainTextRaw = lines[1] ? lines[1].replace(/[()（）]/g, '') : '...';
            // 尝试将判词分为标题和解释 (如果中间有空格)
            // 例如: "潜龙勿用 困顿之时..."
            let mainTitle = mainTextRaw;
            let mainDesc = '';

            // 简单的分割逻辑：第一个空格前是标题
            const spaceIndex = mainTextRaw.indexOf(' ');
            const commaIndex = mainTextRaw.indexOf('，');

            // 优先用空格分割，如果没有空格，就全当标题
            if (spaceIndex > 0 && spaceIndex < 10) { 
                mainTitle = mainTextRaw.substring(0, spaceIndex);
                mainDesc = mainTextRaw.substring(spaceIndex + 1).trim();
            } else if (commaIndex > 0 && commaIndex < 10) {
                 mainTitle = mainTextRaw.substring(0, commaIndex);
                 mainDesc = mainTextRaw.substring(commaIndex).trim(); // 保留逗号
            }

            // Line 3: 解签人 (如果没有就空着)
            const master = lines[2] ? lines[2].replace(/[()（）]/g, '') : '';

            // Line 4: 角色吐槽
            const comment = lines[3] ? lines[3].replace(/[()（）]/g, '') : '';

            const wrapper = document.createElement('div');
            wrapper.className = 'card-content-wrapper';

            // 动态生成HTML
            wrapper.innerHTML = `
                <div class="fortune-paper">
                    <div class="header-row">
                        <span class="method-label">${method}</span>
                        <div class="level-stamp">${level}</div>
                    </div>

                    <div class="content-body">
                        <div class="main-text">${mainTitle}</div>
                        ${mainDesc ? `<div class="sub-text">${mainDesc}</div>` : ''}
                    </div>

                    <div class="footer-section">
                        ${master ? `<div class="master-sign">解曰：${master}</div>` : ''}
                        ${comment ? `<div class="char-comment">${comment}</div>` : ''}
                    </div>
                </div>
            `;
            cardContainer.appendChild(wrapper);

        // ==========================================
        // Mode: Excerpt (残卷 - Default)
        // ==========================================
        } else {
            const mainText = lines[0] || '...';
            const sourceText = lines[1] || '';
            const noteText = lines[2] ? lines[2].replace(/[()]/g, '') : '';

            const wrapper = document.createElement('div');
            wrapper.className = 'card-content-wrapper';
            wrapper.innerHTML = `
                <div class="receipt-header">
                    <h1 class="brand-logo">残卷 · 拾遗</h1>
                </div>
                <div class="item-section">
                    <span class="main-content">${mainText}</span>
                    ${sourceText ? `<div class="sub-content">—— ${sourceText}</div>` : ''}
                    ${noteText ? `<div class="simple-note">${noteText}</div>` : ''}
                </div>
                <div class="stamp-seal">阅</div>
            `;
            cardContainer.appendChild(wrapper);
        }
    });
</script>
