    <script>
        document.addEventListener('DOMContentLoaded', () => {

            // --- 竹叶系统 (保持不变) ---
            function initBamboo() {
                const container = document.getElementById('bamboo-layer');
                // 稍微减少数量，避免遮挡文字
                const leafCount = 10;
                for(let i=0; i<leafCount; i++) {
                    const leaf = document.createElement('div');
                    leaf.classList.add('falling-leaf');

                    const width = Math.random() * 8 + 6; 
                    leaf.style.width = `${width}px`;
                    leaf.style.height = `${width * 2}px`;
                    leaf.style.left = `${Math.random() * 100}%`;

                    const duration = Math.random() * 5 + 8; 
                    const delay = Math.random() * -10; 

                    leaf.style.setProperty('--r-start', Math.random() * 360 + 'deg');
                    leaf.style.setProperty('--r-end', (Math.random() * 360 + 360) + 'deg');
                    leaf.style.setProperty('--x-end', (Math.random() * 100 - 50) + 'px');

                    leaf.style.animationDuration = `${duration}s`;
                    leaf.style.animationDelay = `${delay}s`;

                    container.appendChild(leaf);
                }
            }

            const rawData = document.getElementById('raw-data').innerHTML;
            const contentBody = document.getElementById('content-body');
            const mainContainer = document.getElementById('main-container');
            const footerNav = document.getElementById('footer-nav');
            const pageIndicator = document.getElementById('page-indicator');

            let characters = [];
            let currentIndex = 0;
            let isCollapsed = true;

            const cnNums = ['零', '壹', '贰', '叁', '肆', '伍', '陆', '柒', '捌', '玖'];
            const toCnNum = (n) => (n <= 9) ? cnNums[n] : n;

            document.getElementById('header-trigger').addEventListener('click', () => {
                isCollapsed = !isCollapsed;
                if(isCollapsed) mainContainer.classList.add('is-collapsed');
                else mainContainer.classList.remove('is-collapsed');
            });

            function parseData() {
                const blocks = rawData.match(/<(\w+)[^>]*>([\s\S]*?)<\/\1>/g);
                if(blocks) {
                    characters = blocks.map(b => {
                        const get = (r) => (b.match(r) || [])[1]?.trim() || '';
                        // 修正：支持读取小数，例如 0.0 或 50.5
                        const getNum = (r) => parseFloat((b.match(r) || [])[1]) || 0;

                        let art = b.match(/笔墨丹青：\n([\s\S]*?)笔墨题跋/)?.[1] || '';
                        art = art.replace(/^\n+|\n+$/g, '');

                        // 修正正则：允许匹配小数位
                        return {
                            name: get(/人物名讳：(.*?)\n/),
                            fateNum: getNum(/世俗尘缘：\s*(-?\d+(?:\.\d+)?)/),
                            mood: get(/当前心境：(.*?)\n/),
                            innerVoice: get(/灵台暗语：([\s\S]*?)心之欲念/),
                            desireNum: getNum(/心之欲念：\s*(\d+(?:\.\d+)?)/),
                            bodyCheck: get(/肉身反馈：\n([\s\S]*?)红尘俗务/),
                            tasks: get(/红尘俗务：\n([\s\S]*?)笔墨丹青/),
                            ascii: art,
                            caption: get(/笔墨题跋：(.*?)\n/)
                        };
                    });
                }
            }

            function render(index) {
                if(!characters[index]) return;
                const char = characters[index];

                if(characters.length > 1) {
                    footerNav.classList.remove('hidden');
                    pageIndicator.textContent = `${toCnNum(index+1)} · ${toCnNum(characters.length)}`;
                } else {
                    footerNav.classList.add('hidden');
                }

                // 修正进度条逻辑：完全按照数值 0-100 显示
                // 0 就是 0%，50 就是 50%
                let fatePer = Math.min(100, Math.max(0, char.fateNum));
                let desirePer = Math.min(100, Math.max(0, char.desireNum));

                const moodHtml = char.mood.split(/[；;]/).filter(t=>t).map(t => 
                    `<div class="tag-pill">${t}</div>`
                ).join('');

                const bodyRows = char.bodyCheck.split('\n').filter(l=>l).map(l => {
                    const match = l.match(/【(.*?)】\s*[：:]?\s*(.*)/);
                    const organ = match ? match[1] : '躯干';
                    const desc = match ? match[2] : l;
                    return `
                        <div class="tcm-row">
                            <div class="tcm-label">${organ}</div>
                            <div class="tcm-value">${desc}</div>
                        </div>`;
                }).join('');

                const taskHtml = char.tasks.split('\n').filter(l=>l).map(l => {
                    const isDone = l.includes('<s>') || l.includes('DONE');
                    const txt = l.replace(/<\/?s>/g, '').replace(/^-\s*/, '').trim();
                    return `
                        <div class="task-plaque ${isDone?'task-done':''}">
                            <span class="task-text">${txt}</span>
                            <div class="stamp-finished">终了</div>
                        </div>`;
                }).join('');

                const html = `
                    <div class="char-hero">
                        <div class="char-bg-text">天地玄黄</div>
                        <div class="char-name">${char.name}</div>
                        <div class="tags-wrapper">${moodHtml}</div>
                    </div>

                    <div class="stat-group">
                        <div class="stat-item">
                            <div class="stat-header"><span>世俗尘缘</span><span>${char.fateNum}</span></div>
                            <div class="stat-track"><div class="stat-fill fill-fate" style="width:${fatePer}%"></div></div>
                        </div>
                        <div class="stat-item">
                            <div class="stat-header"><span>心之欲念</span><span>${char.desireNum}</span></div>
                            <div class="stat-track"><div class="stat-fill fill-desire" style="width:${desirePer}%"></div></div>
                        </div>
                    </div>

                    <div class="letter-paper">
                        <div class="letter-header-box">
                            <span class="letter-title">灵台暗语</span>
                        </div>
                        <div class="letter-content">${char.innerVoice}</div>
                    </div>

                    <div class="tcm-record">
                        <div class="tcm-header">
                            <span class="tcm-title">肉身反馈</span>
                        </div>
                        <div class="tcm-grid">
                            ${bodyRows}
                        </div>
                    </div>

                    <div class="task-section">
                        <div class="task-header-beam">
                            <div class="beam-line"></div>
                            <span class="beam-deco">✦</span>
                            <div class="beam-title">红尘俗务</div>
                            <span class="beam-deco">✦</span>
                            <div class="beam-line"></div>
                        </div>
                        <div class="tasks-container">${taskHtml}</div>
                    </div>

                    <div class="art-mounting">
                        <div class="art-paper">
                            <div class="ascii-display">${char.ascii.replace(/</g, '&lt;')}</div>
                            <div class="art-footer">
                                ${char.caption} <span class="seal-mark">绘</span>
                            </div>
                        </div>
                    </div>
                `;

                contentBody.innerHTML = html;
            }

            document.getElementById('prev').addEventListener('click', () => {
                if(characters.length>1) {
                    currentIndex = (currentIndex - 1 + characters.length) % characters.length;
                    render(currentIndex);
                }
            });
            document.getElementById('next').addEventListener('click', () => {
                if(characters.length>1) {
                    currentIndex = (currentIndex + 1) % characters.length;
                    render(currentIndex);
                }
            });

            parseData();
            if(characters.length > 0) render(0);

            initBamboo();
        });
    </script>
</body>
</html>
