    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // ================= 1. 粒子系统 (限制在容器内) =================
            const canvas = document.getElementById('particle-canvas');
            const container = document.querySelector('.meow-fm-container');
            const ctx = canvas.getContext('2d');
            let particles = [];
            let animationFrameId;
            let width, height;

            function resizeCanvas() {
                width = container.offsetWidth;
                height = container.offsetHeight;
                canvas.width = width;
                canvas.height = height;
            }

            const resizeObserver = new ResizeObserver(() => {
                resizeCanvas();
            });
            resizeObserver.observe(container);
            resizeCanvas();

            class Particle {
                constructor() {
                    this.reset(true);
                }

                reset(initial = false) {
                    this.x = Math.random() * width;
                    this.y = initial ? Math.random() * height : height + 5;
                    this.size = Math.random() * 2 + 0.5; 
                    this.speedY = Math.random() * 0.4 + 0.1; 
                    this.speedX = Math.random() * 0.4 - 0.2; 
                    this.opacity = Math.random() * 0.5 + 0.1;
                    this.fadeSpeed = Math.random() * 0.005 + 0.002;
                    this.fadingOut = Math.random() > 0.5;
                }

                update() {
                    this.y -= this.speedY;
                    this.x += this.speedX;

                    if (this.fadingOut) {
                        this.opacity -= this.fadeSpeed;
                        if (this.opacity <= 0.05) this.fadingOut = false;
                    } else {
                        this.opacity += this.fadeSpeed;
                        if (this.opacity >= 0.6) this.fadingOut = true;
                    }

                    if (this.y < -10 || this.x < -10 || this.x > width + 10) {
                        this.reset();
                    }
                }

                draw() {
                    const style = getComputedStyle(document.body);
                    const colorStr = style.getPropertyValue('--particle-color').trim();

                    ctx.fillStyle = colorStr;
                    ctx.globalAlpha = this.opacity;
                    ctx.beginPath();
                    ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
                    ctx.fill();
                    ctx.globalAlpha = 1.0;
                }
            }

            function initParticles() {
                particles = [];
                const count = Math.floor(container.offsetWidth / 8); 
                for (let i = 0; i < count; i++) {
                    particles.push(new Particle());
                }
            }

            function animateParticles() {
                ctx.clearRect(0, 0, width, height);
                particles.forEach(p => {
                    p.update();
                    p.draw();
                });
                animationFrameId = requestAnimationFrame(animateParticles);
            }

            setTimeout(() => {
                resizeCanvas();
                initParticles();
                animateParticles();
            }, 100);


            // ================= 2. 内容解析逻辑 =================
            const regex = /serial[:：]\s*([^\n]*)\s*time[:：]\s*([^\n]*)(?:\s*NSFW[:：]\s*([\s\S]*?))?\s*scene[:：]\s*([\s\S]*?)\s*plot[:：]\s*([\s\S]*?)(?:\s*relation[:：]\s*([\s\S]*?))?(?:\s*echoes[:：]\s*([\s\S]*?))?(?:\s*archived[:：]\s*([\s\S]*?))?(?:\s*enigma[:：]\s*([\s\S]*?))?(?:\s*seeds[:：]\s*([\s\S]*))?$/i;

            function parseAndRender() {
                const rawDataElement = document.querySelector('.raw-data');
                if (!rawDataElement || !rawDataElement.textContent.trim()) return;

                const rawText = rawDataElement.textContent.trim();
                const match = rawText.match(regex);

                if (match) {
                    const [_, serial, time, nsfw, scene, plot, relation, echoes, archived, enigma, seeds] = match;

                    document.querySelector('.serial').textContent = serial.trim();
                    document.querySelector('.time').textContent = time.trim();

                    const nsfwTag = document.querySelector('.nsfw-tag');
                    if (nsfw && nsfw.trim() && nsfw.trim() !== '0' && nsfw.trim().toLowerCase() !== 'false') {
                        nsfwTag.textContent = "NSFW: " + nsfw.trim();
                        nsfwTag.style.display = 'block';
                    } else { 
                        nsfwTag.style.display = 'none'; 
                    }

                    document.querySelector('.plot-content').innerHTML = plot.trim().split('\n').map(l=>`<p>${l}</p>`).join('');
                    document.querySelector('.scene-content').innerHTML = scene.trim().split('\n').map(l=>`<p>${l}</p>`).join('');

                    const relationMod = document.getElementById('mod-relation');
                    if (relation && relation.trim()) {
                        relationMod.style.display = 'block';
                        const container = relationMod.querySelector('.relation-content');
                        container.innerHTML = '';
                        relation.trim().split('◈').forEach(chunk => {
                            if (!chunk.trim()) return;
                            const card = document.createElement('div');
                            card.className = 'char-card';
                            const lines = chunk.trim().split('\n');
                            card.innerHTML = `<div class="char-head">${lines[0].trim()}</div><div class="char-body">${lines.slice(1).join('<br>')}</div>`;
                            container.appendChild(card);
                        });
                    } else { relationMod.style.display = 'none'; }

                    const echoesMod = document.getElementById('mod-echoes');
                    if (echoes && echoes.trim()) {
                        const echoLines = echoes.trim().split('\n').filter(line => line.trim().startsWith('-'));
                        if(echoLines.length > 0) {
                            echoesMod.style.display = 'block';
                            const container = echoesMod.querySelector('.echoes-content');
                            container.innerHTML = '';
                            echoLines.forEach(line => {
                                const content = line.substring(1).trim();
                                const colonIndex = content.search(/[:：]/);
                                if (colonIndex > 0) {
                                    const speaker = content.substring(0, colonIndex).trim();
                                    const quote = content.substring(colonIndex + 1).trim();
                                    const item = document.createElement('div');
                                    item.className = 'echo-item';
                                    item.innerHTML = `<span class="echo-quote">“${quote}”</span><span class="echo-speaker">— ${speaker}</span>`;
                                    container.appendChild(item);
                                }
                            });
                        } else { echoesMod.style.display = 'none'; }
                    } else { echoesMod.style.display = 'none'; }

                    const setupDetailsModule = (moduleKey, content) => {
                        const mod = document.getElementById(`mod-${moduleKey}`);
                        if (content && content.trim()) {
                            mod.style.display = 'block';
                            const container = mod.querySelector(`.${moduleKey}-content`);
                            container.innerHTML = '';
                            content.trim().split('\n').forEach(line => {
                                const p = document.createElement('div');
                                const text = line.trim();
                                if (text.startsWith('-') || text.startsWith('•')) {
                                    p.className = 'item'; 
                                    p.textContent = text.substring(1).trim();
                                } else {
                                    p.className = 'title';
                                    p.textContent = text;
                                }
                                container.appendChild(p);
                            });
                        } else { mod.style.display = 'none'; }
                    };
                    setupDetailsModule('archived', archived);
                    setupDetailsModule('enigma', enigma);
                    setupDetailsModule('seeds', seeds);
                }
            }

            // ================= 3. 交互逻辑 =================
            const header = document.getElementById('toggle-header');
            const collapsibleContent = document.getElementById('collapsible-content');
            const buttonText = document.querySelector('#expand-btn-text span');
            const buttonIcon = document.querySelector('#expand-btn-text i');

            header.addEventListener('click', (e) => {
                if(e.target.closest('.meow-footer') || e.target.closest('.theme-capsule')) return;

                collapsibleContent.classList.toggle('open');
                const isOpen = collapsibleContent.classList.contains('open');
                buttonText.textContent = isOpen ? '封存回响…' : '叩响塔门…';
                buttonIcon.className = isOpen ? 'fa-solid fa-book-open' : 'fa-solid fa-key';
            });

            document.querySelectorAll('.details-box').forEach(el => {
                el.addEventListener('click', (event) => event.stopPropagation());
            });

            // ================= 4. 主题控制 =================
            const body = document.body;
            const autoBtn = document.getElementById('autoBtn');
            const themeBtn = document.getElementById('themeToggleBtn');
            const themeIcon = document.getElementById('themeIcon');
            const footerLight = document.querySelector('.footer-light');
            const footerDark = document.querySelector('.footer-dark');

            let isAutoMode = true;
            let isDarkMode = false;

            function updateThemeUI() {
                // UI 表现：如果是自动模式，高亮无穷大符号，否则淡化
                if (isAutoMode) {
                    autoBtn.classList.add('active');
                    themeBtn.style.opacity = '0.6';
                } else {
                    autoBtn.classList.remove('active');
                    themeBtn.style.opacity = '1';
                }

                themeIcon.innerHTML = isDarkMode 
                    ? '<i class="fa-solid fa-moon"></i>' 
                    : '<i class="fa-solid fa-sun"></i>';

                if (isDarkMode) {
                    body.classList.remove('theme-light');
                    body.classList.add('theme-dark');
                    footerLight.style.display = 'none';
                    footerDark.style.display = 'block';
                } else {
                    body.classList.remove('theme-dark');
                    body.classList.add('theme-light');
                    footerLight.style.display = 'block';
                    footerDark.style.display = 'none';
                }
            }

            function checkTimeAndApplyAuto() {
                if (!isAutoMode) return;
                const hour = new Date().getHours();
                const isDayTime = hour >= 6 && hour < 18;
                isDarkMode = !isDayTime;
                updateThemeUI();
            }

            // Init
            checkTimeAndApplyAuto();
            setInterval(checkTimeAndApplyAuto, 60000);

            autoBtn.addEventListener('click', function(e) {
                e.stopPropagation();
                isAutoMode = !isAutoMode;
                if(isAutoMode) checkTimeAndApplyAuto();
                updateThemeUI();
            });

            themeBtn.addEventListener('click', function(e) {
                e.stopPropagation();
                if (isAutoMode) isAutoMode = false;
                isDarkMode = !isDarkMode;
                updateThemeUI();
            });

            parseAndRender();
        });
    </script>
</body>
</html>
