<body class="theme-light">
    <div class="raw-data" style="display: none;">$1</div>

    <div class="options-panel expanded" id="panel">
        <div class="panel-header" id="header-toggle">
            <div class="panel-title">求索者抉择</div>
            <div class="panel-subtitle">MAKE YOUR DECISION</div>
            <div class="toggle-icon">
                <svg viewBox="0 0 24 24"><path d="M6 9l6 6 6-6"/></svg>
            </div>
        </div>

        <div class="collapsible-wrapper">
            <div class="collapsible-content">
                <div class="options-list" id="list-container">
                    <!-- 选项生成区 -->
                </div>

                <div class="controls-area">
                    <div class="mini-btn-group">
                        <!-- Auto 按钮 -->
                        <button class="mini-btn active" id="autoThemeBtn" title="6:00-18:00 为日间">◐ 自动</button>
                        <!-- 手动切换按钮 -->
                        <button class="mini-btn" id="manualThemeBtn" title="手动切换">◐</button>
                        <button class="mini-btn" id="reset-btn">↺ 重置</button>
                        <button class="mini-btn" id="clear-input-btn">✕ 清空</button>
                    </div>
                    <button class="submit-btn" id="submit-btn">确认</button>
                </div>
            </div>
        </div>
    </div>

    <div id="toast" class="toast"></div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const listContainer = document.getElementById('list-container');
            const submitBtn = document.getElementById('submit-btn');
            const toastEl = document.getElementById('toast');
            const panel = document.getElementById('panel');
            const headerToggle = document.getElementById('header-toggle');

            // 按钮获取
            const autoBtn = document.getElementById('autoThemeBtn');
            const manualBtn = document.getElementById('manualThemeBtn');
            const body = document.body;

            // --- 状态管理 ---
            let selectedOptions = new Set();
            let isAutoMode = true;
            let isDarkMode = false; // 当前显示状态

            // --- 主题切换逻辑 ---
            function updateThemeUI() {
                // 更新按钮外观
                if (isAutoMode) {
                    autoBtn.classList.add('active');
                    manualBtn.style.opacity = '0.5';
                } else {
                    autoBtn.classList.remove('active');
                    manualBtn.style.opacity = '1';
                }

                // 更新图标
                manualBtn.textContent = isDarkMode ? '✯ 夜间' : '☀ 日间';

                // 更新 CSS 类
                if (isDarkMode) {
                    body.classList.remove('theme-light');
                    body.classList.add('theme-dark');
                } else {
                    body.classList.remove('theme-dark');
                    body.classList.add('theme-light');
                }
            }

            function checkTimeAndApplyAuto() {
                if (!isAutoMode) return;
                const hour = new Date().getHours();
                // 6:00 - 18:00 为日间
                const isDayTime = hour >= 6 && hour < 18;
                isDarkMode = !isDayTime;
                updateThemeUI();
            }

            // 初始化主题
            checkTimeAndApplyAuto();
            setInterval(checkTimeAndApplyAuto, 60000); // 每分钟检查

            // 事件：点击 AUTO
            autoBtn.addEventListener('click', () => {
                isAutoMode = true; 
                checkTimeAndApplyAuto();
            });

            // 事件：点击 手动切换
            manualBtn.addEventListener('click', () => {
                isAutoMode = false;
                isDarkMode = !isDarkMode;
                updateThemeUI();
            });


            // --- 选项解析与生成 ---
            function populateOptionsFromRawData() {
                const rawDataElement = document.querySelector('.raw-data');
                if (!rawDataElement || !listContainer) return;

                const rawText = rawDataElement.textContent.trim();
                const options = rawText.split('\n').filter(line => line.trim() !== '');

                listContainer.innerHTML = ''; 

                if (options.length === 0) {
                    listContainer.innerHTML = '<div style="text-align:center;color:var(--text-sub);padding:20px;font-style:italic;">暂无命运分支</div>';
                    return;
                }

                options.forEach((optionText, index) => {
                    const button = document.createElement('button');
                    button.className = 'option-button';
                    button.textContent = optionText.trim();
                    button.style.animation = `slideUp 0.4s ease forwards ${index * 0.05}s`;
                    button.style.opacity = '0';
                    button.style.transform = 'translateY(10px)';

                    button.addEventListener('click', function() {
                        toggleSelection(this, optionText.trim());
                    });

                    listContainer.appendChild(button);
                });
            }

            // --- 基础交互 ---
            headerToggle.addEventListener('click', () => {
                panel.classList.toggle('expanded');
            });

            function toggleSelection(btnElement, text) {
                if (selectedOptions.has(text)) {
                    selectedOptions.delete(text);
                    btnElement.classList.remove('chosen');
                } else {
                    selectedOptions.add(text);
                    btnElement.classList.add('chosen');
                }
                updateSubmitButton();
            }

            function updateSubmitButton() {
                if (selectedOptions.size > 0) {
                    submitBtn.classList.add('show');
                    submitBtn.textContent = `确认 (${selectedOptions.size})`;
                } else {
                    submitBtn.classList.remove('show');
                }
            }

            document.getElementById('reset-btn').addEventListener('click', () => {
                selectedOptions.clear();
                document.querySelectorAll('.option-button').forEach(btn => btn.classList.remove('chosen'));
                updateSubmitButton();
                showToast("已重置选项");
            });

            document.getElementById('submit-btn').addEventListener('click', () => {
                if (selectedOptions.size === 0) return;
                const textToSend = Array.from(selectedOptions).join('\n');
                sendToInput(textToSend);
            });

            document.getElementById('clear-input-btn').addEventListener('click', () => {
                try {
                    const textarea = window.top.document.querySelector('#send_textarea');
                    if (textarea) {
                        textarea.value = '';
                        textarea.dispatchEvent(new Event('input', { bubbles: true }));
                        showToast("输入框已清空");
                    } else { showToast("未找到输入框"); }
                } catch (e) { showToast("权限受限"); }
            });

            function sendToInput(text) {
                try {
                    const textarea = window.top.document.querySelector('#send_textarea');
                    if (textarea) {
                        textarea.value = text;
                        textarea.dispatchEvent(new Event('input', { bubbles: true }));
                        showToast("内容已填入");
                    } else { console.log(text); }
                } catch (e) { console.error(e); }
            }

            let toastTimer;
            function showToast(msg) {
                toastEl.textContent = msg;
                toastEl.classList.add('active');
                clearTimeout(toastTimer);
                toastTimer = setTimeout(() => toastEl.classList.remove('active'), 1500);
            }

            // --- 启动 ---
            populateOptionsFromRawData();
        });
    </script>
</body>
</html>
