                <span id="clock-time" class="text-xs font-serif italic text-ink">--:--</span>
            </div>

            <div class="flex gap-1.5 opacity-60">
                <div class="w-1.5 h-1.5 bg-ink rounded-full"></div>
                <div class="w-1.5 h-1.5 border border-ink rounded-full"></div>
                <div class="w-1.5 h-1.5 border border-ink rounded-full"></div>
            </div>
        </div>

        <!-- Main Screen Content Area -->
        <div id="screen-content" class="flex-1 w-full overflow-y-auto scrollbar-hide px-0 pb-24 pt-2 relative z-10">
            <!-- Dynamic content will be rendered here -->
            <div class="flex flex-col items-center justify-center h-full text-sub opacity-50 space-y-2">
               <p class="font-mono text-xs uppercase tracking-widest">[ WAITING FOR SIGNAL ]</p>
               <p class="font-serif text-[10px] italic">Inject content into #raw-data</p>
            </div>
        </div>

        <!-- Bottom Navigation Overlay -->
        <div id="nav-overlay" class="absolute bottom-8 left-0 w-full flex justify-center z-30 pointer-events-none hidden transition-opacity duration-300">
            <div class="pointer-events-auto flex items-center bg-paper/95 border border-line px-6 py-2.5 shadow-lg gap-8 backdrop-blur-sm">
                <button id="btn-prev" class="text-ink hover:text-rust transition-colors text-base">
                    <i class="fa-solid fa-chevron-left"></i>
                </button>
                <span id="page-indicator" class="font-mono text-xs text-ink tracking-widest">
                    1 / 1
                </span>
                <button id="btn-next" class="text-ink hover:text-rust transition-colors text-base">
                    <i class="fa-solid fa-chevron-right"></i>
                </button>
            </div>
        </div>
    </div>

    <!-- Raw Data Holder (Template Slot) -->
    <div id="raw-data" style="display: none;">
$1
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- State ---
            let characters = [];
            let currentIndex = 0;

            // --- Elements ---
            const screenContent = document.getElementById('screen-content');
            const navOverlay = document.getElementById('nav-overlay');
            const pageIndicator = document.getElementById('page-indicator');
            const clockEl = document.getElementById('clock-time');

            // --- Clock Logic ---
            function updateClock() {
                const now = new Date();
                clockEl.textContent = now.toLocaleTimeString('en-US', { hour12: false, hour: '2-digit', minute: '2-digit' });
            }
            setInterval(updateClock, 1000);
            updateClock();

            // --- Parser (EXACT ORIGINAL LOGIC) ---
            function parseData() {
                const rawData = document.getElementById('raw-data').innerHTML;
                const chars = [];
                const charBlocks = rawData.match(/<(\w+)[^>]*>([\s\S]*?)<\/\1>/g);

                if (charBlocks) {
                    charBlocks.forEach(block => {
                        const extract = (regex) => (block.match(regex) || [])[1]?.trim() || '';

                        const parseStat = (regex) => {
                            const match = block.match(regex);
                            if (!match) return { val: 0, note: '' };
                            return { val: parseFloat(match[1]), note: match[2]?.trim() || '' };
                        };

                        let doodleRaw = (block.match(/随性涂鸦：\n([\s\S]*?)涂鸦解析：/) || [])[1] || '';
                        doodleRaw = doodleRaw.replace(/^\n+|\n+$/g, '');

                        const statRegex = /\s*(-?\d+(?:\.\d+)?)\s*(?:[（(]([^\n]*)[)）])?/;

                        chars.push({
                            name: extract(/角色名称：(.*?)\n/),
                            favor: parseStat(new RegExp("好感指数：" + statRegex.source)),
                            mood: extract(/情绪气泡：(.*?)\n/),
                            secret: extract(/隐秘心声：(.*?)\n/),
                            lust: parseStat(new RegExp("性欲指数：" + statRegex.source)),
                            body: extract(/器官状态：([\s\S]*?)事务备忘：/),
                            memo: extract(/事务备忘：([\s\S]*?)随性涂鸦：/),
                            doodle: doodleRaw,
                            doodleCaption: extract(/涂鸦解析：(.*?)\n/)
                        });
                    });
                }
                characters = chars;
                renderScreen();
            }

            // --- Renderer (MAGAZINE STYLE OPTIMIZED) ---
            function renderScreen() {
                if (characters.length === 0) return;

                const char = characters[currentIndex];
                const favorPercent = Math.min(100, Math.max(0, char.favor.val));
                const lustPercent = Math.min(100, Math.max(0, char.lust.val));

                // --- 1. Smart Name Parsing ---
                const nameMatch = char.name.match(/^(.*?)(?:\s*[（(](.*)[)）])?$/);
                const mainName = nameMatch ? nameMatch[1].trim() : char.name;
                const subName = nameMatch && nameMatch[2] ? nameMatch[2].trim() : '';

                // --- 2. Header Background Logic ---
                let bgLetter = '';
                let bgOpacityClass = 'opacity-5';

                if (subName) {
                    bgLetter = subName.charAt(0).toUpperCase();
                } else {
                    bgLetter = mainName.charAt(0);
                    if (/[\u4e00-\u9fa5]/.test(bgLetter)) {
                        bgOpacityClass = 'opacity-[0.03]'; 
                    }
                }

                // --- 3. Footer Logic ---
                const footerText = subName ? subName.toUpperCase() : mainName.split('').join(' ');
                const footerTracking = subName ? 'tracking-[0.3em]' : 'tracking-[0.15em]';

                // --- HTML Helpers ---
                const formatMemo = (text) => {
                    return text.split('\n').filter(l => l.trim()).map(line => {
                        const isDone = line.includes('<s>') || line.includes('DONE') || line.includes('completed') || line.includes('已完成');
                        const clean = line.replace(/<\/?s>/g, '').replace(/□/g, '').replace(/■/g, '').trim();
                        // Changed text sizes: text-xs for the box, text-sm for content
                        return `
                            <li class="flex items-baseline gap-3 mb-3 group ${isDone ? 'opacity-40' : ''}">
                                <span class="font-mono text-xs font-bold ${isDone ? 'text-sub line-through' : 'text-rust'}">${isDone ? '[X]' : '[_]'}</span>
                                <span class="text-sm font-body leading-tight text-ink ${isDone ? 'line-through decoration-1 decoration-sub' : ''}">${clean}</span>
                            </li>`;
                    }).join('');
                };

                const formatBody = (text) => {
                    return text.split('\n').filter(l => l.trim()).map(line => {
                        const parts = line.split(/(【.*?】)/g);
                        const formattedLine = parts.map(part => {
                            if (part.startsWith('【') && part.endsWith('】')) {
                                // Increased font size for tags
                                return `<span class="font-mono text-xs text-rust uppercase tracking-wider mr-2 font-bold border-b border-rust/20 pb-0.5 mb-1 inline-block">${part.replace(/[【】]/g, '')}</span>`;
                            }
                            return `<span>${part}</span>`;
                        }).join('');
                        return `<div class="mb-3.5 text-sm font-body text-ink leading-relaxed pb-2.5 border-b border-line border-dashed last:border-0">${formattedLine}</div>`;
                    }).join('');
                };

                const formatMood = (mood) => {
                    if (!mood) return '';
                    const parts = mood.split(/；/g);
                    return `<div class="flex flex-wrap gap-x-3 gap-y-2">
                        ${parts.map(p => `<span class="text-xs font-mono text-sub uppercase tracking-wider border border-sub/50 px-2 py-1 bg-white/50">${p.trim()}</span>`).join('')}
                    </div>`;
                };

                const fakeID = Math.random().toString(36).substr(2, 6).toUpperCase();

                const html = `
                    <div class="animate-[fadeIn_0.5s_ease-out]">
                        <!-- Header -->
                        <div class="pt-10 pb-6 px-6 mb-4 relative overflow-hidden">
                            <!-- Optimized Background Char -->
                            <div class="absolute top-0 right-0 font-serif text-[140px] leading-none text-ink -z-10 select-none ${bgOpacityClass}" style="transform: translate(20%, -10%);">
                                ${bgLetter}
                            </div>

                            <!-- Increased Label Size -->
                            <p class="font-mono text-[10px] text-rust tracking-[0.3em] uppercase mb-3">Subject Profile</p>

                            <!-- Main Name -->
                            <h1 class="text-[34px] font-serif font-bold text-ink tracking-tight leading-none mb-1">
                                ${mainName}
                            </h1>

                            <!-- Sub Name -->
                            ${subName 
                                ? `<h2 class="text-lg font-serif italic text-sub/80 mb-5">${subName}</h2>` 
                                : `<div class="h-5"></div>`
                            }

                            <div class="w-16 h-0.5 bg-ink mb-6"></div>

                            <!-- Increased ID Size -->
                            <div class="font-mono text-[11px] text-sub flex justify-between items-center border-y border-line py-2">
                                <span>ID: ${fakeID}</span>
                                <span>VOL. 01</span>
                            </div>
                        </div>

                        <!-- Stats -->
                        <div class="px-6 mb-12 grid grid-cols-1 gap-8">
                            <div>
                                <!-- Affection -->
                                <div class="mb-6 group">
                                    <div class="flex justify-between items-baseline mb-2">
                                        <!-- Increased Label Size -->
                                        <span class="text-xs font-mono uppercase tracking-[0.2em] text-sub">Affection</span>
                                        <span class="text-base font-serif font-bold text-ink">${char.favor.val}</span>
                                    </div>
                                    <div class="w-full h-[2px] bg-line relative overflow-hidden">
                                        <div class="h-full bg-rust absolute left-0 top-0 progress-bar-fill" style="width: ${favorPercent}%"></div>
                                    </div>
                                    <!-- Increased Note Size -->
                                    ${char.favor.note ? `<div class="mt-2 flex justify-end"><span class="text-[10px] font-mono text-sub italic border-l border-rust pl-2">${char.favor.note}</span></div>` : ''}
                                </div>

                                <!-- Lust -->
                                <div class="mb-5 group">
                                    <div class="flex justify-between items-baseline mb-2">
                                        <!-- Increased Label Size -->
                                        <span class="text-xs font-mono uppercase tracking-[0.2em] text-sub">Desire</span>
                                        <span class="text-base font-serif font-bold text-ink">${char.lust.val}</span>
                                    </div>
                                    <div class="w-full h-[2px] bg-line relative overflow-hidden">
                                        <div class="h-full bg-rust absolute left-0 top-0 progress-bar-fill" style="width: ${lustPercent}%"></div>
                                    </div>
                                    ${char.lust.note ? `<div class="mt-2 flex justify-end"><span class="text-[10px] font-mono text-sub italic border-l border-rust pl-2">${char.lust.note}</span></div>` : ''}
                                </div>
                            </div>

                            ${char.mood ? `
                            <div>
                                <!-- Increased Label Size -->
                                <p class="text-[10px] font-mono text-sub uppercase mb-2.5">Current State</p>
                                ${formatMood(char.mood)}
                            </div>` : ''}
                        </div>

                        <!-- Inner Thoughts -->
                        <div class="mx-5 mb-12 p-7 bg-ink text-paper relative shadow-lg">
                            <span class="absolute -top-5 left-4 text-7xl font-serif text-rust leading-none opacity-80">“</span>
                            <p class="font-serif text-[17px] italic leading-relaxed text-center opacity-95 relative z-10 pt-2">${char.secret.replace(/\*/g, '')}</p>
                            <div class="mt-6 flex justify-center">
                                <!-- Increased Label Size -->
                                <span class="text-[10px] font-mono tracking-widest uppercase opacity-50">— Internal Monologue</span>
                            </div>
                        </div>

                        <!-- Content Sections -->
                        <div class="px-6 space-y-12">
                            <!-- Body -->
                            <div>
                                <div class="flex items-center gap-2 mb-4">
                                    <div class="w-1 h-3 bg-ink"></div>
                                    <!-- Increased Header Size -->
                                    <h3 class="text-sm font-mono font-bold uppercase tracking-widest text-ink">Physiology</h3>
                                    <div class="flex-1 h-[1px] bg-line"></div>
                                </div>
                                <div class="px-1">${formatBody(char.body)}</div>
                            </div>

                            <!-- Agenda -->
                            <div>
                                <div class="flex items-center gap-2 mb-4">
                                    <div class="w-1 h-3 bg-ink"></div>
                                    <!-- Increased Header Size -->
                                    <h3 class="text-sm font-mono font-bold uppercase tracking-widest text-ink">Agenda</h3>
                                    <div class="flex-1 h-[1px] bg-line"></div>
                                </div>
                                <ul class="space-y-1 pl-1">${formatMemo(char.memo)}</ul>
                            </div>

                            <!-- Doodle -->
                            <div>
                                <div class="flex items-center gap-2 mb-4">
                                    <div class="w-1 h-3 bg-ink"></div>
                                    <!-- Increased Header Size -->
                                    <h3 class="text-sm font-mono font-bold uppercase tracking-widest text-ink">Visual_Log</h3>
                                    <div class="flex-1 h-[1px] bg-line"></div>
                                </div>
                                <div class="bg-white p-2.5 shadow-lg rotate-1 transition-transform hover:rotate-0 duration-500 border border-gray-100">
                                    <div class="bg-paper border border-line w-full min-h-[160px] py-8 px-4 flex items-center justify-center overflow-hidden relative">
                                        <!-- Increased ASCII Art Size -->
                                        <pre class="font-mono text-[11px] leading-[11px] text-ink whitespace-pre text-center opacity-80 scale-105">${char.doodle}</pre>
                                        <div class="absolute -top-3 left-1/2 -translate-x-1/2 w-12 h-6 bg-yellow-100/80 rotate-1 opacity-60"></div>
                                    </div>
                                    <div class="pt-3 pb-1 bg-white">
                                        <!-- Increased Caption Size -->
                                        <p class="font-serif italic text-sm text-center text-sub">Fig A. ${char.doodleCaption}</p>
                                    </div>
                                </div>
                            </div>
                        </div>

                        <!-- Footer -->
                        <div class="mt-16 mb-8 text-center px-4">
                            <!-- Increased Footer Size -->
                            <p class="font-mono text-xs text-sub uppercase ${footerTracking} break-words leading-relaxed">${footerText}</p>
                        </div>
                    </div>
                `;

                screenContent.innerHTML = html;
                screenContent.scrollTop = 0;

                if (characters.length > 1) {
                    navOverlay.classList.remove('hidden');
                    pageIndicator.innerHTML = `${currentIndex + 1} <span class="text-sub">/</span> ${characters.length}`;
                } else {
                    navOverlay.classList.add('hidden');
                }
            }

            parseData();

            document.getElementById('btn-prev').addEventListener('click', () => {
                if (characters.length > 1) {
                    currentIndex = (currentIndex - 1 + characters.length) % characters.length;
                    renderScreen();
                }
            });

            document.getElementById('btn-next').addEventListener('click', () => {
                if (characters.length > 1) {
                    currentIndex = (currentIndex + 1) % characters.length;
                    renderScreen();
                }
            });
        });
    </script>
</body>
</html>
