        border-radius: 4px;
        width: 100%;
        box-sizing: border-box;
    }

    .style-fortune .char-bubble {
        position: relative;
        bottom: auto;
        right: auto;
        background: #fff;
        border: 2px solid #ff69b4;
        color: #d63384;
        padding: 10px;
        border-radius: 8px;
        font-size: 11px;
        font-weight: bold;
        box-shadow: 2px 2px 0 rgba(0,0,0,0.1);
        width: 100%;
        box-sizing: border-box;
        transform: none;
        z-index: 10;
        animation: pop-in-bubble 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275) 0.3s backwards;
        margin-top: 15px;
        text-align: left;
    }

    .style-fortune .char-bubble::before {
        content: '';
        position: absolute;
        top: -6px; left: 15px;
        width: 10px; height: 10px;
        background: #fff;
        border-left: 2px solid #ff69b4;
        border-top: 2px solid #ff69b4;
        transform: rotate(45deg);
    }

    @keyframes pop-in-bubble {
        0% { transform: scale(0.8); opacity: 0; }
        100% { transform: scale(1); opacity: 1; }
    }

</style>
