<body>

<div class="reverie-container" id="reverieCard"></div>

<!-- 示例数据 -->
<div id="raw-data" style="display: none;">
<echo>
$1
</div>

<script>
    document.addEventListener('DOMContentLoaded', () => {
        // --- 1. 数据获取与预处理 ---
        const cardContainer = document.getElementById('reverieCard');
        const rawData = document.getElementById('raw-data');
        if (!cardContainer || !rawData) return;

        const rawText = rawData.textContent.trim();
        const activeModeMatch = rawText.match(/^(?:模式[:：])?(.*)\n([\s\S]*)$/);

        let mode = 'excerpt'; 
        let content = rawText;

        if (activeModeMatch) {
            mode = activeModeMatch[1].trim(); 
            content = activeModeMatch[2];
        } else {
            content = rawText.trim();
        }

        const cleanSymbols = (str) => {
            if (!str) return '';
            return str.replace(/\*\*|__|\*|_|~~/g, '').replace(/[()（）]/g, '').trim();
        };

        const lines = content.split('\n').map(l => l.trim()).filter(Boolean).map(cleanSymbols);

        // --- 2. 容器初始化 ---
        cardContainer.innerHTML = '';
        cardContainer.className = 'reverie-container';
        cardContainer.classList.add(`style-${mode}`, 'active');

        // Glitch Overlays
        const glitchLayers = (mode === 'postcard') ? `
            <div class="glitch-noise"></div>
            <div class="glitch-scanlines"></div>
            <div class="glitch-bar"></div>
        ` : '';

        // --- 3. 模式构建逻辑 ---
        if (mode === 'postcard') {
            // 模式二：Postcard (复古图片查看器)
            const sketch = lines[0] || 'Unknown Image';
            const timeLoc = lines[1] || '---';
            const message = lines[2] || '...';
            const signature = lines[3] || 'Anonymous';
            const filename = sketch.length > 6 ? sketch.substring(0,6) + '...' : sketch;

            cardContainer.innerHTML = `
                ${glitchLayers}
                <div class="os-window">
                    <div class="title-bar">
                        <span>${filename}.JPG - Image Viewer</span>
                        <div class="win-controls">
                            <div class="win-btn">_</div>
                            <div class="win-btn">X</div>
                        </div>
                    </div>
                    <div class="menu-bar">
                        <span class="menu-item">File</span>
                        <span class="menu-item">Edit</span>
                        <span class="menu-item">View</span>
                        <span class="menu-item">Help</span>
                    </div>
                    <div class="window-content">
                        <div class="img-placeholder">
                            <i class="fa-regular fa-image"></i>
                            <div class="img-caption">${sketch}</div>
                        </div>
                        <div class="meta-bar">
                            <span>INFO:</span>
                            <span>${timeLoc}</span>
                        </div>
                        <div class="post-body">
                            ${message}
                        </div>
                        <div class="signature-box">
                            <i class="fa-solid fa-pen-nib"></i> ${signature}
                        </div>
                    </div>
                </div>
            `;
        } else if (mode === 'recommendation') {
            // 模式三：Steam 商店页面
            const sharer = lines[0] || '鉴赏家';
            let title = lines[1] || '未知应用';
            let artist = '未知厂商';
            for (const sep of [' by ', ' - ', '——', ' / ']) {
                if (title.includes(sep)) {
                    const parts = title.split(sep);
                    title = parts[0].trim();
                    artist = parts[1].trim();
                    break;
                }
            }
            const comment = lines[2] || '暂无评测';

            cardContainer.innerHTML = `
                ${glitchLayers}
                <div class="steam-store-container">
                    <div class="nav-bar">
                        <span>所有游戏</span> <span>&gt;</span> <span>冒险</span> <span>&gt;</span> <span>${title}</span>
                    </div>
                    <div class="hero-section">
                        <div class="app-header">
                            <div class="app-title">${title}</div>
                            <div class="curator-info">
                                <i class="fa-solid fa-user-tag"></i> 鉴赏家推荐: ${sharer}
                            </div>
                        </div>
                        <div class="main-visual">
                            <i class="fa-brands fa-steam-symbol"></i>
                            <div class="visual-tag">VR 支持</div>
                        </div>
                        <div class="game-info-block">
                            <div class="desc-text">"${comment}"</div>
                            <div class="meta-row">
                                <span class="meta-label">开发商:</span>
                                <span class="meta-val">${artist}</span>
                            </div>
                            <div class="meta-row">
                                <span class="meta-label">发行商:</span>
                                <span class="meta-val">${artist}</span>
                            </div>
                            <div class="meta-row">
                                <span class="meta-label">用户综评:</span>
                                <span class="meta-val" style="color:#66c0f4">好评如潮 (98%)</span>
                            </div>
                        </div>
                        <div class="purchase-area">
                            <div class="install-btn">免费开玩</div>
                        </div>
                    </div>
                </div>
            `;
        } else if (mode === 'memory') {
            // 模式五：复古街机 (Arcade Cabinet Style)
            // L1: Time, Location
            // L2: Memory Content
            // L3: Current Thought
            const timeLocation = lines[0] || 'Unknown Zone';
            const memoryText = (lines[1] || 'No Data.').trim(); 
            const currentThought = lines[2] || '...';

            cardContainer.innerHTML = `
                ${glitchLayers}
                <div class="arcade-cabinet">
                    <div class="arcade-marquee">MEMORY LOG</div>

                    <div class="screen-bezel">
                        <div class="crt-screen">
                            <div class="crt-header">
                                <span>RECORDING...</span>
                                <span>${timeLocation}</span>
                            </div>
                            <div class="crt-content">${memoryText}</div>
                            <div class="crt-footer">
                                > ${currentThought} <span style="animation:blink 1s infinite">_</span>
                            </div>
                        </div>
                    </div>

                    <div class="control-panel">
                        <div class="joystick"></div>
                        <div class="arcade-btns">
                            <div class="arcade-btn btn-1"></div>
                            <div class="arcade-btn btn-2"></div>
                        </div>
                    </div>

                    <div class="coin-area">
                        <div class="coin-slot">
                            <div class="slot-hole"></div> 25¢ INSERT COIN
                        </div>
                    </div>
                </div>
            `;
        } else if (mode === 'fortune') {
            // 模式四：Fortune (Game Gacha 抽卡风格)
            // L1: Type - Result
            // L2: Keywords
            // L3: Analysis
            // L4: Comment

            let type = 'LUCKY DRAW';
            let result = 'UR';
            const firstLine = lines[0] || '';

            // 解析类型和结果
            if (firstLine.includes('-')) {
                const parts = firstLine.split('-');
                type = parts[0].trim();
                result = parts[1].trim();
            } else if (firstLine.includes(' ')) {
                const parts = firstLine.split(' ');
                type = parts[0];
                result = parts.slice(1).join(' ');
            } else {
                result = firstLine;
            }

            const keywords = lines[1] || '???';
            const description = lines[2] || 'Loading fate...';
            const charComment = lines[3] || '';

            // 优化：全星级判定逻辑
            let starCount = 0; // 0 表示尚未决定

            // 关键词库
            const rank5 = ['大吉', '正位', 'UR', 'SSR', '极好', '完美', '5星'];
            const rank4 = ['小吉', '吉', 'SR', '不错', '好', '4星'];
            const rank3 = ['平', '中', 'R', '一般', '普通', '3星'];
            const rank2 = ['小凶', '凶', '差', '困难', '2星'];
            const rank1 = ['大凶', '逆位', '极差', '灾难', '1星'];

            const checkKeywords = (text, words) => words.some(w => text.includes(w));

            // 按优先级检查
            if (checkKeywords(firstLine, rank5)) starCount = 5;
            else if (checkKeywords(firstLine, rank1)) starCount = 1; // 极端情况优先判断
            else if (checkKeywords(firstLine, rank4)) starCount = 4;
            else if (checkKeywords(firstLine, rank2)) starCount = 2;
            else if (checkKeywords(firstLine, rank3)) starCount = 3;

            // 如果没有匹配到任何关键词，则在 2, 3, 4 中随机
            if (starCount === 0) {
                 starCount = Math.floor(Math.random() * 3) + 2;
            }

            const stars = '★'.repeat(starCount) + '☆'.repeat(5 - starCount);

            // 图标随机逻辑
            const icons = ['fa-crown', 'fa-gem', 'fa-star', 'fa-heart', 'fa-bolt', 'fa-dragon', 'fa-sun', 'fa-moon'];
            const randIcon = icons[Math.floor(Math.random() * icons.length)];

            cardContainer.innerHTML = `
                ${glitchLayers}
                <div class="gacha-container">
                    <div class="card-inner">
                        <div class="rarity-stars">${stars}</div>
                        <div class="card-type">${type}</div>
                        <div class="main-icon-area">
                            <i class="fa-solid ${randIcon}"></i>
                        </div>
                        <div class="card-title">${result}</div>
                        <div class="keywords-tag">${keywords}</div>
                        <div class="card-desc">${description}</div>
                        ${charComment ? `<div class="char-bubble">${charComment}</div>` : ''}
                    </div>
                </div>
            `;
        } else {
            // 模式一：Game Cartridge (游戏卡带风格 - 原 Excerpt)
            // L1: Content Text
            // L2: Source
            // L3: Reaction
            const contentText = lines[0] || 'Insert Cartridge';
            const sourceText = lines[1] || 'Unknown Game';
            const reactionText = lines[2] || '';

            cardContainer.innerHTML = `
                ${glitchLayers}
                <div class="cartridge-container">
                    <div class="cartridge-grip"></div>
                    <div class="cartridge-label">
                        <div class="label-inner">
                            <div class="label-header">
                                <span class="series-code">EXCERPT</span>
                                <span class="source-badge">QUOTATION</span>
                            </div>
                            <div class="label-deco-bar"></div>

                            <div class="excerpt-text">"${contentText}"</div>

                            <div class="label-footer">
                                <div class="source-text">${sourceText}</div>
                                <div class="nintendo-seal">Qual<br>ity</div>
                            </div>
                        </div>
                    </div>
                    ${reactionText ? `<div class="reaction-sticker">${reactionText}</div>` : ''}
                    <div class="cartridge-arrow">▼ INSERT TO PLAY ▼</div>
                </div>
            `;
        }
    });
</script>
</body>
</html>
