    <script>
        (function () {
            // --- STATE MANAGEMENT ---
            let isEditing = false;
            let showDialog = true;
            let rawContent = '';

            const editToggleButton = document.getElementById('edit-toggle-button');
            const parsedContentContainer = document.getElementById('parsed-content-container');
            const editTextArea = document.getElementById('edit-textarea');
            const timeDisplay = document.getElementById('time-display');
            const dialogWindow = document.getElementById('dialog-window');
            const dialogCloseButton = document.getElementById('dialog-close-button');
            const dialogReadButton = document.getElementById('dialog-read-button');
            const dokiSticker = document.querySelector('.sticker');

            // --- PARSING LOGIC ---
            const highlightColors = ['#f472b6', '#facc15', '#60a5fa', '#a78bfa', '#fb7185'];

            function escapeHtml(text) {
                if (!text || typeof text !== 'string') return '';

                // 1. 先处理 & 符号 (必须最先处理，防止二次转义)
                // 这里的关键是：把 '&amp;' 拆开写，骗过浏览器的预解析
                var res = text.split('&').join('&' + 'amp;');

                // 2. 处理 < 和 >
                res = res.split('<').join('&' + 'lt;');
                res = res.split('>').join('&' + 'gt;');

                // 3. 核心修复：处理双引号 (ASCII 34)
                // 用 String.fromCharCode(34) 拿到双引号，避免代码里出现 "
                // 用 '&' + 'quot;' 拼接结果，避免被浏览器提前还原成 "
                res = res.split(String.fromCharCode(34)).join('&' + 'quot;');

                // 4. 处理单引号 (ASCII 39)
                res = res.split(String.fromCharCode(39)).join('&' + '#039;');

                return res;
            }

            function parseInlineMarkdown(text) {
                let html = escapeHtml(text);
                html = html.replace(/\*\*(.*?)\*\*/g, (match, content) => `<strong>${content}</strong>`);
                html = html.replace(/\*(.*?)\*/g, (match, content) => `<em>${content}</em>`);
                html = html.replace(/~~(.*?)~~/g, (match, content) => `<s>${content}</s>`);
                html = html.replace(/`(.*?)`/g, (match, content) => `<code>${content}</code>`);
                html = html.replace(/(\[.*?\]|\(.*?\)|".*?"|“.*?”)/g, (match) => {
                    const color = highlightColors[Math.floor(Math.random() * highlightColors.length)];
                    return `<span class="highlight" style="color: ${color};">${match}</span>`;
                });
                return html;
            }

            function parseContent(content) {
                const lines = content.split('\n');
                const htmlChunks = [];
                let i = 0;

                const isTableSeparator = (line) => /^\|(?:\s*:?-+:?\s*\|)+$/.test(line);

                while (i < lines.length) {
                    const trimmedLine = lines[i].trim();

                    if (i + 1 < lines.length && isTableSeparator(lines[i + 1].trim()) && trimmedLine.startsWith('|')) {
                        const alignLine = lines[i + 1].trim();
                        const alignments = alignLine.split('|').slice(1, -1).map(s => {
                            s = s.trim();
                            if (s.startsWith(':') && s.endsWith(':')) return 'center';
                            if (s.endsWith(':')) return 'right';
                            return 'left';
                        });

                        let tableHTML = '<table>';
                        const headers = lines[i].trim().split('|').slice(1, -1);
                        tableHTML += '<thead><tr>';
                        headers.forEach((h, idx) => {
                            tableHTML += `<th style="text-align: ${alignments[idx] || 'left'}">${parseInlineMarkdown(h.trim())}</th>`;
                        });
                        tableHTML += '</tr></thead>';

                        tableHTML += '<tbody>';
                        let rowIndex = i + 2;
                        while (rowIndex < lines.length && lines[rowIndex].trim().startsWith('|')) {
                            const cells = lines[rowIndex].trim().split('|').slice(1, -1);
                            tableHTML += '<tr>';
                            cells.forEach((cell, idx) => {
                                tableHTML += `<td style="text-align: ${alignments[idx] || 'left'}">${parseInlineMarkdown(cell.trim())}</td>`;
                            });
                            tableHTML += '</tr>';
                            rowIndex++;
                        }
                        tableHTML += '</tbody></table>';
                        htmlChunks.push(tableHTML);
                        i = rowIndex;
                        continue;
                    }

                    const headingMatch = trimmedLine.match(/^(\#{1,3})\s(.*)/);
                    if (headingMatch) {
                        const level = headingMatch[1].length;
                        const text = headingMatch[2];
                        htmlChunks.push(`<h${level}>${parseInlineMarkdown(text)}</h${level}>`);
                        i++;
                        continue;
                    }

                    if (/^---+\s*$/.test(trimmedLine)) {
                        htmlChunks.push('<hr>');
                        i++;
                        continue;
                    }

                    if (trimmedLine.startsWith('>')) {
                        let quoteHtml = '';
                        while (i < lines.length && lines[i].trim().startsWith('>')) {
                            const lineContent = lines[i].trim().substring(1).trim();
                            if (lineContent) {
                                quoteHtml += `<p>${parseInlineMarkdown(lineContent)}</p>`;
                            }
                            i++;
                        }
                        htmlChunks.push(`<blockquote>${quoteHtml}</blockquote>`);
                        continue;
                    }

                    if (trimmedLine.startsWith('||')) {
                        let blockHtml = '';
                        while (i < lines.length && lines[i].trim().startsWith('||')) {
                            const lineContent = lines[i].trim().substring(2).replace(/\|\|$/, '').trim();
                            if (lineContent) blockHtml += `<p>${parseInlineMarkdown(lineContent)}</p>`;
                            i++;
                        }
                        htmlChunks.push(`<div class="highlight-block">${blockHtml}</div>`);
                        continue;
                    }

                    if (trimmedLine.startsWith('* ')) {
                        let listHtml = '<ul>';
                        while (i < lines.length && lines[i].trim().startsWith('* ')) {
                            listHtml += `<li>${parseInlineMarkdown(lines[i].trim().substring(2))}</li>`;
                            i++;
                        }
                        listHtml += '</ul>';
                        htmlChunks.push(listHtml);
                        continue;
                    }

                    if (trimmedLine !== '') {
                        htmlChunks.push(`<p>${parseInlineMarkdown(trimmedLine)}</p>`);
                    }

                    i++;
                }
                return htmlChunks.join('');
            }

            function autoResizeTextarea() {
                if (!isEditing) return;
                editTextArea.style.height = 'auto';
                editTextArea.style.height = (editTextArea.scrollHeight) + 'px';
            }

            // --- UI UPDATE & RENDER ---
            function render() {
                if (isEditing) {
                    editTextArea.value = rawContent;
                    editTextArea.classList.remove('hidden');
                    parsedContentContainer.classList.add('hidden');
                    editToggleButton.classList.add('active');

                    setTimeout(autoResizeTextarea, 0);
                } else {
                    parsedContentContainer.innerHTML = parseContent(rawContent);
                    editTextArea.classList.add('hidden');
                    parsedContentContainer.classList.remove('hidden');
                    editToggleButton.classList.remove('active');
                }

                if (showDialog) {
                    dialogWindow.classList.remove('hidden');
                } else {
                    dialogWindow.classList.add('hidden');
                }
            }

            // --- EVENT LISTENERS ---
            if (editToggleButton) {
                editToggleButton.addEventListener('click', () => {
                    isEditing = !isEditing;
                    if (!isEditing) {
                        rawContent = editTextArea.value;
                    }
                    render();
                });
            }
            if (dokiSticker) {
                dokiSticker.addEventListener('click', () => {
                    dokiSticker.style.display = 'none';
                });
            }

            if (editTextArea) editTextArea.addEventListener('input', autoResizeTextarea);

            function closeDialog() {
                showDialog = false;
                render();
            }
            if (dialogCloseButton) dialogCloseButton.addEventListener('click', closeDialog);
            if (dialogReadButton) dialogReadButton.addEventListener('click', closeDialog);

            // --- INITIALIZATION ---
            function init() {
                const contentContainer = document.getElementById('parsed-content-container');
                if (contentContainer) {
                    // 获取内容
                    rawContent = contentContainer.textContent.trim();
                    // 清空原始内容，防止渲染前的闪烁（可选）
                    // contentContainer.innerHTML = '';
                } else {
                    rawContent = "Error: #parsed-content-container element not found.";
                    console.error("Fatal Error: Main content container #parsed-content-container not found.");
                    return;
                }

                isEditing = false;
                render();

                function updateTime() {
                    const now = new Date();
                    const timeString = `${String(now.getHours()).padStart(2, '0')}:${String(now.getMinutes()).padStart(2, '0')}`;
                    if (timeDisplay) timeDisplay.textContent = timeString;
                }
                updateTime();
                setInterval(updateTime, 30000);
            }

            // 立即启动！
            init();
        })();
    </script>
</body>

</html>
