<script>
document.addEventListener('DOMContentLoaded', function() {
    // --- Setup ---
    const rawDataElement = document.getElementById('raw-data');
    const groupNameElement = document.getElementById('group-name');
    const chatMessagesContainer = document.getElementById('chat-messages');

    // 【修改点】: 调整了颜色盘，使用更符合像素/复古风格的高饱和度粉彩
    // 配合黑色描边，这些颜色会看起来像糖果
    const pastelColors = [
        '#ffebee', // Red
        '#f3e5f5', // Purple
        '#e8eaf6', // Indigo
        '#e1f5fe', // Light Blue
        '#e0f2f1', // Teal
        '#f9fbe7', // Lime
        '#fff8e1', // Amber
        '#fff3e0', // Orange
        '#fce4ec', // Pink
        '#ede7f6'  // Deep Purple
    ];

    // Fisher-Yates 洗牌算法
    const shuffleArray = (array) => {
        let currentIndex = array.length, randomIndex;
        while (currentIndex !== 0) {
            randomIndex = Math.floor(Math.random() * currentIndex);
            currentIndex--;
            [array[currentIndex], array[randomIndex]] = [array[randomIndex], array[currentIndex]];
        }
        return array;
    };

    const shuffledColors = shuffleArray([...pastelColors]);

    // --- 解析逻辑 (保持不变) ---
    const rawText = rawDataElement.textContent;
    const lines = rawText.split('\n').filter(line => line.trim() !== '');

    const groupNameRegex = /【(.*?)】/;
    const messageRegex = /(.+?)[：:](.*)/;
    const cleanupRegex = /^\s*[>—\s]*/;

    let messageCounter = 0; 

    const groupNameMatch = rawText.match(groupNameRegex);
    groupNameElement.textContent = groupNameMatch ? groupNameMatch[1].trim() : "UNTITLED.TXT"; // 默认标题像素化

    lines.forEach((line) => {
        const cleanedLine = line.replace(cleanupRegex, '').trim();
        if (!cleanedLine) return;

        const messageMatch = cleanedLine.match(messageRegex);

        if (messageMatch) {
            const userBlock = messageMatch[1].trim();
            const messageText = messageMatch[2].trim();

            if (!userBlock) return;
            if (userBlock.startsWith('【')) {
                return; 
            }

            const avatar = [...userBlock][0]; 
            const username = userBlock.substring(avatar.length).trim();

            const messageDiv = document.createElement('div');
            messageDiv.className = 'message';

            const avatarDiv = document.createElement('div');
            avatarDiv.className = 'avatar';
            avatarDiv.textContent = avatar;

            const messageContentDiv = document.createElement('div');
            messageContentDiv.className = 'message-content';

            const usernameDiv = document.createElement('div');
            usernameDiv.className = 'username';
            usernameDiv.textContent = username;

            const bubbleDiv = document.createElement('div');
            bubbleDiv.className = 'bubble';

            // 设置背景色
            bubbleDiv.style.backgroundColor = shuffledColors[messageCounter % shuffledColors.length];

            // 通过 CSS 变量传递颜色给伪元素使用 (修复尖角颜色)
            bubbleDiv.style.setProperty('--bubble-bg', shuffledColors[messageCounter % shuffledColors.length]);

            // 支持 **加粗**、*斜体* 和 (动作)
            let processedMessageHTML = messageText
                .replace(/\*\*(.*?)\*\*/g, (match, content) => `<strong>${content}</strong>`)
                .replace(/\*(.*?)\*/g, (match, content) => `<em class="italic-text">${content}</em>`)
                .replace(/[(（](.*?)[)）]/g, (match) => `<span class="gif-text">${match}</span>`);

            bubbleDiv.innerHTML = processedMessageHTML;

            messageContentDiv.appendChild(usernameDiv);
            messageContentDiv.appendChild(bubbleDiv);
            messageDiv.appendChild(avatarDiv);
            messageDiv.appendChild(messageContentDiv);
            chatMessagesContainer.appendChild(messageDiv);

            // 触发动画
            setTimeout(() => {
                messageDiv.classList.add('animate-in');
            }, messageCounter * 120); // 稍微加快一点速度，符合游戏感

            messageCounter++; 
        }
    });
});
</script>

</body>
</html>
