<script>
document.addEventListener('DOMContentLoaded', function() {
    const rawDataElement = document.getElementById('raw-data');
    const titleElement = document.getElementById('group-title');
    const chatMessagesContainer = document.getElementById('chat-messages');
    const clockElement = document.getElementById('clock');

    const now = new Date();
    clockElement.textContent = `${now.getHours()}:${String(now.getMinutes()).padStart(2,'0')}`;

    const wechatColors = [
        '#ea986c', '#eeb174', '#f3d19e', 
        '#8aa284', '#6a8d87', '#5d6f7a',
        '#d0d0d0', '#9c9c9c'
    ];

    function getUserColor(username) {
        let hash = 0;
        for (let i = 0; i < username.length; i++) {
            hash = username.charCodeAt(i) + ((hash << 5) - hash);
        }
        return wechatColors[Math.abs(hash) % wechatColors.length];
    }

    const rawText = rawDataElement.textContent;
    const lines = rawText.split('\n').filter(line => line.trim() !== '');

    const groupNameRegex = /【(.*?)】/;
    const messageRegex = /(.+?)[：:](.*)/;
    const cleanupRegex = /^\s*[>—\s]*/;

    const groupNameMatch = rawText.match(groupNameRegex);
    if (groupNameMatch) {
        titleElement.innerHTML = `${groupNameMatch[1].trim()} <span>(${Math.floor(Math.random()*200+10)})</span>`;
    }

    lines.forEach((line, index) => {
        const cleanedLine = line.replace(cleanupRegex, '').trim();
        if (!cleanedLine) return;
        if (cleanedLine.startsWith('【')) return;

        const messageMatch = cleanedLine.match(messageRegex);

        if (messageMatch) {
            const userBlock = messageMatch[1].trim();
            const messageText = messageMatch[2].trim();

            if (!userBlock) return;

            const avatarChar = [...userBlock][0]; 
            const username = userBlock.substring(avatarChar.length).trim();
            const bgColor = getUserColor(username);

            const row = document.createElement('div');
            row.className = 'message-row';

            const avatar = document.createElement('div');
            avatar.className = 'avatar';
            avatar.style.backgroundColor = bgColor;
            avatar.textContent = avatarChar;

            const msgContainer = document.createElement('div');
            msgContainer.className = 'msg-container';

            const nameRow = document.createElement('div');
            nameRow.className = 'username';
            nameRow.textContent = username;

            const bubble = document.createElement('div');
            bubble.className = 'bubble';

            let processedHTML = messageText
                .replace(/\*\*(.*?)\*\*/g, (match, content) => `<strong>${content}</strong>`)
                .replace(/\*(.*?)\*/g, (match, content) => `<em>${content}</em>`)
                .replace(/[(（](.*?)[)）]/g, (match) => `<span class="action-text">(${match})</span>`);

            bubble.innerHTML = processedHTML;

            msgContainer.appendChild(nameRow);
            msgContainer.appendChild(bubble);

            row.appendChild(avatar);
            row.appendChild(msgContainer);

            chatMessagesContainer.appendChild(row);

            row.style.animationDelay = `${index * 0.05}s`;
        }
    });
});
</script>

</body>
</html>
