```html
<!DOCTYPE html>
<html lang="zh">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>粉色唱片 (Morandi Rose)</title>
    <link rel="preconnect" href="https://fonts.googleapis.com">
    <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
    <link href="https://fonts.googleapis.com/css2?family=Playfair+Display:ital,wght@1,500;1,700&family=Lato:wght@400;700&display=swap" rel="stylesheet">
    <style>
        :root {
            /* --- 莫兰迪粉色系配色 --- */
            --bg-glass: rgba(255, 252, 253, 0.75); /* 透亮奶白粉玻璃 */

            --text-main: #5d4b4e; /* 深褐灰文字，柔和对比 */
            --text-dim: #9e8c90;  /* 浅灰粉文字 */

            /* 唱片颜色：干枯玫瑰粉 */
            --vinyl-base: #e8d3d9; 
            --vinyl-groove: #d1b6be;

            /* 高亮与装饰：豆沙粉/玫瑰金 */
            --accent-color: #c48a96; 
            --accent-light: #f4eef0;

            --border-soft: 1px solid rgba(196, 138, 150, 0.3);
            --shadow-rose: 0 20px 60px rgba(196, 138, 150, 0.25);
        }

        * { margin: 0; padding: 0; box-sizing: border-box; }

        body {
            display: flex; justify-content: center; align-items: center;
            background: transparent;
            font-family: 'Lato', sans-serif;
            overflow: hidden;
            color: var(--text-main);
        }

        /* --- 主播放器容器 --- */
        .player-card {
            display: flex;
            width: 100%; max-width: 650px;
            background: var(--bg-glass);
            /* 浅色磨砂玻璃 */
            backdrop-filter: blur(25px);
            -webkit-backdrop-filter: blur(25px);
            border: 1px solid rgba(255, 255, 255, 0.8);
            border-radius: 20px;
            padding: 25px;
            box-shadow: var(--shadow-rose);

            /* 进场动画 */
            opacity: 0; transform: translateY(20px);
            animation: floatUp 0.8s cubic-bezier(0.2, 0.8, 0.2, 1) forwards;
            position: relative;
        }

        @keyframes floatUp { to { opacity: 1; transform: translateY(0); } }

        /* 折叠模式 */
        .player-card.collapsed {
            max-width: 280px; padding: 12px 20px;
            border-radius: 50px;
            align-items: center; justify-content: space-between;
        }
        .player-card.collapsed .visual-side,
        .player-card.collapsed .lyrics-container,
        .player-card.collapsed .controls-row { display: none; }
        .player-card.collapsed .mini-title { display: block; }

        /* --- 左侧：粉色唱片区 --- */
        .visual-side {
            flex: 0 0 160px;
            display: flex; flex-direction: column; align-items: center; justify-content: center;
            margin-right: 30px;
        }

        .record-wrapper {
            width: 150px; height: 150px;
            position: relative;
            border-radius: 50%;
            box-shadow: 0 15px 35px rgba(100, 50, 60, 0.15); /* 柔和的阴影 */
            animation: spin 12s linear infinite; /* 保持慢速旋转 */
        }

        .record {
            width: 100%; height: 100%; border-radius: 50%;
            /* 粉色唱片基底 */
            background: radial-gradient(circle, var(--vinyl-base) 0%, var(--vinyl-base) 25%, var(--vinyl-groove) 26%, var(--vinyl-base) 27%, var(--vinyl-base) 100%);
            /* 粉色唱片纹理 */
            background-image: repeating-radial-gradient( var(--vinyl-base) 0, var(--vinyl-base) 2px, var(--vinyl-groove) 3px, var(--vinyl-groove) 4px );
            position: relative;
            border: 1px solid var(--vinyl-groove);
        }

        /* 唱片中心贴纸 */
        .record::after {
            content: ''; position: absolute;
            top: 50%; left: 50%; transform: translate(-50%, -50%);
            width: 38%; height: 38%;
            background: #fff; /* 白色中心 */
            border-radius: 50%;
            box-shadow: inset 0 0 5px rgba(0,0,0,0.1);
            border: 4px solid var(--vinyl-base);
        }

        /* 静态高光 */
        .record-shine {
            position: absolute; top: 0; left: 0; right: 0; bottom: 0; border-radius: 50%;
            background: linear-gradient(135deg, rgba(255,255,255,0.4) 0%, transparent 45%, transparent 55%, rgba(255,255,255,0.1) 100%);
            pointer-events: none;
            z-index: 2;
        }

        @keyframes spin { 100% { transform: rotate(360deg); } }

        .now-playing {
            margin-top: 18px; font-family: 'Playfair Display', serif;
            font-style: italic; color: var(--accent-color); opacity: 0.8; font-size: 0.75rem;
            letter-spacing: 2px;
        }

        /* --- 右侧：歌词选项区 --- */
        .content-side {
            flex: 1; display: flex; flex-direction: column; overflow: hidden;
            position: relative;
        }

        /* 标题栏 */
        .header {
            display: flex; justify-content: space-between; align-items: center;
            margin-bottom: 12px; border-bottom: var(--border-soft);
            padding-bottom: 8px;
        }
        .track-title {
            font-family: 'Playfair Display', serif;
            font-size: 1.5rem; font-weight: 700;
            color: var(--text-main);
            /* 稍微带一点粉色光泽 */
            text-shadow: 2px 2px 0px rgba(255,255,255,0.8); 
        }
        .mini-title { 
            display: none; font-family: 'Playfair Display', serif; 
            color: var(--text-main); font-size: 1.1rem;
        }

        .icon-btn {
            background: transparent; border: none;
            color: var(--text-dim); font-size: 1.5rem;
            cursor: pointer; line-height: 1;
            transition: 0.3s;
        }
        .icon-btn:hover { color: var(--accent-color); }

        /* 歌词列表 */
        .lyrics-container {
            flex: 1; overflow-y: auto; padding-right: 8px;
            max-height: 240px;
            /* 遮罩 */
            mask-image: linear-gradient(to bottom, transparent 0%, black 5%, black 95%, transparent 100%);
            -webkit-mask-image: linear-gradient(to bottom, transparent 0%, black 5%, black 95%, transparent 100%);
        }

        .lyrics-container::-webkit-scrollbar { width: 4px; }
        .lyrics-container::-webkit-scrollbar-thumb { background: var(--vinyl-groove); border-radius: 10px; }

        .lyric-line {
            padding: 12px 15px; margin-bottom: 4px;
            color: var(--text-dim);
            font-size: 1rem; cursor: pointer;
            transition: all 0.4s ease;
            border-left: 3px solid transparent;
            text-align: left; position: relative;
            border-radius: 0 8px 8px 0;
        }

        .lyric-line:hover { 
            color: var(--text-main); 
            background: rgba(255,255,255,0.4); 
        }

        /* 选中状态：豆沙粉高亮 */
        .lyric-line.active {
            color: #fff;
            background: var(--accent-color);
            border-left: 3px solid #8e5865; /* 深色强调 */
            padding-left: 20px;
            font-weight: 500;
            box-shadow: 0 4px 15px rgba(196, 138, 150, 0.3);
        }

        /* 底部控制区 */
        .controls-row {
            display: flex; gap: 15px; margin-top: 15px; padding-top: 12px;
            border-top: var(--border-soft);
            align-items: center;
        }

        .mode-btn {
            background: transparent; color: var(--text-dim);
            border: none; font-size: 0.75rem; text-transform: uppercase;
            letter-spacing: 1px; cursor: pointer; padding: 4px 0;
            position: relative; transition: 0.3s;
        }
        .mode-btn:hover { color: var(--text-main); }
        .mode-btn.active { color: var(--accent-color); font-weight: bold; }
        .mode-btn.active::after {
            content: '♥'; font-size: 10px; position: absolute; right: -12px; top: 1px; color: var(--accent-color);
        }

        .play-btn {
            margin-left: auto;
            background: var(--accent-color); 
            color: #fff; border: none;
            padding: 8px 24px; border-radius: 20px;
            font-family: 'Lato', sans-serif;
            font-weight: 700; font-size: 0.85rem; letter-spacing: 1px;
            cursor: pointer; opacity: 0; pointer-events: none; transition: 0.3s;
            text-transform: uppercase;
            box-shadow: 0 4px 10px rgba(196, 138, 150, 0.4);
        }
        .play-btn.show { opacity: 1; pointer-events: auto; }
        .play-btn:hover { background: #b0707d; transform: translateY(-1px); }

        /* --- 响应式 --- */
        @media (max-width: 520px) {
            .player-card { flex-direction: column; padding: 20px; }
            .visual-side { 
                flex: 0 0 auto; margin-right: 0; margin-bottom: 15px; 
                transform: scale(0.9);
            }
            .lyrics-container { max-height: 180px; }
            .header { justify-content: center; position: relative; }
            .icon-btn { position: absolute; right: 0; top: -5px; }
            .track-title { font-size: 1.3rem; }
            .now-playing { display: none; }
        }
    </style>
</head>
<body>

    <div class="raw-data" style="display: none;">$1</div>

    <div class="player-card" id="player">
        <!-- 左侧：粉色唱片 -->
        <div class="visual-side">
            <div class="record-wrapper">
                <div class="record"></div>
                <div class="record-shine"></div>
            </div>
            <div class="now-playing">NOW PLAYING</div>
        </div>

        <!-- 右侧：内容 -->
        <div class="content-side">
            <!-- 头部 -->
            <div class="header">
                <div class="track-title">ROSE VINYL</div>
                <div class="mini-title">Paused</div>
                <button class="icon-btn" id="collapseBtn" title="Mini Mode">_</button>
            </div>

            <!-- 歌词式选项 -->
            <div class="lyrics-container" id="lyricsList"></div>

            <!-- 底部控制 -->
            <div class="controls-row">
                <button class="mode-btn active" id="btnSingle">Single</button>
                <button class="mode-btn" id="btnMulti">Playlist</button>
                <button class="play-btn" id="btnPlay">Play</button>
            </div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const player = document.getElementById('player');
            const list = document.getElementById('lyricsList');
            const btnSingle = document.getElementById('btnSingle');
            const btnMulti = document.getElementById('btnMulti');
            const btnPlay = document.getElementById('btnPlay');
            const collapseBtn = document.getElementById('collapseBtn');
            const trackTitle = document.querySelector('.track-title');

            let isMulti = false;
            let selected = [];

            // 1. 解析数据
            const init = () => {
                const raw = document.querySelector('.raw-data').innerHTML;
                const txt = document.createElement('textarea');
                txt.innerHTML = raw;
                const content = txt.value.replace(/<\/?branches>/gi, '');
                const lines = content.split('\n').filter(l => l.trim());

                list.innerHTML = '';
                lines.forEach((text) => {
                    const div = document.createElement('div');
                    div.className = 'lyric-line';
                    div.textContent = text.trim();
                    div.onclick = () => selectOption(div, text.trim());
                    list.appendChild(div);
                });
            };

            // 2. 核心逻辑
            const selectOption = (el, text) => {
                if (!isMulti) {
                    // 单选
                    document.querySelectorAll('.lyric-line').forEach(d => d.classList.remove('active'));
                    el.classList.add('active');
                    sendData(text);
                } else {
                    // 多选
                    el.classList.toggle('active');
                    if(selected.includes(text)) selected = selected.filter(t => t !== text);
                    else selected.push(text);

                    btnPlay.classList.toggle('show', selected.length > 0);
                    btnPlay.textContent = `PLAY (${selected.length})`;
                }
            };

            const sendData = (text) => {
                try {
                    const target = window.top.document.querySelector('#send_textarea');
                    if (target) {
                        target.value = text;
                        target.dispatchEvent(new Event('input', { bubbles: true }));
                    } else {
                        console.log('Selected:', text);
                    }
                } catch(e) {}
            };

            // 3. 按钮切换
            btnSingle.onclick = () => {
                isMulti = false;
                btnSingle.classList.add('active');
                btnMulti.classList.remove('active');
                trackTitle.textContent = "ROSE VINYL";
                reset();
            };

            btnMulti.onclick = () => {
                isMulti = true;
                btnMulti.classList.add('active');
                btnSingle.classList.remove('active');
                trackTitle.textContent = "FAVORITE TRACKS";
                reset();
            };

            btnPlay.onclick = () => {
                if(selected.length) sendData(selected.join('\n\n'));
            };

            const reset = () => {
                selected = [];
                document.querySelectorAll('.lyric-line').forEach(d => d.classList.remove('active'));
                btnPlay.classList.remove('show');
            };

            // 4. 折叠逻辑
            collapseBtn.onclick = () => {
                player.classList.toggle('collapsed');
                collapseBtn.textContent = player.classList.contains('collapsed') ? '+' : '_';
            };

            init();
        });
    </script>
</body>
</html>
