<!DOCTYPE html>
<html lang="zh">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>星河神谕 (Starlight Oracle)</title>
    <link rel="preconnect" href="https://fonts.googleapis.com">
    <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
    <link href="https://fonts.googleapis.com/css2?family=Cinzel:wght@500;700&family=Noto+Serif+SC:wght@400;600&display=swap" rel="stylesheet">
    <style>
        :root {
            --card-w: 160px;
            --card-h: 240px;

            /* 新配色：银河冷色调 */
            --bg-glass: rgba(20, 23, 30, 0.7);
            --border-norm: rgba(255, 255, 255, 0.15);
            --border-hover: rgba(255, 255, 255, 0.5);

            --text-main: #Eaeaea;
            --text-dim: #8899a6;

            /* 极光渐变 */
            --aurora: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            --aurora-border: linear-gradient(45deg, #00c6ff, #0072ff, #9d50bb);
        }

        * { margin: 0; padding: 0; box-sizing: border-box; }

        body {
            display: flex; flex-direction: column; align-items: center; justify-content: center;
            background: transparent;
            font-family: 'Noto Serif SC', serif;
            overflow: hidden;
            color: var(--text-main);
        }

        /* 顶部装饰 */
        .oracle-header {
            margin-bottom: 25px;
            text-align: center;
            opacity: 0; animation: fadeIn 1s forwards;
        }
        .oracle-title {
            font-family: 'Cinzel', serif;
            font-size: 1.4rem; letter-spacing: 4px;
            background: -webkit-linear-gradient(#fff, #999);
            -webkit-background-clip: text; -webkit-text-fill-color: transparent;
            margin-bottom: 5px;
        }
        .oracle-sub { font-size: 0.8rem; color: var(--text-dim); letter-spacing: 2px; text-transform: uppercase; }

        @keyframes fadeIn { to { opacity: 1; } }

        /* 卡牌区域 */
        .card-grid {
            display: flex; flex-wrap: wrap; justify-content: center; gap: 20px;
            max-width: 900px; padding: 10px;
            max-height: 75vh; overflow-y: auto;
            perspective: 1000px;
            /* 隐藏滚动条 */
            scrollbar-width: none; 
        }
        .card-grid::-webkit-scrollbar { display: none; }

        /* 卡牌本体 */
        .tarot-card {
            width: var(--card-w); min-height: var(--card-h);
            background: var(--bg-glass);
            backdrop-filter: blur(10px);
            border: 1px solid var(--border-norm);
            border-radius: 12px;
            padding: 20px;

            display: flex; flex-direction: column; align-items: center; justify-content: center;
            text-align: center;
            position: relative;
            cursor: pointer;
            transition: all 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);
            overflow: hidden; /* 关键：流光不溢出 */

            /* 进场动画 */
            opacity: 0; transform: translateY(30px);
        }

        /* 悬停效果：上浮 + 边框亮 */
        .tarot-card:hover {
            transform: translateY(-8px) scale(1.02);
            border-color: var(--border-hover);
            box-shadow: 0 10px 30px rgba(0,0,0,0.5);
            background: rgba(30, 35, 45, 0.85);
        }

        /* 选中状态：极光背景 */
        .tarot-card.selected {
            border: 1px solid transparent; /* 边框交给伪元素处理 */
            background: rgba(20, 20, 30, 0.9);
            transform: translateY(-5px);
            box-shadow: 0 0 25px rgba(118, 75, 162, 0.4);
        }
        /* 选中时的渐变边框模拟 */
        .tarot-card.selected::before {
            content: ''; position: absolute; inset: 0;
            border-radius: 12px; padding: 2px; /* 边框宽度 */
            background: var(--aurora-border); 
            -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
            -webkit-mask-composite: xor;
            mask-composite: exclude;
            pointer-events: none;
        }

        /* 全息流光特效 (Hover时划过) */
        .tarot-card::after {
            content: ''; position: absolute;
            top: 0; left: -100%; width: 50%; height: 100%;
            background: linear-gradient(to right, transparent, rgba(255,255,255,0.1), transparent);
            transform: skewX(-25deg);
            transition: 0s;
        }
        .tarot-card:hover::after {
            left: 200%; transition: 0.6s ease;
        }

        /* 牌面内容 */
        .card-symbol {
            font-size: 1.5rem; margin-bottom: 15px; color: var(--text-dim);
            transition: 0.3s;
        }
        .tarot-card.selected .card-symbol { color: #fff; text-shadow: 0 0 10px #fff; }

        .card-text {
            font-size: 0.95rem; line-height: 1.6;
            color: var(--text-main);
            font-weight: 500;
        }

        /* 底部控制 */
        .controls {
            margin-top: 20px; display: flex; gap: 15px;
            opacity: 0; animation: fadeIn 1s forwards 0.5s;
        }

        .btn-mode {
            background: transparent; border: 1px solid var(--text-dim);
            color: var(--text-dim); padding: 6px 16px;
            border-radius: 20px; cursor: pointer;
            font-family: 'Cinzel', serif; font-size: 0.8rem;
            transition: 0.3s;
        }
        .btn-mode:hover, .btn-mode.active {
            border-color: #fff; color: #fff; text-shadow: 0 0 8px rgba(255,255,255,0.5);
        }

        .btn-confirm {
            display: none;
            background: var(--aurora);
            color: #fff; border: none;
            padding: 8px 24px; border-radius: 20px;
            cursor: pointer; font-weight: bold;
            box-shadow: 0 4px 15px rgba(118, 75, 162, 0.4);
            animation: pulse 2s infinite;
        }
        .btn-confirm:hover { filter: brightness(1.2); }

        @keyframes pulse { 0% { box-shadow: 0 0 0 0 rgba(118, 75, 162, 0.4); } 70% { box-shadow: 0 0 0 10px rgba(118, 75, 162, 0); } 100% { box-shadow: 0 0 0 0 rgba(118, 75, 162, 0); } }

        /* 响应式：手机端变成横向滑动的卡片流 */
        @media (max-width: 600px) {
            .card-grid {
                flex-wrap: nowrap;
                justify-content: flex-start;
                width: 100%;
                overflow-x: scroll; /* 强制横向滚动 */
                scroll-snap-type: x mandatory;
                padding: 20px 20px 40px 20px; /* 增加底部padding防止阴影被切 */
                mask-image: linear-gradient(to right, transparent, black 10%, black 90%, transparent);
            }
            .tarot-card {
                flex: 0 0 200px; /* 固定手机端卡片宽度 */
                scroll-snap-align: center;
                min-height: 280px;
            }
        }
    </style>
</head>
<body>

    <div class="raw-data" style="display: none;">$1</div>

    <div class="oracle-header">
        <div class="oracle-title">STARLIGHT ORACLE</div>
        <div class="oracle-sub">Choose your Path</div>
    </div>

    <div class="card-grid" id="cardGrid"></div>

    <div class="controls">
        <button class="btn-mode active" id="modeSingle">Destiny (单选)</button>
        <button class="btn-mode" id="modeMulti">Constellation (多选)</button>
        <button class="btn-confirm" id="btnSubmit">Manifest</button>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const grid = document.getElementById('cardGrid');
            const modeSingle = document.getElementById('modeSingle');
            const modeMulti = document.getElementById('modeMulti');
            const btnSubmit = document.getElementById('btnSubmit');

            // 罗马数字装饰数组
            const romans = ['I', 'II', 'III', 'IV', 'V', 'VI', 'VII', 'VIII', 'IX', 'X', 'XI', 'XII'];
            let isMulti = false;
            let selected = [];

            // 1. 初始化
            const init = () => {
                const raw = document.querySelector('.raw-data').innerHTML;
                const txt = document.createElement('textarea');
                txt.innerHTML = raw;
                const content = txt.value.replace(/<\/?branches>/gi, '');
                const lines = content.split('\n').filter(l => l.trim());

                grid.innerHTML = '';
                lines.forEach((text, i) => {
                    const card = document.createElement('div');
                    card.className = 'tarot-card';
                    // 动画延迟
                    card.style.animation = `fadeIn 0.6s ease forwards ${i * 0.1}s`;
                    card.style.opacity = '0'; // 初始隐藏配合动画

                    // 随机获取一个罗马数字或符号作为装饰
                    const symbol = romans[i % romans.length] || '✦';

                    card.innerHTML = `
                        <div class="card-symbol">${symbol}</div>
                        <div class="card-text">${text}</div>
                    `;

                    card.onclick = () => handleClick(card, text);
                    grid.appendChild(card);
                });

                // 补充关键帧
                const style = document.createElement('style');
                style.innerHTML = `@keyframes fadeIn { from { opacity: 0; transform: translateY(30px); } to { opacity: 1; transform: translateY(0); } }`;
                document.head.appendChild(style);
            };

            // 2. 点击逻辑
            const handleClick = (card, text) => {
                if (!isMulti) {
                    // 单选
                    document.querySelectorAll('.tarot-card').forEach(c => c.classList.remove('selected'));
                    card.classList.add('selected');
                    // 稍作延迟发送，让用户看到选中效果
                    setTimeout(() => sendData(text), 400); 
                } else {
                    // 多选
                    card.classList.toggle('selected');
                    if (selected.includes(text)) {
                        selected = selected.filter(t => t !== text);
                    } else {
                        selected.push(text);
                    }

                    btnSubmit.style.display = selected.length > 0 ? 'block' : 'none';
                    btnSubmit.textContent = `Manifest (${selected.length})`;
                }
            };

            const sendData = (text) => {
                try {
                    const target = window.top.document.querySelector('#send_textarea');
                    if (target) {
                        target.value = text;
                        target.dispatchEvent(new Event('input', { bubbles: true }));
                    } else {
                        console.log('Chosen:', text);
                    }
                } catch(e) {}
            };

            // 3. 模式切换
            const reset = () => {
                selected = [];
                document.querySelectorAll('.tarot-card').forEach(c => c.classList.remove('selected'));
                btnSubmit.style.display = 'none';
            };

            modeSingle.onclick = () => {
                isMulti = false;
                modeSingle.classList.add('active');
                modeMulti.classList.remove('active');
                reset();
            };

            modeMulti.onclick = () => {
                isMulti = true;
                modeMulti.classList.add('active');
                modeSingle.classList.remove('active');
                reset();
            };

            btnSubmit.onclick = () => {
                if(selected.length) sendData(selected.join('\n\n'));
            };

            init();
        });
    </script>
</body>
</html>
