            --tuner-bg: #f7f5f0;   /* 奶油米白 */
            --btn-base: #e8e4dd;   /* 燕麦色 */

            --text-main: #6b706b;  /* 灰橄榄色文字 */
            --text-light: #9fa69f;

            --accent-pink: #e09f95; /* 珊瑚粉 (指针/高亮) */
            --accent-green: #8da38d; /* 深一点的绿色装饰 */

            --shadow-soft: rgba(139, 156, 139, 0.4);
            --shadow-inset: rgba(166, 176, 166, 0.5);
        }

        * { margin: 0; padding: 0; box-sizing: border-box; }

        body {
            display: flex; justify-content: center; align-items: center;
            background: transparent;
            font-family: 'Noto Sans SC', sans-serif;
            overflow: hidden;
            user-select: none;
            color: var(--text-main);
        }

        /* --- 收音机外壳 --- */
        .radio-case {
            width: 100%; max-width: 600px;
            background: var(--case-color);
            border-radius: 24px; /* 更圆润的边角 */
            padding: 25px;
            /* 柔和的立体阴影，不再死黑 */
            box-shadow: 
                inset 2px 2px 0 rgba(255,255,255,0.4),
                inset -2px -2px 0 rgba(0,0,0,0.05),
                0 20px 40px var(--shadow-soft);
            display: flex; flex-direction: column; gap: 20px;

            opacity: 0; transform: scale(0.95);
            animation: turnOn 0.6s cubic-bezier(0.34, 1.56, 0.64, 1) forwards;
        }

        @keyframes turnOn { to { opacity: 1; transform: scale(1); } }

        /* --- 顶部：调频窗口 --- */
        .tuner-window {
            position: relative;
            background: var(--tuner-bg);
            border-radius: 12px;
            height: 120px;
            overflow: hidden;
            /* 像纸张一样的内陷感 */
            box-shadow: inset 2px 2px 8px var(--shadow-inset);
            display: flex; flex-direction: column;
            border: 4px solid #fff; /* 白色边框 */
        }

        /* 刻度盘背景 */
        .scale-plate {
            flex: 1;
            position: relative;
            display: flex; align-items: center;
            padding: 0 20px;
        }

        /* 刻度线 */
        .scale-lines {
            width: 100%; height: 25px;
            background-image: 
                linear-gradient(90deg, var(--text-light) 1px, transparent 1px),
                linear-gradient(90deg, var(--text-light) 1px, transparent 1px);
            background-size: 20px 8px, 100px 18px;
            background-position: 0 bottom, 0 bottom;
            background-repeat: repeat-x;
        }

        /* 频率数字 */
        .scale-nums {
            position: absolute; width: 100%; top: 20px;
            display: flex; justify-content: space-between;
            font-family: 'DM Mono', monospace;
            color: var(--text-light); font-size: 0.8rem; font-weight: 500;
        }

        /* 珊瑚粉指针 */
        .needle {
            position: absolute; top: 10px; bottom: 10px; width: 4px;
            background: var(--accent-pink);
            border-radius: 4px;
            left: 5%;
            transition: left 0.5s cubic-bezier(0.25, 1, 0.5, 1);
            z-index: 10;
