        .lcd-freq { font-size: 1.1rem; font-weight: 600; color: var(--accent-green); }
        .lcd-text { font-size: 0.85rem; letter-spacing: 0.5px; }

        /* 灯 */
        .stereo-light {
            width: 8px; height: 8px; border-radius: 50%;
            background: #dcdcdc; margin-right: 8px;
            border: 1px solid rgba(0,0,0,0.1);
        }
        .stereo-light.on { 
            background: var(--accent-pink); 
            box-shadow: 0 0 8px var(--accent-pink); 
            border-color: transparent;
        }

        /* --- 中间：装饰条 --- */
        .speaker-bar {
            height: 8px;
            background-image: radial-gradient(var(--text-light) 20%, transparent 20%);
            background-size: 6px 6px;
            opacity: 0.2;
        }

        /* --- 底部：奶油色按钮 --- */
        .controls-area {
            display: grid; 
            grid-template-columns: repeat(auto-fill, minmax(180px, 1fr));
            gap: 12px;
            max-height: 250px; overflow-y: auto;
            padding-right: 6px;
        }

        .controls-area::-webkit-scrollbar { width: 6px; }
        .controls-area::-webkit-scrollbar-thumb { background: #d0d6d0; border-radius: 3px; }

        .preset-btn {
            position: relative;
            background: var(--btn-base);
            border: none;
            padding: 14px 16px;
            color: var(--text-main);
            font-family: 'Noto Sans SC', sans-serif;
            font-weight: 600; font-size: 0.95rem;
            text-align: left;
            cursor: pointer;
            border-radius: 8px;
            /* 软软的阴影 */
            box-shadow: 
                0 4px 0 #cfc9c0, /* 侧边阴影 */
                0 6px 6px rgba(139, 156, 139, 0.15);
            transition: all 0.15s;
            display: flex; align-items: center; gap: 10px;
