</head>
<body>

    <div class="raw-data" style="display: none;">$1</div>

    <div class="radio-case">
        <!-- 顶部：调频窗口 -->
        <div class="tuner-window">
            <div class="scale-plate">
                <div class="scale-lines"></div>
                <div class="scale-nums">
                    <span>88</span><span>92</span><span>96</span><span>100</span><span>104</span><span>108</span>
                </div>
                <div class="needle" id="needle"></div>
            </div>
            <div class="lcd-panel">
                <div style="display:flex; align-items:center;">
                    <span class="stereo-light on"></span> 
                    <span id="lcdFreq" class="lcd-freq">FM 88.0</span>
                </div>
                <div id="lcdText" class="lcd-text">SPRING VIBES...</div>
            </div>
        </div>

        <div class="speaker-bar"></div>

        <!-- 底部：按钮列表 -->
        <div class="controls-area" id="optionsList"></div>

        <!-- 底部开关 -->
        <div class="switch-row">
            <button class="toggle-switch active" id="modeSingle">Single</button>
            <button class="toggle-switch" id="modeMulti">Multi</button>
            <button class="toggle-switch" id="btnClear">Clear</button>
            <button class="submit-knob" id="submitBtn" title="Tune In">♥</button>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            const list = document.getElementById('optionsList');
            const needle = document.getElementById('needle');
            const lcdFreq = document.getElementById('lcdFreq');
            const lcdText = document.getElementById('lcdText');
            const modeSingle = document.getElementById('modeSingle');
            const modeMulti = document.getElementById('modeMulti');
            const btnClear = document.getElementById('btnClear');
            const submitBtn = document.getElementById('submitBtn');

            let isMulti = false;
            let selected = [];

            // 1. 初始化
            const init = () => {
                const raw = document.querySelector('.raw-data').innerHTML;
                const txt = document.createElement('textarea');
                txt.innerHTML = raw;
                const content = txt.value.replace(/<\/?branches>/gi, '');
                const lines = content.split('\n').filter(l => l.trim());

                list.innerHTML = '';
                lines.forEach((text, i) => {
                    const btn = document.createElement('button');
                    btn.className = 'preset-btn';

                    const freqPos = 5 + (90 / (lines.length - 1 || 1)) * i;
                    const mhz = (88 + (20 * (freqPos/100))).toFixed(1); 

                    btn.innerHTML = `<span class="btn-led"></span> ${text}`;

                    btn.onmouseenter = () => {
                        needle.style.left = `${freqPos}%`;
                        lcdFreq.textContent = `FM ${mhz}`;
                        lcdText.textContent = text.length > 20 ? text.substring(0,20)+'...' : text;
                    };

                    btn.onclick = () => handleClick(btn, text);
                    list.appendChild(btn);
                });
            };

            // 2. 点击逻辑
            const handleClick = (btn, text) => {
                if (!isMulti) {
                    document.querySelectorAll('.preset-btn').forEach(b => b.classList.remove('pressed'));
                    btn.classList.add('pressed');
                    lcdText.textContent = "PLAYING: " + text;
                    sendData(text);
                } else {
                    btn.classList.toggle('pressed');
                    if(selected.includes(text)) selected = selected.filter(t => t !== text);
                    else selected.push(text);
                    submitBtn.style.display = selected.length > 0 ? 'flex' : 'none';
                    lcdText.textContent = `SELECTED: ${selected.length}`;
                }
            };

            const sendData = (text) => {
                try {
                    const target = window.top.document.querySelector('#send_textarea');
                    if (target) {
                        target.value = text;
                        target.dispatchEvent(new Event('input', { bubbles: true }));
                    } else {
                        console.log('Station:', text);
                    }
                } catch(e) {}
            };

            // 3. 模式切换
            const reset = () => {
                selected = [];
                document.querySelectorAll('.preset-btn').forEach(b => b.classList.remove('pressed'));
                submitBtn.style.display = 'none';
                lcdText.textContent = "SPRING VIBES...";
                needle.style.left = "5%";
            };

            modeSingle.onclick = () => {
                isMulti = false;
                modeSingle.classList.add('active');
                modeMulti.classList.remove('active');
                reset();
            };

            modeMulti.onclick = () => {
                isMulti = true;
                modeMulti.classList.add('active');
                modeSingle.classList.remove('active');
                reset();
            };

            btnClear.onclick = () => {
                try {
                    const target = window.top.document.querySelector('#send_textarea');
                    if (target) {
                        target.value = '';
                        target.dispatchEvent(new Event('input', { bubbles: true }));
                    }
                } catch(e) {}
                reset();
            };

            submitBtn.onclick = () => {
                if(selected.length) sendData(selected.join('\n\n'));
            };

            init();
        });
    </script>
</body>
</html>
