            border-radius: 50%;
            position: relative; z-index: 2;
            display: flex; justify-content: center; align-items: center;
            font-size: 20px; color: white;
        }

        @keyframes spin { 100% { transform: rotate(360deg); } }
        .playing .vinyl-record { animation: spin 5s linear infinite; }

        .reading-mode .album-area { transform: scale(0.6) translateY(-10px); padding: 0; margin-bottom: -30px; }

        /* Track Info */
        .track-info { text-align: center; padding: 0 15px; z-index: 2; }
        .song-title { font-size: 1.2em; font-weight: 700; color: var(--text-main); margin-bottom: 4px; white-space: nowrap; overflow: hidden; text-overflow: ellipsis; }
        .artist-name { font-size: 0.85em; color: var(--accent); font-family: "Fredoka", sans-serif; display: flex; align-items: center; justify-content: center; gap: 5px; }

        .explicit-tag {
            border: 1px solid var(--text-sub); color: var(--text-sub);
            font-size: 0.6em; padding: 0px 3px; border-radius: 3px;
            display: none; vertical-align: middle;
        }

        /* Progress Bar */
        .progress-container { padding: 15px 25px 5px; }
        .progress-bar { height: 3px; background: var(--control-bg); border-radius: 2px; position: relative; }
        .progress-fill { width: 30%; height: 100%; background: var(--accent); border-radius: 2px; position: absolute; }
        .progress-time { display: flex; justify-content: space-between; font-size: 9px; color: var(--text-sub); margin-top: 4px; }

        /* Controls */
        .controls {
            display: flex; justify-content: center; align-items: center; gap: 20px;
            padding-bottom: 15px;
        }
        .btn-icon { color: var(--text-main); font-size: 1.1em; cursor: pointer; transition: transform 0.2s; }
        .btn-icon:hover { transform: scale(1.1); color: var(--accent); }

        .play-btn {
            width: 50px; height: 50px;
            background: var(--play-btn-bg);
            border-radius: 50%;
            display: flex; justify-content: center; align-items: center;
            color: var(--play-btn-icon);
            font-size: 1.3em;
            box-shadow: 0 4px 10px rgba(0,0,0,0.15);
            cursor: pointer;
            transition: transform 0.2s;
        }
        .play-btn:active { transform: scale(0.9); }

        /* Lyrics / Content Area */
        .lyrics-sheet {
            background: var(--lyric-bg);
            border-radius: 20px 20px 0 0;
            overflow: hidden;
            max-height: 0;
            transition: max-height 0.6s cubic-bezier(0.65, 0, 0.35, 1);
            position: relative;
        }
        .reading-mode .lyrics-sheet { max-height: 800px; padding-bottom: 20px; box-shadow: 0 -5px 20px rgba(0,0,0,0.05); }

        .lyrics-scroll {
            padding: 25px 20px;
            overflow-y: auto;
        }

        .module { margin-bottom: 20px; }
        .module-title { 
            font-family: "Fredoka", sans-serif; 
            color: var(--accent); 
            font-size: 0.85em; letter-spacing: 1px; 
            margin-bottom: 8px; font-weight: bold;
        }

        .text-line { line-height: 1.7; color: var(--text-main); margin-bottom: 6px; font-size: 0.9em; }
        .scene-text { text-align: center; color: var(--text-sub); font-style: italic; }
        .highlight-text { background: var(--lyric-highlight); padding: 0 2px; border-radius: 2px; color: var(--accent); font-weight: bold;}

        .avatar-row { display: flex; gap: 8px; overflow-x: auto; padding-bottom: 8px; }
        .avatar-card { 
            min-width: 90px; 
            background: var(--bg-color); 
            padding: 8px; border-radius: 8px; 
            text-align: center; border: 1px solid var(--text-sub);
        }
        .avatar-name { font-weight: bold; font-size: 0.75em; color: var(--accent); }

    </style>
</head>
<body>
    <div class="raw-data" style="display: none;">\$1</div>

    <div class="phone-frame" id="main-frame">
        <!-- Status Bar: Modified Order -->
        <div class="status-bar">
            <!-- Left Side: Signal -->
            <div>5G <i class="fa-solid fa-signal"></i></div>
            <!-- Right Side: Theme Switch -->
            <div id="theme-btn" class="theme-switch">☾</div>
        </div>

        <!-- Album Art -->
        <div class="album-area">
            <div class="vinyl-record">
                <div class="vinyl-label"><i class="fa-solid fa-music"></i></div>
            </div>
        </div>

        <!-- Track Info -->
        <div class="track-info">
            <div class="song-title serial">Loading...</div>
            <div class="artist-name">
                <span class="explicit-tag">E</span>
                <span class="time">Artist Name</span>
            </div>
        </div>

        <!-- Progress -->
        <div class="progress-container">
            <div class="progress-bar"><div class="progress-fill"></div></div>
            <div class="progress-time">
                <span>0:00</span>
                <span>-3:42</span>
            </div>
        </div>

        <!-- Controls -->
        <div class="controls">
            <i class="fa-solid fa-backward-step btn-icon"></i>
            <div class="play-btn" id="play-toggle">
                <i class="fa-solid fa-play" id="play-icon"></i>
            </div>
            <i class="fa-solid fa-forward-step btn-icon"></i>
        </div>

        <!-- "Lyrics" Content -->
        <div class="lyrics-sheet">
            <div class="lyrics-scroll">

                <div class="module scene">
                    <div class="module-title">SCENARIO</div>
                    <div class="scene-content"></div>
                </div>

                <div class="module plot">
                    <div class="module-title">LYRICS / PLOT</div>
                    <div class="plot-content"></div>
                </div>

                <div class="module relation" style="display:none;">
                    <div class="module-title">FEATURING</div>
                    <div class="avatar-row relation-content"></div>
                </div>

                <div class="module echoes" style="display:none;">
                    <div class="module-title">AD-LIBS</div>
                    <div class="echoes-content"></div>
                </div>

                <!-- Extras -->
                <div id="extras-container"></div>
            </div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const regex = /serial[:：]\s*([^\n]*)\s*time[:：]\s*([^\n]*)(?:\s*NSFW[:：]\s*([\s\S]*?))?\s*scene[:：]\s*([\s\S]*?)\s*plot[:：]\s*([\s\S]*?)(?:\s*relation[:：]\s*([\s\S]*?))?(?:\s*echoes[:：]\s*([\s\S]*?))?(?:\s*archived[:：]\s*([\s\S]*?))?(?:\s*enigma[:：]\s*([\s\S]*?))?(?:\s*seeds[:：]\s*([\s\S]*))?$/i;

            function parseAndRender() {
                const rawDataElement = document.querySelector('.raw-data');
                if (!rawDataElement || !rawDataElement.textContent.trim()) return;

                const rawText = rawDataElement.textContent.trim();
                const match = rawText.match(regex);

                if (match) {
                    const [_, serial, time, nsfw, scene, plot, relation, echoes, archived, enigma, seeds] = match;

                    document.querySelector('.serial').textContent = serial.trim();
                    document.querySelector('.time').textContent = time.trim();
                    const nsfwTag = document.querySelector('.explicit-tag');
                    if (nsfw && nsfw.trim() && nsfw.trim()[0] !== '0') {
                        nsfwTag.style.display = 'inline-block';
                    } else { nsfwTag.style.display = 'none'; }

                    const plotContent = document.querySelector('.plot-content');
                    plotContent.innerHTML = '';
                    plot.trim().split('\n').forEach(line => {
                        const p = document.createElement('div');
                        p.className = 'text-line';
                        p.textContent = line.trim();
                        if (line.includes('【')) p.classList.add('highlight-text');
                        plotContent.appendChild(p);
                    });

                    const sceneContent = document.querySelector('.scene-content');
                    sceneContent.innerHTML = '';
                    scene.trim().split('\n').forEach(line => {
                        const p = document.createElement('div');
                        p.className = 'text-line scene-text';
                        p.textContent = line.trim();
                        sceneContent.appendChild(p);
                    });

                    const relationMod = document.querySelector('.relation');
                    if (relation && relation.trim()) {
                        relationMod.style.display = 'block';
                        const container = relationMod.querySelector('.relation-content');
                        container.innerHTML = '';
                        relation.trim().split('◈').forEach(chunk => {
                            if (!chunk.trim()) return;
                            const card = document.createElement('div');
                            card.className = 'avatar-card';
                            const lines = chunk.trim().split('\n');
                            card.innerHTML = `<div class="avatar-name">${lines[0].trim()}</div><div style="font-size:0.7em;">${lines.slice(1).join(' ')}</div>`;
                            container.appendChild(card);
                        });
                    }

                    const echoesMod = document.querySelector('.echoes');
                    if (echoes && echoes.trim()) {
                        const echoLines = echoes.trim().split('\n').filter(line => line.trim().startsWith('-'));
                        if(echoLines.length > 0) {
                            echoesMod.style.display = 'block';
                            const container = echoesMod.querySelector('.echoes-content');
                            container.innerHTML = '';
                            echoLines.forEach(line => {
                                const p = document.createElement('div');
                                p.className = 'text-line';
                                p.style.fontStyle = 'italic';
                                p.textContent = line.trim();
                                container.appendChild(p);
                            });
                        }
                    }

                    const extrasContainer = document.getElementById('extras-container');
                    const addExtra = (title, content) => {
                        if(!content || !content.trim()) return;
                        const div = document.createElement('div');
                        div.className = 'module';
                        div.innerHTML = `<div class="module-title">${title}</div><div class="text-line">${content}</div>`;
                        extrasContainer.appendChild(div);
                    };
                    addExtra("BONUS TRACKS", archived);
                    addExtra("HIDDEN VERSES", enigma);
                    addExtra("UP NEXT", seeds);
                }
            }

            const playBtn = document.getElementById('play-toggle');
            const playIcon = document.getElementById('play-icon');
            const mainFrame = document.getElementById('main-frame');
            const themeBtn = document.getElementById('theme-btn');

            playBtn.addEventListener('click', () => {
                const isPlaying = mainFrame.classList.toggle('playing');
                mainFrame.classList.toggle('reading-mode'); 

                if (isPlaying) {
                    playIcon.classList.remove('fa-play');
                    playIcon.classList.add('fa-pause');
                } else {
                    playIcon.classList.remove('fa-pause');
                    playIcon.classList.add('fa-play');
                }
            });

            themeBtn.addEventListener('click', () => {
                const isDark = document.body.classList.toggle('dark-mode');
                themeBtn.textContent = isDark ? '☀' : '☾';
                localStorage.setItem('vinyl-mini-theme-v2', isDark ? 'dark' : 'light');
            });

            parseAndRender();
            const savedTheme = localStorage.getItem('vinyl-mini-theme-v2');
            if(savedTheme === 'dark') {
                document.body.classList.add('dark-mode');
                themeBtn.textContent = '☀';
            }
        });
    </script>
</body>
</html>
