  function smartScroll(container, targetPos) {
    if (!container) return;
    const currentPos = container.scrollTop;
    const diff = targetPos - currentPos;
    const threshold = 600;

    if (Math.abs(diff) > threshold) {
      const jumpTo = diff > 0 ? targetPos - threshold : targetPos + threshold;
      container.scrollTop = jumpTo;
    }

    window.requestAnimationFrame(() => {
      container.scrollTo({
        top: Math.max(0, targetPos),
        behavior: 'smooth',
      });
    });
  }

  // ========== å®½åº¦è‡ªé€‚åº” ==========
  function autoFitPanelWidth() {
    const parentDoc = window.parent.document;
    const panel = parentDoc.getElementById(PANEL_ID);
    if (!panel) return;

    const screenWidth = window.parent.innerWidth || window.innerWidth;
    const screenHeight = window.parent.innerHeight || window.innerHeight;

    let targetWidth, leftPos;

    if (isMobile) {
      targetWidth = screenWidth * 0.95;
      panel.style.height = screenHeight * 0.6 + 'px';
    } else {
      targetWidth = Math.min(1200, screenWidth * 0.92);
    }

    leftPos = (screenWidth - targetWidth) / 2;

    panel.style.width = `${targetWidth}px`;
    panel.style.left = `${leftPos}px`;

    const currentTop = parseFloat(panel.style.top) || 60;
    if (currentTop < 0 || currentTop > screenHeight - 100) {
      panel.style.top = '60px';
    }
  }

  function loadScript(url, parentDoc) {
    return new Promise((resolve, reject) => {
