      script.src = url;
      script.onload = resolve;
      script.onerror = reject;
      parentDoc.head.appendChild(script);
    });
  }

  function ensureElementInViewport(element) {
    if (!element || element.id !== FLOATING_BALL_ID) return;
    const rect = element.getBoundingClientRect();
    const viewportWidth = window.innerWidth;
    const viewportHeight = window.innerHeight;
    let currentLeft = parseFloat(element.style.left) || 20;
    let currentTop = parseFloat(element.style.top) || 20;
    let newLeft = Math.max(20, Math.min(currentLeft, viewportWidth - rect.width - 20));
    let newTop = Math.max(20, Math.min(currentTop, viewportHeight - rect.height - 20));
    element.style.left = newLeft + 'px';
    element.style.top = newTop + 'px';
  }

