  function triggerSave() {
    const parent$ = window.parent.$;
    const parentDoc = window.parent.document;
    const $native = parent$(`#${NATIVE_CSS_TEXTAREA_ID}`);
    if ($native.length) {
      $native[0].dispatchEvent(new Event('input', { bubbles: true }));
      $native[0].dispatchEvent(new Event('change', { bubbles: true }));
    }
    const btn = parentDoc.querySelector('#ui-preset-update-button');
    if (btn && window.getComputedStyle(btn).display !== 'none') btn.click();
    else if (typeof window.parent.saveSettingsDebounced === 'function') {
      window.parent.saveSettingsDebounced();
      toastr.info('å·²æš‚å­˜è‡³æœ¬åœ°ç¼“å­˜', 'CSSç¼–è¾‘å™¨');
    }
