  function readAndRenderCSS() {
    const parent$ = window.parent.$;
    const cssText = parent$(`#${NATIVE_CSS_TEXTAREA_ID}`).val() || '';
    const container = parent$(`#${PALETTE_CONTAINER_ID}`);
    container.empty();
    CSS_BLOCK_REGEX.lastIndex = 0;
    let match;
    while ((match = CSS_BLOCK_REGEX.exec(cssText)) !== null) {
      const rawHeader = match[1],
        body = match[2];
      const cleanBody = body.replace(/\/\*[\s\S]*?\*\//g, '');
      let displayTitle = '';
      const commentRegex = /\/\*([\s\S]*?)\*\//g;
      let lastMatch = null,
        temp;
      while ((temp = commentRegex.exec(rawHeader)) !== null) lastMatch = temp;
      if (lastMatch) {
        const comment = lastMatch[1]
          .replace(/^\/\*+|\*+\/$/g, '')
          .replace(/^[=\-~\s]+|[=\-~\s]+$/g, '')
          .trim();
        const selector = rawHeader
          .substring(lastMatch.index + lastMatch[0].length)
          .trim()
          .replace(/\s+/g, ' ');

        if (rawHeader.includes(':root')) {
            displayTitle = selector;
        } else {
            displayTitle = comment ? (selector ? `${comment} | ${selector}` : comment) : selector;
        }
      } else {
        displayTitle = rawHeader.trim().replace(/\s+/g, ' ');
      }
      const props = [];
      cleanBody.split(';').forEach(line => {
        const colon = line.indexOf(':');
        if (colon !== -1) {
          let k = line
            .substring(0, colon)
            .replace(/\/\*[\s\S]*?\*\//g, '')
            .trim();
          let v = line.substring(colon + 1).trim();

          COLOR_REGEX.lastIndex = 0;
          if (COLOR_REGEX.test(v)) props.push({ k, v });
        }
      });
      if (props.length > 0) {
        const card = $('<div class="vce-card"></div>');
        card.append($(`<div class="vce-card-header" title="${displayTitle}">${displayTitle}</div>`));
        props.forEach(p => {
          const row = $('<div class="vce-prop-row"><div class="vce-prop-name">' + p.k + '</div></div>');
          const regex = new RegExp(COLOR_REGEX.source, COLOR_REGEX.flags);
          let m,
            idx = 0;
          while ((m = regex.exec(p.v)) !== null) row.append(createColorControl(rawHeader.trim(), p.k, m[0], idx++));
          card.append(row);
        });
        container.append(card);
      }
    }
  }

  function autoResizeInput(input) {
    const val = input.value;
    input.style.width = val.length * 7.5 + 5 + 'px';
  }

  function createColorControl(selector, propKey, color, colorIndex) {
    const parent$ = window.parent.$;
    const wrapper = $('<div class="vce-color-wrapper"></div>');
    const pickerId = `vce-pk-${Math.random().toString(36).substr(2, 5)}`;
    const picker = $(
      `<toolcool-color-picker id="${pickerId}" color="${color}" class="vce-picker"></toolcool-color-picker>`,
    );
    const input = $(`<input type="text" class="vce-color-input" value="${color}">`);
    setTimeout(() => autoResizeInput(input[0]), 0);
    wrapper.append(picker).append(input);
    const sync = newColor => {
      const $native = parent$(`#${NATIVE_CSS_TEXTAREA_ID}`);
      let cssText = $native.val();
      const escaped = selector.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
      const blockRegex = new RegExp(`(${escaped}\\s*\\{)([^}]+)(\\})`, 'g');
      const newCss = cssText.replace(blockRegex, (m, prefix, content, suffix) => {
        const propRegex = new RegExp(`(^|[;\\{\\s])(${propKey}\\s*:\\s*)([^;]+)(;?)`, 'gi');
        return (
          prefix +
          content.replace(propRegex, (m2, pStart, pPrefix, pValue, pSuffix) => {
            let cur = 0;
            const localCol = new RegExp(COLOR_REGEX.source, COLOR_REGEX.flags);
            return `${pStart}${pPrefix}${pValue.replace(localCol, old =>
              cur++ === colorIndex ? newColor : old,
            )}${pSuffix}`;
          }) +
          suffix
        );
      });
      if (newCss !== cssText) {
        $native.val(newCss);
        $native[0].dispatchEvent(new Event('input', { bubbles: true }));
        $native[0].dispatchEvent(new Event('change', { bubbles: true }));
        if (codeEditorInstance) codeEditorInstance.setValue(newCss);
        const styleEl = window.parent.document.getElementById('custom-style');
        if (styleEl) styleEl.textContent = newCss;
      }
    };

    // æ ¸å¿ƒä¿®å¤ï¼šå»¶è¿Ÿç»‘å®šchangeäº‹ä»¶ï¼Œé¿å…åˆå§‹åŒ–æ—¶è‡ªåŠ¨è½¬æ¢é¢œè‰²æ ¼å¼
    setTimeout(() => {
        picker.on('change', e => {
          const c = e.detail.rgba;
          if (c !== input.val()) {
            input.val(c);
            autoResizeInput(input[0]);
            sync(c);
          }
        });
    }, 200);

    input.on('input', e => {
      autoResizeInput(e.target);
    });
    input.on('change', () => {
      const c = input.val();
      const el = window.parent.document.getElementById(pickerId);
      if (el) el.color = c;
      autoResizeInput(input[0]);
      sync(c);
    });
    return wrapper;
  }

  function getDynamicVars(cm) {
    const text = cm.getValue();
    const rootMatch = text.match(/:root\s*\{([\s\S]*?)\}/);
    if (!rootMatch) return [];
    const content = rootMatch[1];
    const vars = content.match(/--[^\s:;{}]+/g);
    return vars ? [...new Set(vars)] : [];
  }

  function handleEnterKey(cm) {
    cm.execCommand('newlineAndIndent');
    let i = cm.getCursor().line - 1;
    let closedBraces = 0;
    while (i >= 0) {
      const text = cm.getLine(i);
      for (let charIdx = text.length - 1; charIdx >= 0; charIdx--) {
        const char = text[charIdx];
        if (char === '}') {
          closedBraces++;
        } else if (char === '{') {
          if (closedBraces > 0) {
            closedBraces--;
          } else {
            if (text.includes(':root')) {
              cm.replaceSelection('--');
            }
            return;
          }
        }
      }
      i--;
    }
  }

