  // ========== æ ¸å¿ƒä¿®å¤: é˜²æŠ–åŒæ­¥ä¸ç§»åŠ¨ç«¯å…¼å®¹ ==========
  function initCodeMirror() {
    const parentDoc = window.parent.document;
    const container = parentDoc.getElementById(EDITOR_CONTAINER_ID);

    codeEditorInstance = parent.CodeMirror(container, {
      mode: 'css',
      theme: 'material-darker',
      lineNumbers: true,
      lineWrapping: true,
      // æ ¸å¿ƒä¿®å¤ï¼šç”µè„‘ç«¯ç”¨ textarea (è§£å†³ç‚¹å‡»é—®é¢˜)ï¼Œæ‰‹æœºç«¯ç”¨ contenteditable (è§£å†³é€‰å­—é—®é¢˜)
      inputStyle: isMobile ? 'contenteditable' : 'textarea', 
      spellcheck: false, 
      autocorrect: false,
      autocapitalize: false,
      extraKeys: {
        Enter: handleEnterKey,
      },
    });

    // 2. æ ¸å¿ƒä¿®å¤ï¼šå®‰å“åˆ é™¤é”®å¤±æ•ˆçš„è¡¥ä¸
    if (isMobile) {
        codeEditorInstance.on('keydown', (cm, e) => {
            if (e.keyCode === 8 || e.keyCode === 229) {
                // å…è®¸é»˜è®¤è¡Œä¸ºï¼Œåç»­ç”±ç›‘å¬å™¨å¤„ç†
            }
        });
        const inputDiv = codeEditorInstance.getInputField();
        if (inputDiv) {
            inputDiv.addEventListener('input', (e) => {
                if (e.inputType === 'deleteContentBackward') {
                    // æ ¸å¿ƒä¿®å¤ï¼šç§»åŠ¨ç«¯æœ‰é€‰åŒºæ—¶ï¼Œä¸€é”®åˆ é™¤é€‰åŒºå†…å®¹
                    if (codeEditorInstance.somethingSelected()) {
                        codeEditorInstance.replaceSelection('');
                    }
                }
            });
        }
    }

    // 3. æ ¸å¿ƒä¿®å¤ï¼šç›‘å¬å…‰æ ‡æ´»åŠ¨ï¼Œæ§åˆ¶å½“å‰è¡Œæ ·å¼ï¼ˆè§£å†³çº¢è‰²èƒŒæ™¯å¹²æ‰°ï¼‰
    codeEditorInstance.on('cursorActivity', () => {
        const cur = codeEditorInstance.getCursor();
        const newHandle = codeEditorInstance.getLineHandle(cur.line);
        if (activeLineHandle && activeLineHandle !== newHandle) {
            codeEditorInstance.removeLineClass(activeLineHandle, 'wrap', 'vce-active-line');
        }
        if (activeLineHandle !== newHandle) {
            activeLineHandle = newHandle;
            codeEditorInstance.addLineClass(activeLineHandle, 'wrap', 'vce-active-line');
        }
    });
    activeLineHandle = codeEditorInstance.getLineHandle(0);
    codeEditorInstance.addLineClass(activeLineHandle, 'wrap', 'vce-active-line');

    // ========== æ ¸å¿ƒä¿®å¤ï¼šäº‹ä»¶åˆ†ç¦»é€»è¾‘ ==========

    // PCç«¯ï¼šåªä½¿ç”¨ inputRead ç›‘å¬è¾“å…¥å’Œè§¦å‘è¡¥å…¨
    if (!isMobile) {
        codeEditorInstance.on('inputRead', function (cm, change) {
            if (change.origin === '+input') {
                const text = change.text[0];
                handleTyping(cm, text);

                // ç¬¦å·è¡¥å…¨åçš„è‡ªåŠ¨è§¦å‘æ£€æŸ¥
                if (/[a-zA-Z!-\.\u4e00-\u9fa5\u00A0-\uFFFF]/.test(text)) {
                    triggerAutocomplete(cm);
                }
            }
        });
    }

    // é€šç”¨/æ‰‹æœºç«¯ï¼šchange ç›‘å¬
    codeEditorInstance.on('change', (cm, change) => {
      // 1. åŒæ­¥åˆ°åŸç”Ÿ textarea (é˜²æŠ–)
      clearTimeout(syncTimeout);
      syncTimeout = setTimeout(() => {
        const val = cm.getValue();
        const $native = window.parent.$(`#${NATIVE_CSS_TEXTAREA_ID}`);
        if ($native.val() !== val) {
          $native.val(val);
          $native[0].dispatchEvent(new Event('input', { bubbles: true }));
          $native[0].dispatchEvent(new Event('change', { bubbles: true }));
        }
      }, 300);

      const isInputOrDelete = change.origin !== 'setValue'; // åŒ…å« +input, +delete, *compose ç­‰

      // 2. æ‰‹æœºç«¯ç‰¹æœ‰é€»è¾‘ï¼šåœ¨ change ä¸­å¤„ç†è¾“å…¥å’Œè¡¥å…¨ (å› ä¸º inputRead ä¸å¯é )
      if (isMobile && isInputOrDelete) {
          // æ‰‹æœºç«¯ç¬¦å·è¡¥å…¨é€»è¾‘
          if (change.text && change.text.length === 1 && change.origin === '+input') {
              const char = change.text[0];
              // æ£€æŸ¥æ˜¯å¦æ˜¯éœ€è¦é…å¯¹çš„ç¬¦å·
              if ([':', '(', '{'].includes(char)) {
                 handleTyping(cm, char);
              }
          }

          // æ‰‹æœºç«¯è‡ªåŠ¨è¡¥å…¨è§¦å‘é€»è¾‘
          const cur = cm.getCursor();
          const token = cm.getTokenAt(cur);
          // æ’é™¤åˆ†å·ã€ç©ºæ ¼ã€èŠ±æ‹¬å·ç­‰ç»“æŸç¬¦ï¼Œé¿å…é¢‘ç¹è·³å‡ºå¹²æ‰°
          // æ³¨æ„ï¼šåˆ é™¤æ“ä½œ change.text å¯èƒ½ä¸ºç©ºï¼Œæ‰€ä»¥ç›´æ¥æŸ¥ token
          if (token && token.string.trim().length > 0) {
              const lastChar = token.string.slice(-1);
              const validTypes = ['property', 'variable-2', 'atom', 'keyword', 'def', 'string-2'];
              // åŠ å…¥ . å…è®¸ç±»åè§¦å‘ï¼Œä¸”å…è®¸ä¸­æ–‡/Unicode
              if ((validTypes.includes(token.type) || /^[a-zA-Z0-9!-\.\u4e00-\u9fa5\u00A0-\uFFFF]/.test(token.string)) && !/[;\{\}\(\)\s]/.test(lastChar)) {
                   if (!cm.state.completionActive) {
                       triggerAutocomplete(cm);
                   }
              }
          }
      }
      // PCç«¯åˆ é™¤è§¦å‘é€»è¾‘ (è¾“å…¥é€»è¾‘åœ¨ inputRead)
      else if (!isMobile && change.origin === '+delete') {
           const cur = cm.getCursor();
           const token = cm.getTokenAt(cur);
           if (token && token.string.trim().length > 0 && /^[a-zA-Z0-9!-\.\u4e00-\u9fa5\u00A0-\uFFFF]/.test(token.string)) {
               if (!cm.state.completionActive) {
                   triggerAutocomplete(cm);
               }
           }
      }

      // 3. æ³¨é‡Šå¿«æ·é”®é€»è¾‘ (é€šç”¨)
      const lastChange = change.text[0];
      if (lastChange && lastChange.includes('æ³¨é‡Š')) {
        const cur = cm.getCursor();
        const lineText = cm.getLine(cur.line);
        const pos = lineText.lastIndexOf('æ³¨é‡Š');
        if (pos !== -1) {
          cm.replaceRange('/*  */', { line: cur.line, ch: pos }, { line: cur.line, ch: pos + 2 });
          cm.setCursor({ line: cur.line, ch: pos + 3 });
        }
      }
    });
  }

  // ç»Ÿä¸€çš„æ‰“å­—å¤„ç†å‡½æ•° (ç¬¦å·é…å¯¹)
  function handleTyping(cm, text) {
    if (!text) return;
    const cur = cm.getCursor();

    if (text === '(') {
      cm.replaceRange(')', cur); 
      cm.setCursor(cur); // ä¿®å¤ï¼šæ‹¬å·è¡¥å…¨åï¼Œå…‰æ ‡å›é€€åˆ°æ‹¬å·ä¸­é—´
      return;
    } else if (text === '{') {
      cm.replaceRange('}', cur);
      cm.setCursor(cur); // ä¿®å¤ï¼šæ‹¬å·è¡¥å…¨åï¼Œå…‰æ ‡å›é€€åˆ°æ‹¬å·ä¸­é—´
      return;
    } else if (text === ':') {
      cm.replaceRange(' ;', cur);
      cm.setCursor({ line: cur.line, ch: cur.ch + 1 });
      return;
    } 
  }

  function triggerAutocomplete(cm) {
    if (cm.state.completionActive) return; 

    const dynamicVars = getDynamicVars(cm);
    cm.showHint({
      completeSingle: false, 
      // æ ¸å¿ƒä¿®å¤ï¼šç”µè„‘ç«¯(true)è‡ªåŠ¨æ”¶èµ·ï¼Œæ‰‹æœºç«¯(false)é˜²æ­¢é”®ç›˜æ”¶èµ·
      closeOnUnfocus: !isMobile, 
      extraKeys: {
        "Up": function() {},
        "Down": function() {}
      },
      hint: function (cm) {
        const cursor = cm.getCursor();
        const token = cm.getTokenAt(cursor);
        const currentWord = token.string.toLowerCase();
        const start = token.start;
        const end = cursor.ch;

        let allItems = [
          ...CSS_PROPERTIES,
          ...ST_VARIABLES,
          ...dynamicVars,
          ...CSS_FUNCTIONS,
          ...CSS_VALUES_CLASSES,
          'root',
        ];

        const list = allItems
          .filter(item => {
            const lowerItem = item.toLowerCase();
            return (
              lowerItem.includes(currentWord) ||
              (item.startsWith('--') && lowerItem.replace(/^--/, '').includes(currentWord))
            );
          })
          .map(item => {
            const type = getHintType(item);
            let displayText = item;
            let text = item;
            let cursorOffset = 0;

            if (item === 'url()') {
                text = "url('')";
                cursorOffset = -2;
            } 
            else if (item.endsWith('()')) {
                text = item;
                cursorOffset = -1; 
            }
            else if (item === 'root') {
              text = ':root {}';
              cursorOffset = -1;
            } else if (item === 'content') {
              text = "content: '';";
              cursorOffset = -2;
              displayText = 'content*';
            } else if (type === 1) {
              text = item + ': ;';
              cursorOffset = -1;
              displayText = item + '*';
            } else if (type === 2) {
              cursorOffset = 0;
            }

            return {
              text: text,
              displayText: displayText,
              hint: function (cm, self, data) {
                cm.replaceRange(text, { line: cursor.line, ch: start }, { line: cursor.line, ch: end });
                if (cursorOffset !== 0) {
                  const newCursor = cm.getCursor();
                  cm.setCursor({ line: newCursor.line, ch: newCursor.ch + cursorOffset });
                }
                cm.focus();
              },
            };
          });

        return {
          list: list,
          from: parent.CodeMirror.Pos(cursor.line, start),
          to: parent.CodeMirror.Pos(cursor.line, end),
        };
      },
    });
  }

  // è¾…åŠ©å‡½æ•°ï¼šåˆ¤æ–­ç±»å‹
  function getHintType(item) {
      if (CSS_PROPERTIES.includes(item)) return 1;
      if (ST_VARIABLES.includes(item) || item.startsWith('--')) return 2;
      return 0;
  }

  function setupSearch(parent$, parentDoc) {
    const $vSearchInp = parent$('#visual-search-input'),
      $vSearchDrop = parent$('#visual-search-dropdown');

    $vSearchInp.on('input focus', function () {
      const q = $(this).val().trim().toLowerCase();
      $vSearchDrop.empty();
      if (!q) {
        $vSearchDrop.hide();
        return;
      }
      const cards = parent$('.vce-card');
      let found = false;
      cards.each(function (idx) {
        const head = $(this).find('.vce-card-header').text();
        if (head.toLowerCase().includes(q)) {
          found = true;
          const highlight = head.replace(
            new RegExp(`(${q.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')})`, 'gi'),
            '<span class="vce-highlight-text">$1</span>',
          );
          $vSearchDrop.append($(`<div class="vce-search-item" data-idx="${idx}">${highlight}</div>`));
        }
      });
      found ? $vSearchDrop.show() : $vSearchDrop.hide();
    });

    $vSearchDrop.on('click', '.vce-search-item', function () {
      const idx = $(this).data('idx'),
        $card = parent$('.vce-card').eq(idx),
        $area = parent$(`#${PALETTE_CONTAINER_ID}`);
      $vSearchInp.blur();
      if ($card.length && $area.length) {
        setTimeout(() => {
          smartScroll($area[0], $card.position().top + $area[0].scrollTop);
          $card.addClass('vce-flash-highlight');
          setTimeout(() => $card.removeClass('vce-flash-highlight'), 1200);
        }, 300);
      }
      $vSearchDrop.hide();
    });

    const $cSearchInp = parent$('#code-search-input'),
      $cSearchDrop = parent$('#code-search-dropdown');
    $cSearchInp.on('input focus', function () {
      const q = $(this).val().trim().toLowerCase();
      $cSearchDrop.empty();
      if (!q || !codeEditorInstance) {
        $cSearchDrop.hide();
        return;
      }
      const lines = codeEditorInstance.getValue().split('\n');
      let count = 0;
      for (let i = 0; i < lines.length; i++) {
        if (lines[i].toLowerCase().includes(q)) {
          const highlight = lines[i]
            .trim()
            .replace(
              new RegExp(`(${q.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')})`, 'gi'),
              '<span class="vce-highlight-text">$1</span>',
            );
          $cSearchDrop.append(
            $(
              `<div class="vce-search-item" data-line="${i}"><span class="vce-line-num">${
                i + 1
              }</span>${highlight}</div>`,
            ),
          );
          if (++count >= 100) break;
        }
      }
      count > 0 ? $cSearchDrop.show() : $cSearchDrop.hide();
    });

    $cSearchDrop.on('click', '.vce-search-item', function () {
      const line = $(this).data('line'),
        q = $cSearchInp.val().trim(),
        ch = codeEditorInstance.getLine(line).toLowerCase().indexOf(q.toLowerCase());
      $cSearchInp.blur();
      setTimeout(() => {
        codeEditorInstance.setSelection({ line: line, ch: ch }, { line: line, ch: ch + q.length });
        const scroller = parentDoc.querySelector('.CodeMirror-scroll');
        smartScroll(scroller, codeEditorInstance.charCoords({ line: line, ch: 0 }, 'local').top);
        if (window.innerWidth >= 768) codeEditorInstance.focus();
      }, 300);
      $cSearchDrop.hide();
    });

    parent$(parentDoc).on('click', e => {
      if (!parent$(e.target).closest('.vce-search-wrapper').length) {
        $vSearchDrop.hide();
        $cSearchDrop.hide();
      }
    });
  }

  function setupFloatingBase(p$, pDoc) {
    const panel = pDoc.getElementById(PANEL_ID),
      topRow = panel.querySelector('.panel-top-row'),
      ball = pDoc.getElementById(FLOATING_BALL_ID);

    // ä¿®å¤ï¼šæ‚¬æµ®çƒæ¶ˆå¤±é—®é¢˜
    p$(`#${MINIMIZE_BUTTON_ID}`).on('click', e => {
      e.stopPropagation();
      p$(`#${OVERLAY_ID}`).hide();
      p$(ball).removeClass('hidden');
      ensureElementInViewport(ball);
    });

    p$(`#${CLOSE_BUTTON_ID}`).on('click', e => {
      e.stopPropagation();
      p$(`#${OVERLAY_ID}`).hide();
      p$(ball).addClass('hidden');
    });

    const handleStart = e => {
      if (e.target.closest('button, .panel-tab')) return;
      isDragging = true;
      hasUserResized = true;
      const clientX = e.touches ? e.touches[0].clientX : e.clientX;
      const clientY = e.touches ? e.touches[0].clientY : e.clientY;
      const rect = panel.getBoundingClientRect();
      panelOffsetX = clientX - rect.left;
      panelOffsetY = clientY - rect.top;
      panel.style.transition = 'none';
      if (!e.touches) e.preventDefault();
    };

    const handleMove = e => {
      const clientX = e.touches ? e.touches[0].clientX : e.clientX;
      const clientY = e.touches ? e.touches[0].clientY : e.clientY;

      if (isDragging) {
        const newLeft = clientX - panelOffsetX;
        const newTop = clientY - panelOffsetY;
        panel.style.left = newLeft + 'px';
        panel.style.top = newTop + 'px';
        if (e.touches) e.preventDefault();
      } else if (isResizing) {
        const rect = panel.getBoundingClientRect();
        if (resizeDirection.includes('right') || resizeDirection.includes('corner'))
          panel.style.width = Math.max(300, clientX - rect.left) + 'px';
        if (resizeDirection.includes('bottom') || resizeDirection.includes('corner'))
          panel.style.height = Math.max(400, clientY - rect.top) + 'px';
        if (codeEditorInstance) codeEditorInstance.refresh();
        if (e.touches) e.preventDefault();
      }
    };

    const handleEnd = () => {
      isDragging = false;
      isResizing = false;
      panel.style.transition = '';
    };

    topRow.addEventListener('mousedown', handleStart);
    topRow.addEventListener('touchstart', handleStart, { passive: false });

    const handles = panel.querySelectorAll('.resize-handle');
    handles.forEach(h => {
      const startResize = e => {
        isResizing = true;
        hasUserResized = true;
        resizeDirection = h.className.split(' ')[1];
        e.preventDefault();
        e.stopPropagation();
      };
      h.addEventListener('mousedown', startResize);
      h.addEventListener('touchstart', startResize, { passive: false });
    });

    pDoc.addEventListener('mousemove', handleMove);
    pDoc.addEventListener('touchmove', handleMove, { passive: false });
    pDoc.addEventListener('mouseup', handleEnd);
    pDoc.addEventListener('touchend', handleEnd);

    p$(ball)
      .on('mousedown', e => {
        isBallDragging = true;
        ballStartX = e.clientX;
        ballStartY = e.clientY;
        const rect = ball.getBoundingClientRect();
        ballOffsetX = e.clientX - rect.left;
        ballOffsetY = e.clientY - rect.top;
        e.preventDefault();
        e.stopPropagation();
      })
      .on('click', e => {
        if (Math.abs(e.clientX - ballStartX) > 5 || Math.abs(e.clientY - ballStartY) > 5) return;
        p$(ball).addClass('hidden');
        p$(`#${OVERLAY_ID}`).css('display', 'flex');
        if (!hasUserResized) autoFitPanelWidth();
        if (codeEditorInstance)
          setTimeout(() => {
            codeEditorInstance.refresh();
            // ä¿®å¤ï¼šæ‰‹æœºç«¯å±•å¼€é¢æ¿æ—¶ä¸è¦focusï¼Œå¦åˆ™ä¼šå¼¹å‡ºé”®ç›˜
            const isMobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(
              navigator.userAgent,
            );
            if (!isMobile) codeEditorInstance.focus();
          }, 150);
      });
  }

  async function init() {
    await injectStylesAndLibraries();
    createAndInjectUI();
    bindEvents();
    window.parent.$(`#${OVERLAY_ID}`).hide();
    log('CSSç¼–è¾‘å™¨å·²å°±ç»ªï¼ˆæ‰‹æœºç«¯ä¿®å¤æœ€ç»ˆç‰ˆï¼‰');
  }
  setTimeout(init, 500);
})();

