let lastTap = {
  time: 0,
  target: null
};

// æå–çº¯æ–‡æœ¬ï¼Œå»é™¤HTMLæ ‡ç­¾
function getPlainText(html) {
  const div = document.createElement('div');
  div.innerHTML = html;
  return div.textContent || div.innerText || '';
}

// ç®€å•çš„æ–‡æœ¬åŒ¹é…
function findParagraphMatch(clickedElement, mesid) {
  const messageText = SillyTavern.chat[mesid].mes;
  const paragraphs = messageText.split(/(?:\r?\n){2,}/);

  const clickedText = $(clickedElement).text().trim();

  for (let i = 0; i < paragraphs.length; i++) {
    const paraText = getPlainText(paragraphs[i]).trim();
    if (paraText.includes(clickedText) || clickedText.includes(paraText)) {
      return i;
    }
  }

  return -1;
}

// è·å–<details>çš„å±•å¼€çŠ¶æ€
function getDetailsStates(mesid) {
  const $message = $(`[mesid="${mesid}"]`);
  const states = {};

