function handleTouchEnd(event) {
  const currentTime = new Date().getTime();
  const tapLength = currentTime - lastTap.time;
  const target = event.currentTarget;

  // åŒå‡»æ£€æµ‹ï¼ˆ300mså†…å¯¹åŒä¸€å…ƒç´ ä¸¤æ¬¡ç‚¹å‡»ï¼‰
  if (tapLength < 300 && tapLength > 0 && lastTap.target === target) {
    event.preventDefault();
    editParagraph.call(target, event);
    lastTap = { time: 0, target: null };
  } else {
    lastTap = { time: currentTime, target: target };

