    return;
  }

  // å¦‚æœresultæ˜¯undefinedæˆ–è€…ä¸åŸå§‹å†…å®¹ç›¸åŒï¼Œç›´æ¥è¿”å›
  if (result === undefined || contentToEdit === result) {
    return;
  }

  // ç¡®ä¿resultæ˜¯å­—ç¬¦ä¸²ç±»å‹
  const newContent = String(result);

  // æ›´æ–°æ¶ˆæ¯
  const freshText = SillyTavern.chat[mesid].mes;
  let freshParagraphs = freshText.split(/(?:\r?\n){2,}/);

  // ä¿®å¤ï¼šæ£€æŸ¥æ•°ç»„ç´¢å¼•æ˜¯å¦å­˜åœ¨
  if (!freshParagraphs[paraIndex]) {
    console.log("æ®µè½ç´¢å¼•æ— æ•ˆ");
    return;
  }

  if (currentPara.includes('<details') && currentPara.includes('</details>')) {
    if (isSummary) {
      freshParagraphs[paraIndex] = currentPara.replace(/<summary>[\s\S]*?<\/summary>/, `<summary>${newContent}</summary>`);
    } else {
      const summaryMatch = currentPara.match(/<summary>[\s\S]*?<\/summary>/);
      const summaryPart = summaryMatch ? summaryMatch[0] : '<summary>æ‘˜è¦</summary>';
