custom_css:
/*
* 作者：@小颂降临
* 创作感谢：旅程美化区帖子里的各位老师，代码参考了墨千尾（discord@这对吗/inkfoxxxx）以及玉元一老师
* 美化名称：崖山初雪（谭见雪专属美化）
* 本美化仅发布于DC社区，严禁二传/商用，二改自用随意
*/

/* ===== 字体导入 ===== */
@import url("https://fontsapi.zeoseven.com/415/main/result.css");

/* --- 全局字体 --- */
:not([class*="fa-"]):not(.fas):not(.far):not(.fal):not(.fab):not(.fa),
#send_textarea,
.menu_button_icon.fa-repeat,
.menu_button_icon.fa-bolt {
font-family: "WenYuan Serif SC VF";
}

/* ===== 全局控制 ===== */
:root {
  --自定义思维链文案:"˚⊹.白い 雪 の 季節 の恋人.♡ ⊹";
  --自定义"加载更多消息"文案:"展开更多消息";
  --自定义输入框文字:"爱若爱到大雪满弓刀地步";
}

/* 全局弹出面板 */
.drawer-content,
#table_drawer_content,
.options-content {
  background-color: rgba(219, 243, 255, 0.92) !important;
  border: 1px solid rgba(172, 222, 255, 0.6) !important;
  box-shadow: 0 4px 20px rgba(172, 222, 255, 0.3);
  backdrop-filter: blur(12px);
  -webkit-backdrop-filter: blur(12px);
}

/* ===== 顶栏 ===== */
#top-settings-holder {
    background: linear-gradient(135deg, rgba(249, 255, 255, 0.85), rgba(219, 243, 255, 0.85));
    box-shadow: 0 2px 12px rgba(172, 222, 255, 0.4);
    border: 1px solid rgba(172, 222, 255, 0.5);
    border-radius: 16px;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
}

#top-bar {
    border-radius: 15px !important;
    background-color: transparent !important;
    box-shadow: none !important;
    border: none !important;
}

/* 图标处理 */
#top-settings-holder .drawer-icon::before {
    content: '' !important;
    display: inline-block;
    width: 20px;
    height: 26px;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
}
#top-settings-holder .drawer-icon { opacity: 1; }

/* 图标URL映射 */
#leftNavDrawerIcon::before { background-image: url('https://z.wiki/u/YZvsmkoAD'); }
#sys-settings-button .drawer-icon::before { background-image: url('https://z.wiki/u/NjVpkg9zM'); }
#advanced-formatting-button .drawer-icon::before { background-image: url('https://z.wiki/u/YZvsmkoAD'); }
#WI-SP-button .drawer-icon::before { background-image: url('https://z.wiki/u/NjVpkg9zM'); }
#user-settings-button .drawer-icon::before { background-image: url('https://z.wiki/u/YZvsmkoAD'); }
#backgrounds-button .drawer-icon::before { background-image: url('https://z.wiki/u/NjVpkg9zM'); }
#extensions-settings-button .drawer-icon::before { background-image: url('https://z.wiki/u/YZvsmkoAD'); }
#table_drawer_icon::before {
    background-image: url('https://z.wiki/u/NjVpkg9zM');
}
#persona-management-button .drawer-icon::before { background-image: url('https://z.wiki/u/YZvsmkoAD'); }
#rightNavDrawerIcon::before { background-image: url('https://z.wiki/u/NjVpkg9zM'); }

/* ===== 底栏 ===== */
#send_form {
  background: linear-gradient(135deg, rgba(249, 255, 255, 0.9), rgba(219, 243, 255, 0.9));
  border-radius: 18px !important;
  border: 1px solid rgba(172, 222, 255, 0.6) !important;
  padding: 12px;
  box-shadow: 0 -2px 12px rgba(172, 222, 255, 0.3);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
}

#send_textarea {
  border-radius: 14px !important;
  clip-path: none !important;
  background-color: rgba(255, 255, 255, 0.7) !important;
  border: 1px solid rgba(172, 222, 255, 0.4) !important;
  color: #5A8AA6 !important;
}

/* 底栏图标 */
#send_form [class*="fa-"]::before {
  content: '' !important;
  display: inline-block;
  width: 22px;
  height: 22px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  vertical-align: middle;
}

#options_button::before { background-image: url('https://z.wiki/u/6qolT1k0q'); }
#extensionsMenuButton::before { background-image: url('https://z.wiki/u/mapcOyIT6'); }
#quick-reply-rocket-button i::before { background-image: url('https://z.wiki/u/KzBgtdinR'); }
#send_but::before { background-image: url('https://z.wiki/u/YZvsmkoAD'); }
#mes_stop i::before { background-image: url('https://z.wiki/u/UB4Bn6aeO'); }

/* ===== 聊天区域 ===== */
#chat {
  background-color: transparent !important;
  backdrop-filter: none !important;
  -webkit-backdrop-filter: none !important;
}

/* 聊天气泡整体布局 */
.mes {
    display: flex !important;
    align-items: center !important;
    flex-direction: column !important;
    gap: 0px;
    background: transparent !important;
    border: none !important;
    margin-bottom: 25px;
}

/* ========================================= */
/* !!! 头像容器与三元素布局 !!! */

/* 头像容器：变成一个固定的画框 */
.mes .mesAvatarWrapper {
    position: relative;
    width: 100%;
    height: 200px !important;
    display: flex;
    flex-direction: row;
    justify-content: flex-end;
    align-items: flex-end;
    margin-bottom: 12px;
    padding: 8px;
    box-sizing: border-box;

    border-radius: 12px;
    border: 2px solid rgba(172, 222, 255, 0.5);
    box-shadow: 0 4px 16px rgba(172, 222, 255, 0.3);
    overflow: hidden;
}

/* 头像图片：作为背景铺满容器 */
.mesAvatarWrapper .avatar {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    margin: 0;
    z-index: 1;
    pointer-events: auto;
}

.mes .avatar img {
    width: 100% !important;
    height: 100% !important;
    object-fit: cover !important;
    border-radius: 0 !important;
    border: none !important;
    box-shadow: none !important;
}

/* 三元素（楼层/时间/Token）：浮在图片右下角 */
.mesAvatarWrapper > *:not(.avatar) {
    position: relative;
    z-index: 10;
    display: inline-flex;
    align-items: center;
    margin: 0 0 0 6px;

    font-size: 0.75em;
    color: #4A7A96;
    font-weight: 600;

    background: rgba(230, 248, 255, 0.85); 
    border: 1px solid rgba(172, 222, 255, 0.8);
    padding: 2px 8px;
    border-radius: 8px;
    backdrop-filter: blur(5px);
    -webkit-backdrop-filter: blur(5px);
}
/* ========================================= */


/* 气泡内容区域 */
.mes .mes_block {
    background: linear-gradient(
        135deg,
        rgba(249, 255, 255, 0.75) 0%,
        rgba(219, 243, 255, 0.65) 100%
    );
    border: 1px solid rgba(172, 222, 255, 0.5);
    border-radius: 16px;
    padding: 18px 22px;
    box-shadow: 0 3px 15px rgba(172, 222, 255, 0.2);
    width: 100%;
    backdrop-filter: blur(8px);
    -webkit-backdrop-filter: blur(8px);
}

/* 用户气泡 */
.mes[is_user="true"] .mes_block {
    background: linear-gradient(
        135deg,
        rgba(209, 238, 253, 0.8) 0%,
        rgba(219, 243, 255, 0.7) 100%
    );
    border-color: rgba(172, 222, 255, 0.6);
}

/* AI角色气泡 */
.mes[is_user="false"] .mes_block {
    background: linear-gradient(
        135deg,
        rgba(249, 255, 255, 0.75) 0%,
        rgba(219, 243, 255, 0.65) 100%
    );
    border-color: rgba(172, 222, 255, 0.5);
}

/* ========================================= */
/* !!! 关键修改：消息按钮区域与间距 !!! */

.mes_buttons, .extraMesButtons {
    background: rgba(219, 243, 255, 0.5) !important;
    border-radius: 8px; /* 稍微调小圆角 */
    padding: 2px 6px; /* 缩小内边距 */
    margin-top: 15px; /* 增加与正文的距离，防止重叠 */
    width: fit-content; /* 宽度适应内容 */
}

/* 缩小按钮图标大小 */
.mes_button, .mes_buttons .fa-solid, .mes_buttons .fas,
.extraMesButtons .fa-solid, .extraMesButtons .fas {
    color: #6B9DB8 !important;
    font-size: 0.85em !important; /* 缩小图标，防止过大遮挡 */
}

/* ========================================= */

/* 消除最后一条消息间距 */
.mes.last_mes .mesAvatarWrapper {
    padding-bottom: 8px !important;
}

/* 文本样式 */
.mes .mes_text {
    padding: 0px;
    width: 100%;
    color: #5A8AA6;
    line-height: 1.75;
    margin-bottom: 5px; /* 在文本底部也增加一点保险间距 */
}

.mes .mes_text p {
    margin-bottom: 12px;
    color: #5A8AA6;
}

.mes .mes_text em {
    color: #6B9DB8;
    font-style: italic;
    background-color: rgba(172, 222, 255, 0.15);
    padding: 2px 5px;
    border-radius: 4px;
    -webkit-box-decoration-break: clone;
    box-decoration-break: clone;
}
/* 双引号文字颜色区分 */
.mes .mes_text q {
    display: inline;
    background-color: rgba(209, 238, 233, 0.7);
    color: #3B7A99 !important;
    font-weight: 500;
    border-radius: 4px;
    padding: 2px 5px;
    margin: 0 2px;
    -webkit-box-decoration-break: clone;
    box-decoration-break: clone;
}

.mes .name_text {
    color: #6B9DB8;
    font-weight: 500;
}

.mes .timestamp {
    color: rgba(107, 157, 184, 0.7);
    font-size: 0.85em;
}

/* ===== 滚动条与滑动条 ===== */
::-webkit-scrollbar {
    width: 8px;
    height: 8px;
    background-color: transparent;
}

::-webkit-scrollbar-track {
    background-color: rgba(219, 243, 255, 0.3);
    border-radius: 4px;
}

::-webkit-scrollbar-thumb {
    background: linear-gradient(to bottom, #ACDEFF, #D1EEFD);
    border-radius: 4px;
    border: none;
}

/* 滑动条 */
input[type="range"] {
    -webkit-appearance: none;
    appearance: none;
    width: 100%;
    background: transparent !important;
    cursor: pointer;
}

input[type="range"]::-webkit-slider-runnable-track {
    width: 100%;
    height: 6px;
    background: linear-gradient(to right, #ACDEFF, #D1EEFD);
    border-radius: 3px;
    border: 1px solid rgba(172, 222, 255, 0.4);
}

input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    margin-top: -8px;
    height: 20px;
    width: 20px;
    background-image: url('https://z.wiki/u/KzBgtdinR');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    background-color: transparent;
    border: none;
    border-radius: 0;
    box-shadow: none;
}

input[type="range"]::-moz-range-track {
    height: 6px;
    background: linear-gradient(to right, #ACDEFF, #D1EEFD);
    border-radius: 3px;
    border: 1px solid rgba(172, 222, 255, 0.4);
}

input[type="range"]::-moz-range-thumb {
    height: 20px;
    width: 20px;
    background-image: url('https://z.wiki/u/KzBgtdinR');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    background-color: transparent;
    border: none;
    border-radius: 0;
}

/* ===== 输入框提示文字 ===== */
#nonQRFormItems {
    position: relative;
}

#send_textarea::placeholder {
    color: transparent !important;
    text-shadow: none !important;
}

#nonQRFormItems:has(#send_textarea:placeholder-shown)::before {
    content: "爱若爱到大雪满弓刀地步";
    color: rgba(107, 157, 184, 0.6);
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    font-size: 0.4em;
    pointer-events: none;
    z-index: 10;
    white-space: nowrap;
}

/* ===== 正则+脚本易读+防误触 ===== */
#saved_regex_scripts,
#saved_scoped_scripts,
#saved_preset_scripts,
#global-script-list,
#character-script-list {
    margin-right: 80px !important;
}

.regex-script-label.flex-container,
.flex-container .script-item {
    flex-wrap: wrap !important;
    padding: 8px 12px;
    border-radius: 10px;
    margin-bottom: 6px;
    background: rgba(219, 243, 255, 0.6);
    border: 1px solid rgba(172, 222, 255, 0.5);
}

.regex-script-label.flex-container .regex_script_name,
.script-item .script-item-name {
    flex-basis: 100%;
    white-space: normal;
    overflow: visible;
    text-overflow: clip;
    word-break: break-word;
    font-weight: 500;
    font-size: 1.05em;
    color: #5A8AA6;
}

.regex-script-label.flex-container > .flex-container.flexnowrap,
.script-item .script-item-control {
    width: 100%;
    justify-content: flex-start;
}

/* ===== "加载更多消息"美化 ===== */
#show_more_messages {
    font-size: 0 !important;
    color: transparent !important;
    display: block;
    padding: 10px 24px;
    margin: 15px auto;
    width: fit-content;
    background: linear-gradient(135deg, #D1EEFD, #ACDEFF);
    border: 1px solid rgba(172, 222, 255, 0.6);
    border-radius: 16px;
    box-shadow: 0 2px 10px rgba(172, 222, 255, 0.3);
    cursor: pointer;
    backdrop-filter: blur(8px);
    -webkit-backdrop-filter: blur(8px);
}

#show_more_messages::before {
    content: "展开更多消息";
    font-size: 14px !important;
    color: #5A8AA6 !important;
}

/* ===== 思维链美化 ===== */
.mes_reasoning_summary {
    list-style: none;
    cursor: pointer;
    display: flex !important;
    align-items: center;
}

.mes_reasoning_header {
    background: linear-gradient(135deg, rgba(219, 243, 255, 0.6), rgba(209, 238, 253, 0.6));
    border: 1px solid rgba(172, 222, 255, 0.5);
    border-radius: 10px;
    padding: 8px 15px;
}

.mes_reasoning_header_title {
    font-size: 0 !important;
}

.mes_reasoning_header_title::before {
    content: '˚⊹.白い 雪 の 季節 の恋人.♡ ⊹';
    font-size: 13px;
    color: #6B9DB8;
    font-weight: 500;
}

.mes_reasoning_arrow {
    font-size: 0 !important;
}

.mes_reasoning_arrow::before {
    content: '▶';
    font-size: 11px;
    color: rgba(107, 157, 184, 0.7);
    display: inline-block;
}

details[open] .mes_reasoning_arrow::before {
    transform: rotate(90deg);
}

.mes_reasoning {
    margin-top: 6px;
    padding: 14px;
    background-color: rgba(219, 243, 255, 0.4);
    border-left: 3px solid #ACDEFF;
    border-radius: 0 8px 8px 0;
    color: #5A8AA6;
    font-size: 13px;
    line-height: 1.7;
}

/* ===== 收藏栏左右滑动 ===== */
.hotswap.avatars_inline {
    display: flex;
    flex-wrap: nowrap;
    overflow-x: auto;
    padding-right: 15px;
}

.hotswap.avatars_inline::-webkit-scrollbar {
    display: none;
}

.hotswap.avatars_inline {
    scrollbar-width: none;
}

/* ===== UI组件统一配色 ===== */
.menu_button, button {
    background: rgba(219, 243, 255, 0.7) !important;
    color: #5A8AA6 !important;
    border: 1px solid rgba(172, 222, 255, 0.5) !important;
    box-shadow: 0 2px 8px rgba(172, 222, 255, 0.2);
}

.menu_button:hover, button:hover {
    background: rgba(209, 238, 253, 0.8) !important;
    border-color: rgba(172, 222, 255, 0.7) !important;
}

select,
input:not([type="range"]):not([type="checkbox"]):not([type="radio"]),
textarea {
    background: rgba(255, 255, 255, 0.8) !important;
    color: #5A8AA6 !important;
    border: 1px solid rgba(172, 222, 255, 0.5) !important;
    border-radius: 8px !important;
}

/* 开关样式 */
.switch-btn, .checkbox-label .slider {
    background-color: rgba(219, 243, 255, 0.6) !important;
    border: 1px solid rgba(172, 222, 255, 0.5) !important;
}

.switch-btn::after, .checkbox-label .slider::before {
    background-color: #ACDEFF !important;
}

input:checked + .switch-btn,
input:checked + .slider,
.checkbox-label input:checked + .slider {
    background-color: #ACDEFF !important;
    border-color: #ACDEFF !important;
}

input:checked + .switch-btn::after,
input:checked + .slider::before,
.checkbox-label input:checked + .slider::before {
    background-color: #F9FFFF !important;
}

/* 勾选框 */
input[type="checkbox"], input[type="radio"] {
    -webkit-appearance: none !important;
    width: 18px !important;
    height: 18px !important;
    background-color: rgba(255, 255, 255, 0.8) !important;
    border: 1px solid rgba(172, 222, 255, 0.5) !important;
    border-radius: 4px;
}

input[type="checkbox"]:checked, input[type="radio"]:checked {
    background-color: #ACDEFF !important;
    border-color: #ACDEFF !important;
}

/* 扩展面板标题 */
.inline-drawer-toggle, .standoutHeader, .extension_button,
#user-settings-block h4, .inline-drawer-header {
    background-image: none !important;
    background-color: rgba(219, 243, 255, 0.7) !important;
    border: 1px solid rgba(172, 222, 255, 0.5) !important;
    color: #5A8AA6 !important;
    box-shadow: 0 2px 8px rgba(172, 222, 255, 0.2) !important;
}

/* 多选标签 */
.select2-container--default .select2-selection--multiple .select2-selection__choice {
    background-image: none !important;
    background-color: rgba(209, 238, 253, 0.8) !important;
    border: 1px solid rgba(172, 222, 255, 0.5) !important;
    color: #5A8AA6 !important;
}

.select2-container--default .select2-selection--multiple {
    background-color: rgba(255, 255, 255, 0.8) !important;
    border: 1px solid rgba(172, 222, 255, 0.4) !important;
}

/* 全局文字颜色 */
body, .mes_text, p, span, div, label {
    color: #5A8AA6;
}

/* 隐藏思维链背景 */
#chat .mes .mes_reasoning_header {
  background: transparent !important; /* 背景改为透明 */
  border: none !important; /* 移除边框 */
  box-shadow: none !important; /* 移除阴影 */
  border-radius: 0; /* 移除圆角 */
  backdrop-filter: none !important; 
  -webkit-backdrop-filter: none !important; 
  padding: 0 !important; /* 移除内边距 */
  margin-bottom: 0 !important; /* 移除底部外边距 */
  color: #C1B8B5 !important; /* 字体颜色自定义 */
  font-size: 0.9em !important;/* 字体大小 */
  display: inline-flex !important;
  align-items: center !important;
  transition: none !important; 
}


.mes_reasoning_header_title::before {
    content: "˚⊹.白い 雪 の 季節 の恋人.♡ ⊹";
    font-size: var(--mainFontSize);
}

.mes_reasoning_header_title {
    font-size: 0;
}
/* 隐藏箭头 */
.mes_reasoning_arrow {
    display: none !important;
}

/* 全局隐藏 Webkit 系浏览器的滚动条 */
::-webkit-scrollbar {
  display: none;
}

/* Firefox 全局滚动条样式 */
html {
  scrollbar-width: thin; /* auto | thin | none */
  scrollbar-color: #888 #f1f1f1; /* 滑块颜色 轨道颜色 */
}
