  width: 100% !important;
  height: 100% !important;
  object-fit: cover !important;
  margin: 0 !important;
  padding: 0 !important;
}

/* === 彻底修复：禁用/只读=== */
input:disabled,
select:disabled,
textarea:disabled,
.text_pole[disabled],
.text_pole[readonly],
[disabled] {
  box-shadow: none !important;
  background: rgba(255, 255, 255, 0.3) !important;
  background-image: none !important;
  border: 1px solid rgba(255, 255, 255, 0.2) !important;
  color: var(--text-color) !important;
  opacity: 0.6 !important;
  filter: none !important;
  cursor: not-allowed;
}

/* === 专项修复：编辑模式=== */
.mes_edit_buttons,
.mes_buttons {
  box-shadow: none !important;
  filter: none !important;
}

.mes_edit_buttons .menu_button {
  background: linear-gradient(145deg, #ffffff 0%, var(--poster-bar-bg) 100%) !important;
  border: 1.5px solid rgba(255, 255, 255, 0.8) !important;
  border-radius: 999px !important;
  color: var(--text-color) !important;
  font-size: 14px !important;
  box-shadow:
    0 4px 12px rgba(107, 139, 158, 0.15),
    inset 0 1px 1px rgba(255, 255, 255, 0.8) !important;
  padding: 6px 12px !important;
  margin: 0 3px !important;
  transition: all 0.2s ease !important;
  text-shadow: none !important;
  filter: none !important;
}

/* 顶部扩展栏图标渐变 */
#extensionTopBar i,
#extensionTopBar .icon-svg,
#extensionConnectionProfilesIcon,
#extensionTopBarToggleConnectionProfiles,
#extensionTopBarToggleSidebar {
  background-image: linear-gradient(135deg, var(--icon-color) 0%, #FFFFFF 100%) !important;
  -webkit-background-clip: text !important;
  -webkit-text-fill-color: transparent !important;
  background-clip: text !important;
  color: transparent !important;
  filter: none !important;
  text-shadow: none !important;
}

.toast {
  background-color: #6B8B9E !important;
  color: #EEE !important;
  box-shadow: none !important;
  filter: none !important;
}

/* === 1. 彻底解决底栏菜单双层/圆角问题 === */

/* ⭐关键：杀掉最外层容器的干扰，让它完全透明 */
#options,
#extensionsMenu {
  background: none !important;
  background-color: transparent !important;
  box-shadow: none !important;
  filter: none !important;
  border: none !important;
  padding: 0 !important;
  overflow: visible !important;
}

/* ⭐核心：只美化内层实际内容的盒子 */
.options-content,
#extensionsMenu {
  background: var(--main-bg) !important;
  border-radius: 5px !important;
  border: 1.5px solid var(--poster-border) !important;
  box-shadow: none !important;
  padding: 5px !important;
  margin-bottom: 5px !important;
  list-style: none !important;
}

/* --- 自定义 CSS 全屏展开弹窗美化 --- */
.popup-body {
  background-image: none !important;
  background-color: var(--main-bg) !important;
  border-radius: 8px !important;
  border: none !important;
  box-shadow: none !important;
}

dialog {
  border: 1px solid var(--main-border) !important;
  background-color: var(--main-bg) !important;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1) !important;
}

.avatar img {
  box-shadow: none !important;
}

/* 修改>缩进文本 */
.mes_text blockquote,
.mes_reasoning blockquote {
  background-color: color-mix(in srgb, var(--text-color), transparent 90%) !important;
  border-left: 4px solid color-mix(in srgb, var(--text-color), transparent 50%) !important;
  color: var(--SmartThemeBodyColor) !important;
  padding: 12px !important;
  border-radius: 0 !important;
}
