/* èœå•æ å…¨å±åŒ–ï¼Œæ¥è‡ªNAè€å¸ˆ */
.drawer-content.openDrawer {
  max-height: calc(100dvh - var(--topBarBlockSize));
  height: calc(100dvh - var(--topBarBlockSize));
}

#left-nav-panel,
#right-nav-panel {
  height: 100dvh;
  max-height: 100dvh;
}

@media (max-width: 600px) {
  #right-nav-panel {
    max-height: calc(100dvh - var(--topBarBlockSize));
  }
}

* {
  scrollbar-width: none !important;
  /* Firefox */
  -ms-overflow-style: none !important;
  /* IE/Edge */
}

/* å…¨å±€å¼¹å‡ºé¢æ¿ä¸»è‰²è°ƒ */
.drawer-content,
