  background-color: rgba(253, 242, 244, 0.95) !important;
}

/* ===== äºŒã€é¡¶æ  ===== */
#top-settings-holder {
  background-color: transparent !important;
  background-image: linear-gradient(180deg, rgba(255, 255, 255, 0.3) 0%, transparent 100%);
  box-shadow: 0 2px 10px rgba(93, 64, 55, 0.1);
}

/* åˆ é™¤é¡¶æ é˜´å½± */
#top-bar {
  box-shadow: none !important;
}

/* å›¾æ ‡æ›¿æ¢ */
#top-settings-holder .drawer-icon::before {
  content: '' !important;
  visibility: hidden;
}

#top-settings-holder .drawer-icon {
  display: block;
  margin: 0 auto;
  background-image: url('https://i.postimg.cc/7ZBtZ9KM/wu-biao-ti18-20260104105105.png') !important;
  background-size: contain;
  background-position: center;
  background-repeat: no-repeat;
  transition: all 0.3s ease-in-out;
  width: 33px;
  height: 33px;
  opacity: 1 !important;
  filter: drop-shadow(1px 1px 0px white);
}

#top-settings-holder .drawer-icon:hover {
  transform: scale(1.2) rotate(5deg);
}

/* --- æ‰©å±•æ ä¸»å®¹å™¨ï¼šçº¤ç»†ç‰ˆ --- */
#extensionTopBar,
#extensionConnectionProfiles {
  position: fixed !important;
  top: var(--topBarBlockSize) !important;
  margin-top: 10px !important;
  top: calc(var(--topBarBlockSize) + var(--extension-bar-move-down)) !important;
  left: 50%;
