  background-size: var(--bar-wallpaper-size) !important;
  background-repeat: repeat !important;
  /* 1. æ ¸å¿ƒï¼šåŒå±‚è¾¹æ¡† */
  border: 2px solid var(--morandi-green) !important;
  outline: 2px solid white !important;
  outline-offset: -5px;
  /* ç™½è‰²æè¾¹å‘å†…ç¼© */
  /* 2. æ ¸å¿ƒï¼šå†…å‡¹è´¨æ„Ÿ */
  box-shadow:
    1px 2px 2px var(--code-inner-shadow),
    inset 0px -1px 6px var(--code-inner-shadow),
    inset 1px 1px 3px rgba(255, 255, 255, 0.5) !important;
  border-radius: 0 !important;
