custom_css:
/* ===== 字体 ===== */

@font-face {
    font-family: "Jing Hua ";
    src: url("https://files.catbox.moe/138mza.ttf") format("truetype");
}
body, #nonQRFormItems textarea,.mes_text pre code,code , .mes_reasoning pre code,input, select, button {
    font-family: "Jing Hua ", sans-serif;
}

code {
    background-color: rgb(33 52 107 / 17%);
    color: rgb(115 123 141);
    box-shadow: 0px 0px 3px 2px rgb(61 77 114);
    border-radius: 0px;
}

/* 斜体 */
.mes_text em {
    text-decoration: underline;
    text-decoration-color: #404c63d9;
    text-decoration-thickness: 4px;
    text-underline-offset: -2px;
    text-decoration-skip-ink: none;
}

.mes_text q {
    background-color: rgb(102 113 135 / 40%);
    border-radius: 0;
    padding: 0px 0px;
    display: inline;
    box-decoration-break: clone;
    -webkit-box-decoration-break: clone;
    box-shadow: 3px 3px 1px 0px rgba(0, 0, 50, 0.3);
}
/* 代码块 */
.mes_text pre, .mes_reasoning pre {
    background-color: #15223e;
    border: 2px solid rgb(54 68 93);
    padding: 2px;
    border-radius: 0;
    position: relative;
    box-sizing: border-box;
}

.mes_text pre code, .mes_reasoning pre code {
    background-color: #15223e;
    border: 2px solid rgb(91 108 139);
    color: #a8b1c3;
    box-sizing: border-box;
    border-radius: 0px;
    box-shadow: none;
}

/*引用块*/
.mes_text blockquote, .mes_reasoning blockquote {
    background-color: rgb(161 153 143 / 10%);
    border-radius: 5px;
}

.mes_text p, .mes_reasoning p {
    margin-top: 0;
    margin-bottom: 10px;
    text-align: justify;  /* 两端对齐 */
    text-justify: inter-word;  /* 单词间调整 */
}

/* ===== 动画 ===== */
/* 移除原始伪元素 */
#load-spinner ,#load-spinner::before{
   animation: none;
   content: "";
}

/* 创建新的自定义加载动画 */
#load-spinner::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 50%;
    width: 60px;
    height: 60px;
    background: url('https://iili.io/fDw6zpR.png') center/contain no-repeat;
    transform: translate(-20%, -20%);
    z-index: 1000;
    /* 添加左右旋转摇晃动画 */
    animation: rotate-shake 3s ease-in-out infinite;
}

/* 左右旋转摇晃动画关键帧 */
@keyframes rotate-shake {
    0%, 100% {
        transform: translate(-50%, -50%) rotate(0deg);
    }
    25% {
        transform: translate(-50%, -50%) rotate(-15deg);
    }
    50% {
        transform: translate(-50%, -50%) rotate(0deg);
    }
    75% {
        transform: translate(-50%, -50%) rotate(15deg);
    }
}

/* 隐藏原有旋转动画 */
@keyframes custom-spin {
    display: none;
}
/* ===== 思维块美化 ===== */
.mes_reasoning_header {
    cursor: pointer;
    position: relative;
    user-select: none;
    margin: 0.5em 2px;
    padding: 7px 14px;
    padding-right: calc(0.7em + 14px);
    background-color: transparent;
    font-size: calc(var(--mainFontSize) * 0.9);
    align-items: baseline;
    /* Empty the content */
    font-size: 0 !important;
}

.mes_reasoning_header::before {
    content: "私たちの約束ᵕ̈ᐟᐟ ✰‧ ₊˚";
    color: #dbdbdb;
    font-size: calc(var(--mainFontSize) * 0.9);
    display: inline;
    text-shadow: 0 0 5px rgba(255, 255, 255, 0.7);
    animation: subtle-glow 2s ease-in-out infinite alternate;
}

@keyframes subtle-glow {
    from {
        text-shadow: 0 0 3px rgba(255, 255, 255, 0.5);
    }
    to {
        text-shadow: 0 0 6px rgba(255, 255, 255, 0.8);
    }
}
/* ====== 气泡 ====== */


.mes_block {
    padding-left: 0;
}

body.bubblechat .mes, body.bubblechat .mes[is_user="true"] {
    padding: 0 30px 0;
    border-radius: 0px;
    margin-bottom: 5px;
    border: 1px solid var(--SmartThemeBorderColor);
    max-width: 405px;
    margin: 220px auto 45px auto;
    position: relative;
    /* 添加背景图片 */
    background-image: url("https://iili.io/fUHi4wu.png");
    background-size: 405px 270px;
    background-repeat: repeat;
    background-position: center;
    overflow: visible; 
}

.mes {

    --swipeCounterHeight: 15px;
    --swipeCounterMargin: 5px;
    display: flex;
    align-items: flex-start;
    padding: 0 30px 0;
    margin: 220px auto 45px auto;
    width: 405px;
    max-width: 405px;
    color: var(--SmartThemeBodyColor);
    position: relative;
    /* 添加背景图片 */
    background-image: url("https://iili.io/fUHi4wu.png");
    background-size: 405px 270px;
    background-repeat: repeat;
    background-position: center;
 z-index: 0
}

/*感谢秋依老师 */
#form_sheld {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    background-color:transparent;
}
#chat {
    padding-bottom: 140px;
}

/* 为所有气泡添加顶部图片 */
.mes::before {
    content: "";
    position: absolute;
    top: -183.6px; /* 图片高度 */
    width: 405px;
    height: 183.6px;
    left: 50%;
    transform: translateX(-50%);
    background-image: url("https://iili.io/fgsTdOb.png");
