    pointer-events: none; /* 防止影响点击 */

}

/* 为所有气泡添加底部图片 */
.mes::after {
    content: "";
    position: absolute;
    bottom: -108px; 
    width: 405px;
    height: 108px;
    left: 50%;
    transform: translateX(-50%);
    background-image: url("https://iili.io/fgs91rN.png");
    background-size: 405px 108px; 
    background-repeat: no-repeat;
    background-position: center;
    z-index: 1;
}

/* 翻页 */
.swipe_left {
    left: 120px;
    bottom:-45px;
}
.swipe_right {
    position: absolute;
    right: 110px;
    bottom:-45px;
}

.fa-chevron-right:before {
    content: "";
    display: inline-block;
    width: 20px;
    height: 20px;
    background-image: url("https://iili.io/fDWc3Ne.png");
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    vertical-align: middle;
}

.fa-chevron-left:before {
    content: "";
    display: inline-block;
    width: 20px;
    height: 20px;
    background-image: url("https://iili.io/fDWc3Ne.png");
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    vertical-align: middle;
}

.fa-chevron-right,.fa-chevron-left{
opacity:0.7;
}

/* 文本 */
.mes_text {
    padding-left: 5px;
    padding-top: 5px;
    padding-bottom: 5px;
    padding-right: 5px;
}

.reasoning_edit_textarea, .edit_textarea {
    background-color: rgb(81 86 110 / 52%);
}
/* ====== 头像 ====== */
.mesAvatarWrapper {
    width: 80px;
    height: 90px;
    object-fit: cover;
    display: block;
    position: absolute;
    left: 80px;
    top: -120px;
}

.mesAvatarWrapper .avatar img {
width: 160%;
height: 180%;
object-fit: cover;
border-radius: inherit;
 z-index: 0;
    box-shadow:none;
}

.mes .mes_timer {
    cursor: default;
    opacity: 1;
    font-size: 0.65em;
    font-weight: 600;
    text-align: right;
    position: absolute;
    left: 108px;
    white-space: nowrap;
    top: 61.5px;
    z-index: 10;
