}


.mes .mesIDDisplay {
    cursor: default;
    opacity: 1;
    font-size: 0.65em;
    font-weight: 600;
    text-align: right;
    position: absolute;
    white-space: nowrap;
    left: 108px;
    top: 78px;
    z-index: 10;
    color: #1D275C !important;
}

.mes .tokenCounterDisplay {
    cursor: default;
    opacity: 1;
    font-size: 0.65em;
    font-weight: 600;
    text-align: right;
    position: absolute;
    left: 108px;
    text-align: right;
    white-space: nowrap;
