    font-weight: 600;
    position: absolute; /* 绝对定位 */
    left: 50%; 
    transform: translateX(-50%);
    bottom: -40px; /* Y轴偏移-40px */
    text-align: right; /* 右对齐 */
    white-space: nowrap; /* 防止换行 */
    z-index: 10; /* 确保显示在最前面 */
    opacity: 0.7; /* 保持原有透明度 */
}

.mes .name_text {
    font-size: 22px;
    position: absolute;
    left: 170px;
    top: -101px;
    transform: translateY(-50%);
    text-align: center;
    white-space: nowrap;
    z-index: 10;
    cursor: default;
    opacity: 0.5;
    font-weight: 600;
}

.mes_block {
  transform: none !important;
}

/* ===== 底部输入栏 ===== */

#qr--bar {
    outline: none;
    margin: 0;
    transition: 0.3s;
    opacity: 0.7;
    display: flex;
    align-items: center;
    justify-content: center;
    top:5px ;
    width: 100%;
    max-width: 100%;
    /* 修改高度为自适应 */
    height: auto;
    /* 设置最大高度并允许垂直滚动 */
    max-height: 30px; /* 保留原始高度作为最大高度 */
    /* 允许水平和垂直方向滚动 */
    overflow: auto; /* 替代原来的overflow-x和overflow-y */
    scrollbar-width: thin;
    scrollbar-color: #888 #f0f0f0;
}

#form_sheld {
    white-space: nowrap;
    border: none !important;
    outline: none !important;
    box-shadow: none !important;
}

#send_form {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    width: 100%;
    margin: 0px auto 0px auto;
    border: 0;
    border-radius: 0;
    backdrop-filter: blur(var(--SmartThemeBlurStrength));
    position: relative;
    background-color: rgb(0 0 0 / 0%);
}
#nonQRFormItems {
    padding: 5px;
    border: 0;
    position: relative;
    display: flex;
    flex-direction: row;
    column-gap: 5px;
    font-size: var(--bottomFormIconSize);
    order: 25;
    width: 95%;
    min-height: 30px;
    align-items: center;
    overflow: hidden;
    background-color: #15223e !important;
    border-radius: 0 !important;
    border: 3px solid #2b426b !important;
    box-shadow: inset 0px 0px 0px 0px rgb(100 115 155 / 30%), 0px 1px 4px 2px rgb(16 45 111) !important;
    margin: 0 auto !important;
}
#nonQRFormItems textarea {
    flex: 1;
    min-height: 30px;
    max-height: 100px;
    resize: none;
    overflow-y: auto;
    line-height: normal;
    padding: 5px;
    margin: 0;
    border: none;
    outline: none;
    background: rgb(43 73 107 / 48%) !important;
    border-radius: 0 !important;
    box-shadow: inset 0 1px 3px rgb(188 205 211 / 0%), inset 0 -1px 3px rgb(174 177 178 / 40%);
}

/* 字体图标容器固定大小 */
#nonQRFormItems .icon-container {
    display: flex;
    align-items: center;
    justify-content: center;
    height: 30px;
    width: 30px; /* 固定宽度 */
    flex-shrink: 0; /* 防止图标被压缩 */
}

#qr--bar > .qr--buttons .qr--button, #qr--popout > .qr--body > .qr--buttons .qr--button{
    transform: scale(0.9);
}


/*停止钮*/
.fa-circle-stop:before,
.fa-stop-circle:before {
  content: ""; /* 移除原图标 */
  display: inline-block;
  width: 20px;
  height: 20px;
  background: url('https://iili.io/fDGhp0G.png') no-repeat center;
  background-size: contain;
  opacity: 1;
  animation: blink 1.5s ease-in-out infinite; /* 改为闪烁动画 */
  transform-origin: center;
}

/* 闪烁动画 */
@keyframes blink {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.3;
  }
}

/*小飞机*/
.fa-paper-plane:before {
  content: "";
  display: inline-block;
  width: 20px;
  height: 20px;
  background: url('https://iili.io/fDGhp0G.png') no-repeat center;
  background-size: contain;
  vertical-align: middle;
}


/* ===== 顶部 ===== */
#top-bar {
    margin: 0 auto;
    left: 0;
    right: 0;
    display: inline-block;
    height: auto; /* 改为自动高度 */
    min-height: 30px; /* 设置最小高度 */
    position: absolute;
    z-index: 3005;
    background-color: transparent !important; /* 改为透明 */
    border-radius: 0 !important;
    box-shadow: none !important;
    overflow: hidden;
}

#top-bar::before {
    content: "";
    display: block;
    width: 100%;
    /* 根据图片比例 375:55 = 1:0.1467 */
    padding-top: 12%; /* 55 ÷ 375 × 100% */
    background-image: url('https://iili.io/fPuqRUb.png');
    background-size: 100% 100%; /* 完全填满 */
    background-position: center;
    background-repeat: no-repeat;
    border-radius: 0;
}

.drawer-toggle .drawer-icon {
    transform-origin: center;
    display: inline-block;
    width: auto;
    height: auto;
}


/* 如果原有内容是通过伪元素实现的 */
.drawer-toggle .drawer-icon:before {
    content: "" !important;
    background: url('https://iili.io/fPTkCjs.png') no-repeat center !important;
    background-size: contain !important;
    display: inline-block;
    width: 20px;
    height: 20px;
}
.drawer-toggle .drawer-icon {
    transform-origin: center;
    display: inline-block;
    width: auto;
    height: auto;
}

/* ===== UI ===== */
#extensions_settings .inline-drawer-toggle.inline-drawer-header, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header, #user-settings-block h4, .standoutHeader {
    background: #516d9c69;
    opacity: 0.7;
    margin-bottom: 4px;
    padding: 2px 5px 2px 5px;
    border: 2px solid rgb(89 101 144);
    position: relative;
    border-radius: 0;
    box-shadow: none !important;
}
/* 滑块 */

input[type="range"] {
    -webkit-appearance: none;
    appearance: none;
    margin: 0;
    padding: 0;
    width: 100%;
    height: 5px;
    background: var(--white50a);
    border-radius: 15px;
    background-size: 70% 100%;
    background-repeat: no-repeat;
    cursor: ew-resize;
    /* 移除可能的阴影 */
    box-shadow: none;
}

/* Webkit浏览器滑块样式 */
input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    width: 20px;
    height: 20px;
    background: url('https://iili.io/fDw6zpR.png') center/contain no-repeat;
    cursor: pointer;
    border: none;
    /* 移除滑块阴影 */
    box-shadow: none;
}

.neo-range-slider {
    -webkit-appearance: none !important;
    appearance: none !important;
    margin: 0 !important;
    margin-top: 7px !important;
    padding: 0 !important;
    width: 100% !important;
    height: 5px !important;
    background: rgb(181 212 255) !important;
    border-radius: 7px 7px 0 0 !important;
    background-size: 70% 100% !important;
    background-repeat: no-repeat !important;
    box-shadow: none !important; 
    cursor: ew-resize !important;
    filter: brightness(0.75);
}

/* Firefox浏览器滑块样式 */
input[type="range"]::-moz-range-thumb {
    width: 20px;
    height: 20px;
    background: url('https://iili.io/fDw6zpR.png') center/contain no-repeat;
    cursor: pointer;
    border: none;
    /* 移除滑块阴影 */
    box-shadow: none;
}

/* 针对IE浏览器（如果需要） */
input[type="range"]::-ms-thumb {
    box-shadow: none;
}

/* 图标 */

.fa-bars:before, .fa-navicon:before {
    content: ""; /* 移除原有图标 */
    display: inline-block;
    width: 20px;
    height: 20px;
    background: url('https://iili.io/fDGhp0G.png') center/contain no-repeat;
    vertical-align: middle;
}

/*魔法棒*/
.fa-magic-wand-sparkles:before, .fa-wand-magic-sparkles:before {
    content: ""; /* 移除原有图标 */
    display: inline-block;
    width: 20px;
    height: 20px;
    background: url('https://iili.io/fDGhp0G.png') center/contain no-repeat;
    vertical-align: middle;
}

.drawer-content select {
    background-color: #2a3a60;
    border-radius: 0px;
}



textarea, .textarea_compact, .range-block-counter input, .text_pole {
    background-color: #212c48;
    color: rgb(165 171 202);
}

#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
    color: #62709e;
}

#completion_prompt_manager .completion_prompt_manager_header div {
    color: #7081a9;
}

#completion_prompt_manager .completion_prompt_manager_header_advanced span {
    filter:none;
}

/*预设正则空间预留*/
#global_scripts_block ,#scoped_scripts_block ,#completion_prompt_manager_list{
    padding-right: 30px;
}

/*热切换*/
#HotSwapWrapper {
    position: relative;
    overflow-x: auto;
    scrollbar-width: thin; /* Firefox */
    scrollbar-color: rgba(255, 182, 193, 0.3) 
}

#HotSwapWrapper::-webkit-scrollbar {
    height: 1px; /* 设置滚动条高度为3px */
}

#HotSwapWrapper::-webkit-scrollbar-track {
    background: transparent; /* 滚动条轨道透明 */
}

#HotSwapWrapper::-webkit-scrollbar-thumb {
    scrollbar-color: rgba(255, 182, 193, 0.3) 
    border-radius: 0; /* 无圆角 */
    border: none; /* 无边框 */
}

#HotSwapWrapper > * {
    flex-shrink: 0;
}


/* --- 默认隐藏 QR 按钮容器，并设置平滑过渡 --- */
.qr--buttons {
  opacity: 0;
  visibility: hidden;
  max-height: 0;
  transform: translateY(20px);
  pointer-events: none;
  transition:
    opacity 0.3s ease-in-out,
    visibility 0.3s ease-in-out,
    max-height 0.5s ease,
    transform 0.5s ease;
}

/* 感谢twelve.老师分享的隐藏qr和dengdeng老师分享的隐藏弹窗，感谢nini老师进行的整合TT爱大家…… */
#send_form:focus-within .qr--buttons {
  opacity: 1;
  visibility: visible;
  max-height: 120px;
  transform: translateY(0);
  pointer-events: auto;
  border: 0 !important;
}

#qr--bar {
  opacity: 0;
  visibility: hidden;
  max-height: 0;
  pointer-events: none;
  transition:
    opacity 0.3s ease-in-out,
    visibility 0.3s ease-in-out,
    max-height 0.3s ease;
}

#send_form:focus-within #qr--bar,
.qr--buttons.active #qr--bar {
  opacity: 1;
  visibility: visible;
  max-height: 40px;
  pointer-events: auto;
}

/* 分割线 */
hr {
    background-image: url("https://iili.io/fD2PLZu.png") !important;
    background-size: 400px 30px !important; /* 设置图片大小 */
    background-repeat: no-repeat !important; /* 不重复 */
    background-position: center !important; /* 位于中心 */
    margin: 5px 0 !important;
    height: 30px !important; /* 设置高度为25px */
    min-height: 30px !important; /* 最小高度也为25px */
    border: 0 !important;
    opacity: 0.8 !important;
    display: block !important;
    visibility: visible !important;
}

body.bubblechat .welcomePanel {
    backdrop-filter: blur(10px);
    background-color: rgb(21 34 62 / 70%);
}

.select2-container .select2-selection--multiple, .select2-container .select2-selection--single {
    background-color: rgb(0 0 0 / 0%);
}

.select_chat_block[highlight] {
    background-color: rgb(86 96 119 / 30%);
}

.reverse_proxy_warning:not(small) {
    background-color: rgb(42 58 96);
    color: rgb(129 140 168 / 90%);
}

/* --- 通用输入框提示词自定义 ，源自墨千尾，感谢老师/(ㄒoㄒ)/~~--- */

#send_textarea::placeholder {
    color: transparent !important;
    text-shadow: none !important; /* 移除可能存在的文字阴影 */
}

#nonQRFormItems:has(#send_textarea:placeholder-shown)::before {
    content: "幸せが続きますように"; /*☁️自定义文本*/
    position: absolute; /* 使用绝对定位，让它能自由移动 */
    top: 50%; 
    left: 50%;
    transform: translate(-50%, -50%); /* 精确居中 */

    font-style: oblique; /* 字体样式：可以改为 normal (正常) 或 oblique (倾斜) */
    font-size: 0.5em;  
    pointer-events: none; 
    z-index: 10; /* 确保它显示在最上层 */

    white-space: nowrap; 
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: calc(100% - 30px); /* 限制最大宽度，防止溢出输入框 */
}

.popup[open]::backdrop {
    background-color: #ece4d570;
}



/* 全局滑动条样式 */
::-webkit-scrollbar {
    width: 1px; /* 滑动条宽度 */
    height: 1px; /* 水平滑动条高度 */
}

::-webkit-scrollbar-track {
    background: transparent; /* 轨道背景透明 */
}

::-webkit-scrollbar-thumb {
    background: rgb(54 81 113 / 50%); /* 半透明浅粉色 */
    border-radius: 0; /* 无圆角 */
}

::-webkit-scrollbar-thumb:hover {
    background: rgb(54 81 113 / 50%); /* 鼠标悬停时稍微加深 */
}

