custom_css:
/* ===== 字体 ===== */
@font-face {
    font-family: "HWMCT";
    src: url("https://files.catbox.moe/ug39z8.ttf") format("truetype");
}
body, #nonQRFormItems textarea,.mes_text pre code, .mes_reasoning pre code,input, select, button,code {
    font-family: "HWMCT", sans-serif;
}

code {
    background-color: rgb(78 89 82 / 53%);
    color: rgb(98 111 95);
}
/* 斜体 */
.mes_text em {
    text-decoration: underline;
    text-decoration-color: #44523ec2;
    text-decoration-thickness: 2px;
}

/* 引用 */
.mes_text q {
    background-color: rgb(109 121 111 / 40%);
    border-radius: 0;
    padding: 0px 0px;
    display: inline;
    box-decoration-break: clone;
    -webkit-box-decoration-break: clone;
}
/* 代码块 */
.mes_text pre, .mes_reasoning pre {
    background-color: #1f2b1c;
    border: 2px solid rgb(75 88 77);
    padding: 2px;
    border-radius: 0;
    position: relative;
    box-sizing: border-box;
}

.mes_text pre code, .mes_reasoning pre code {
    background-color: #1f2b1c;
    border: 2px solid rgb(75 88 77);
    color: #6b7c66;
    box-sizing: border-box;
    border-radius: 0px;
}

#chat > div > div.mes_block > div.mes_text > pre > code > i {
  font-size: 0; /* 隐藏原图标内容 */
  display: inline-block;
  width: 25px;
  height: 25px;
  background: url('https://i.postimg.cc/x8Dgsbyk/IMG-2814.png') no-repeat center;
  background-size: contain;
  vertical-align: middle;
  margin:0;
}

/*引用块*/
.mes_text blockquote, .mes_reasoning blockquote {
    background-color: rgb(125 149 128 / 10%);
    border-radius: 5px;
}

.mes_text p, .mes_reasoning p {
    margin-top: 0;
    margin-bottom: 10px;
    text-align: justify;  /* 两端对齐 */
    text-justify: inter-word;  /* 单词间调整 */
}
/* ===== 动画 ===== */
/* 移除原始伪元素 */
#load-spinner ,#load-spinner::before{
   animation: none;
   content: "";
}

/* 创建新的自定义加载动画 */
#load-spinner::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 50%;
    width: 60px;
    height: 60px;
    background: url('https://i.postimg.cc/0QJcdCmQ/IMG-2817.png') center/contain no-repeat;
    transform: translate(-50%, -50%);
    z-index: 1000;
    /* 添加左右旋转摇晃动画 */
    animation: rotate-shake 3s ease-in-out infinite;
}

/* 左右旋转摇晃动画关键帧 */
@keyframes rotate-shake {
    0%, 100% {
        transform: translate(-50%, -50%) rotate(0deg);
    }
    25% {
        transform: translate(-50%, -50%) rotate(-15deg);
    }
    50% {
        transform: translate(-50%, -50%) rotate(0deg);
    }
    75% {
        transform: translate(-50%, -50%) rotate(15deg);
    }
}

/* 隐藏原有旋转动画 */
@keyframes custom-spin {
    display: none;
}
/* ===== 思维块美化 ===== */
.mes_reasoning_header {
    cursor: pointer;
    position: relative;
    left: 50%;
    transform: translate(-50%, -50%);
    user-select: none;
    margin: 5px;
    padding: 2px 8px;
    padding-right: calc(0.7em + 14px);
    background-color: transparent; /* 去除背景颜色 */
    font-size: calc(var(--mainFontSize) * 0.9);
    color: #8a5c2b;
    align-items: baseline;
    font-size: 0 !important;
    z-index: 1; /* 确保元素在前层 */
}

.mes_reasoning_header::before{
    content: "";
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%); /* 居中对齐 */
    width: 85px;
    height: 25px;
    background: url('https://i.postimg.cc/brcL62HJ/IMG-2812.png') no-repeat center;
    background-size: contain;
}

.fa-chevron-up:before {
    content: "";
}

/* ====== 气泡 ====== */


.mes_block {
    padding-left: 0;
}

body.bubblechat .mes, body.bubblechat .mes[is_user="true"] {
    padding: 0 30px 0;
    border-radius: 0px;
    margin-bottom: 5px;
    border: none;
    max-width: 420px;
    margin: 180px auto 65px auto;
    position: relative;
    /* 添加背景图片 */
    background-image: url("https://i.postimg.cc/TYnQnFTN/IMG-2808.png");
    background-size: 420px 280px;
    background-repeat: repeat;
    background-position: center;
}

.mes {
    --swipeCounterHeight: 15px;
    --swipeCounterMargin: 5px;
    display: flex;
    align-items: flex-start;
    padding: 0 30px 0;
    margin: 180px auto 65px auto;
    width: 420px;
    max-width: 420px;
    color: var(--SmartThemeBodyColor);
    position: relative;
    /* 添加背景图片 */
    background-image: url("https://i.postimg.cc/TYnQnFTN/IMG-2808.png");
    background-size: 420px 280px;
    background-repeat: repeat;
    background-position: center;
 z-index: 1
}

.last_mes {
    margin-bottom: 0;
}

/* 为所有气泡添加顶部图片 */
.mes::before {
    content: "";
    position: absolute;
    top: -168px; /* 图片高度 */
    width: 420px;
    height: 168px;
    left: 50%;
    transform: translateX(-50%);
    background-image: url("https://i.postimg.cc/qBPjh4Hm/IMG-2810.png");
    background-size: 420px 168px; 
    background-repeat: no-repeat;
    background-position: center;
    z-index: 2;
    pointer-events: none; /* 防止影响点击 */

}

/* 为所有气泡添加底部图片 */
.mes::after {
    content: "";
    position: absolute;
    bottom: -70px; 
    width: 420px;
    height: 70px;
    left: 50%;
    transform: translateX(-50%);
    background-image: url("https://i.postimg.cc/c1pXg0G2/IMG-2809.png");
    background-size: 420px 70px; 
    background-repeat: no-repeat;
    background-position: center;
    z-index: 1;
}

/* 翻页 */
.swipe_left {
    left: 140px;
    bottom:-50px;
}
.swipe_right {
    position: absolute;
    right: 140px;
    bottom:-50px;
}

.fa-chevron-right:before {
    content: "";
    display: inline-block;
    width: 15px;
    height: 15px;
    background-image: url("https://i.postimg.cc/fL0BjvdP/IMG-2819.png");
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    vertical-align: middle;
}

.fa-chevron-left:before {
    content: "";
    display: inline-block;
    width: 15px;
    height: 15px;
    background-image: url("https://i.postimg.cc/fL0BjvdP/IMG-2819.png");
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    vertical-align: middle;
}

/* 文本 */
.mes_text {
    padding-left: 5px;
    padding-top: 5px;
    padding-bottom: 5px;
    padding-right: 5px;
}

/*幽灵*/
.fa-ghost:before {
  content: "";
  display: inline-block;
  width: 25px;
  height: 25px;
  background: url('https://i.postimg.cc/cCyTD8Mg/IMG-2820.png') no-repeat center;
  background-size: contain;
  vertical-align: middle;
}

.reasoning_edit_textarea, .edit_textarea {
    background-color: rgb(58 66 57 / 52%);
}
/* ====== 头像 ====== */
.mesAvatarWrapper {
    width: 138px;
    height: 138px;
    object-fit: cover;
    display: block;
    position: absolute;
    left: 233px;
    top: -152px;
}

.mesAvatarWrapper .avatar img {
width: 276%;
height: 276%;
object-fit: cover;
border-radius: inherit;
 z-index: 0;
    box-shadow:none;
}

.mes .mes_timer {
    cursor: default;
    opacity: 0.5;
    font-size: 0.7em;
    font-weight: 600;
    text-align: right;
    position: absolute;
    left: -7px;
    white-space: nowrap;
    top: 113px;
    z-index: 10;
}


.mes .mesIDDisplay {
    cursor: default;
    opacity: 0.5;
    font-size:0.7em;
    font-weight: 600;
    text-align: right;
    position: absolute;
    white-space: nowrap; /* 防止换行 */
    left: 52px;
    top: 113px;   
    z-index: 10;
}


.mes .tokenCounterDisplay {
    cursor: default;
    opacity: 0.5;
    font-size: 0.7em;
    font-weight: 600;
    text-align: right;
    position: absolute;
    left: 113px; 
    text-align: right; /* 右对齐 */
    white-space: nowrap; /* 防止换行 */
    top: 113px;
    z-index: 10;
}

/* 时间戳位置 */
.mes .timestamp {
    font-size: 10px;
    font-weight: 600;
    position: absolute; /* 绝对定位 */
    left: 50%; 
    transform: translateX(-50%);
    bottom: -40px; /* Y轴偏移-40px */
    text-align: right; /* 右对齐 */
    white-space: nowrap; /* 防止换行 */
    z-index: 10; /* 确保显示在最前面 */
    opacity: 0.7; /* 保持原有透明度 */
}

.mes .name_text {
    font-size: 27px;
    position: absolute; /* 绝对定位 */
    left: calc(50% - 95px); /* 中心向左偏移80px */
    transform: translateX(-50%); /* 移除水平居中变换 */
    top: -50px; /* Y轴向上偏移40px */
    text-align: center; 
    white-space: nowrap; /* 防止换行 */
    z-index: 10; /* 确保显示在最前面 */
    cursor: default;
    opacity: 0.5;
    font-weight: 600;
}

.mes_block {
  transform: none !important;
}

/* ===== 底部输入栏 ===== */
#qr--bar {
    outline: none;
    margin: 0;
    transition: 0.3s;
    opacity: 0.7;
    display: flex;
    align-items: center;
    justify-content: center;
    top:5px ;
    width: 100%;
    max-width: 100%;
    /* 修改高度为自适应 */
    height: auto;
    /* 设置最大高度并允许垂直滚动 */
    max-height: 30px; /* 保留原始高度作为最大高度 */
    /* 允许水平和垂直方向滚动 */
    overflow: auto; /* 替代原来的overflow-x和overflow-y */
    scrollbar-width: thin;
    scrollbar-color: #888 #f0f0f0;
}

/* 统一滚动条样式 */
#qr--bar::-webkit-scrollbar {
    width: 4px;  /* 垂直滚动条宽度 */
    height: 4px; /* 水平滚动条高度 */
}

#qr--bar::-webkit-scrollbar-track {
    background: #f0f0f0;
}

#qr--bar::-webkit-scrollbar-thumb {
    background-color: #888;
    border-radius: 2px;
}

#send_form {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    width: 100%;
    margin: 0px auto 0px auto;
    border: 0;
    border-radius: 0;
    backdrop-filter: blur(var(--SmartThemeBlurStrength));
    position: relative;
    background-color: rgb(0 0 0 / 0%);
}
#nonQRFormItems {
    padding: 5px;
    border: 0;
    position: relative;
    display: flex;
    flex-direction: row;
    column-gap: 5px;
    font-size: var(--bottomFormIconSize);
    order: 25;
    width: 95%;
    min-height: 30px;
    align-items: center;
    overflow: hidden;
    background-color: #101e10 !important;
    border-radius: 20px !important;
    border: 3px solid #25321c !important;
    box-shadow: inset 0 2px 4px rgb(0 0 0 / 40%), 0 2px 6px rgb(115 145 121) !important;
    margin: 0 auto !important;
}
#nonQRFormItems textarea {
    flex: 1;
    min-height: 30px;
    max-height: 100px;
    resize: none;
    overflow-y: auto;
    line-height: normal;
    padding: 5px;
    margin: 0;
    border: none;
    outline: none;
    background: rgb(75 80 73 / 32%) !important;
}

/* 字体图标容器固定大小 */
#nonQRFormItems .icon-container {
    display: flex;
    align-items: center;
    justify-content: center;
    height: 30px;
    width: 30px; /* 固定宽度 */
    flex-shrink: 0; /* 防止图标被压缩 */
}

#qr--bar > .qr--buttons .qr--button, #qr--popout > .qr--body > .qr--buttons .qr--button{
    transform: scale(0.9);
}


/*停止钮*/
.fa-circle-stop:before,
.fa-stop-circle:before {
  content: ""; /* 移除原图标 */
  display: inline-block;
  width: 30px;
  height: 30px;
  background: url('https://i.postimg.cc/hvWMC7bq/IMG-2815.png') no-repeat center;
  background-size: contain;
  opacity: 1;
  animation: blink 1.5s ease-in-out infinite; /* 改为闪烁动画 */
  transform-origin: center;
}

/* 闪烁动画 */
@keyframes blink {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.3;
  }
}

/*小飞机*/
.fa-paper-plane:before {
  content: "";
  display: inline-block;
  width: 30px;
  height: 30px;
  background: url('https://i.postimg.cc/hvWMC7bq/IMG-2815.png') no-repeat center;
  background-size: contain;
  vertical-align: middle;
}


/* ===== 顶部 ===== */

#top-bar {
    margin: 0 auto;
    left: 0;
    right: 0;
    display: inline-block;
    height: 30px; 
    position: absolute;
    z-index: 3005;
    background-color: rgb(219 196 170 / 0%) !important;
    border-radius: 20px !important;
    box-shadow: none !important;
}

.drawer-toggle .drawer-icon {
    transform-origin: center;
    display: inline-block;
    width: auto;
    height: auto;
}

.drawer-icon.closedIcon {
    opacity: 0;
    transition: all 0.275s;
}

/* 如果原有内容是通过伪元素实现的 */
.drawer-toggle .drawer-icon:before {
    content: "" !important;
    background: url('https://i.postimg.cc/fL0BjvdP/IMG-2819.png') no-repeat center !important;
    background-size: contain !important;
    display: inline-block;
    width: 25px;
    height: 25px;
}
.drawer-toggle .drawer-icon {
    transform-origin: center;
    display: inline-block;
    width: auto;
    height: auto;
}

/* ===== UI ===== */
#extensions_settings .inline-drawer-toggle.inline-drawer-header, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header, #user-settings-block h4, .standoutHeader {
    background: -webkit-linear-gradient(right, #444e42e6, #4d574c36);
    opacity: 0.7;
    margin-bottom: 4px;
    padding: 2px 5px 2px 5px;
    border: 2px solid rgb(91 103 90);
    position: relative;
    border-radius: 10px;
    box-shadow: none !important;
}

/* 滑块 */

input[type="range"] {
    -webkit-appearance: none;
    appearance: none;
    margin: 0;
    padding: 0;
    width: 100%;
    height: 5px;
    background: var(--white50a);
    border-radius: 15px;
    background-size: 70% 100%;
    background-repeat: no-repeat;
    cursor: ew-resize;
    /* 移除可能的阴影 */
    box-shadow: none;
}

/* Webkit浏览器滑块样式 */
input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    width: 20px;
    height: 20px;
    background: url('https://i.postimg.cc/fL0BjvdP/IMG-2819.png') center/contain no-repeat;
    cursor: pointer;
    border: none;
    /* 移除滑块阴影 */
    box-shadow: none;
}

/* Firefox浏览器滑块样式 */
input[type="range"]::-moz-range-thumb {
    width: 20px;
    height: 20px;
    background: url('https://i.postimg.cc/fL0BjvdP/IMG-2819.png') center/contain no-repeat;
    cursor: pointer;
    border: none;
    /* 移除滑块阴影 */
    box-shadow: none;
}

/* 针对IE浏览器（如果需要） */
input[type="range"]::-ms-thumb {
    box-shadow: none;
}

/* 图标 */

.fa-bars:before, .fa-navicon:before {
    content: ""; /* 移除原有图标 */
    display: inline-block;
    width: 30px;
    height: 30px;
    background: url('https://i.postimg.cc/rmtQC9Ww/IMG-2816.png') center/contain no-repeat;
    vertical-align: middle;
}

.fa-magic-wand-sparkles:before, .fa-wand-magic-sparkles:before {
    content: ""; /* 移除原有图标 */
    display: inline-block;
    width: 30px;
    height: 30px;
    background: url('https://i.postimg.cc/vB63Lv9Y/IMG-2818.png') center/contain no-repeat;
    vertical-align: middle;
}

.drawer-content select {
    background-color: #4f5952b3;
    border-radius: 5px;
}

.neo-range-input, #completion_prompt_manager #completion_prompt_manager_list {
    background-color: #53645261;
}
textarea, .textarea_compact, .range-block-counter input, .text_pole {
    background-color: #37473885;
    color: rgb(130 144 127);
}
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
    color: #566a59de;
}

#completion_prompt_manager .completion_prompt_manager_header div {
    color: #62795d;
}
#completion_prompt_manager .completion_prompt_manager_header_advanced span {
    filter:none;
}

/*预设正则空间预留*/
#global_scripts_block ,#scoped_scripts_block ,#completion_prompt_manager_list{
    padding-right: 30px;
}

/*热切换*/
#HotSwapWrapper {
    position: relative;
    overflow-x: auto;
    scrollbar-width: thin; /* Firefox */
    scrollbar-color: rgba(255, 182, 193, 0.3) 
}

#HotSwapWrapper::-webkit-scrollbar {
    height: 1px; /* 设置滚动条高度为3px */
}

#HotSwapWrapper::-webkit-scrollbar-track {
    background: transparent; /* 滚动条轨道透明 */
}

#HotSwapWrapper::-webkit-scrollbar-thumb {
    scrollbar-color: rgba(255, 182, 193, 0.3) 
    border-radius: 0; /* 无圆角 */
    border: none; /* 无边框 */
}

#HotSwapWrapper > * {
    flex-shrink: 0;
}



/* --- 默认隐藏 QR 按钮容器，并设置平滑过渡 --- */
.qr--buttons {
  opacity: 0;
  visibility: hidden;
  max-height: 0;
  transform: translateY(20px);
  pointer-events: none;
  transition:
    opacity 0.3s ease-in-out,
    visibility 0.3s ease-in-out,
    max-height 0.5s ease,
    transform 0.5s ease;
}

/* 感谢twelve.老师分享的隐藏qr和dengdeng老师分享的隐藏弹窗，感谢nini老师进行的整合TT爱大家…… */
#send_form:focus-within .qr--buttons {
  opacity: 1;
  visibility: visible;
  max-height: 120px;
  transform: translateY(0);
  pointer-events: auto;
}

#qr--bar {
  opacity: 0;
  visibility: hidden;
  max-height: 0;
  pointer-events: none;
  transition:
    opacity 0.3s ease-in-out,
    visibility 0.3s ease-in-out,
    max-height 0.3s ease;
}

#send_form:focus-within #qr--bar,
.qr--buttons.active #qr--bar {
  opacity: 1;
  visibility: visible;
  max-height: 40px;
  pointer-events: auto;
}


/* 全局滑动条样式 */
::-webkit-scrollbar {
    width: 1px; /* 滑动条宽度 */
    height: 1px; /* 水平滑动条高度 */
}

::-webkit-scrollbar-track {
    background: transparent; /* 轨道背景透明 */
}

::-webkit-scrollbar-thumb {
    background: rgb(92 109 90 / 50%); /* 半透明浅粉色 */
    border-radius: 0; /* 无圆角 */
}

::-webkit-scrollbar-thumb:hover {
    background: rgb(92 109 90 / 50%); /* 鼠标悬停时稍微加深 */
}

/* Firefox 浏览器 */
* {
    scrollbar-width: thin; /* 细滚动条 */
    scrollbar-color: rgb(92 109 90 / 50%) transparent; /* 滑块半透明浅粉色，轨道透明 */
}

/* 分割线 */
hr {
    background-image: url("https://i.postimg.cc/QMxhYFxt/IMG-2822.png") !important;
    background-size: 250px 25px !important; /* 设置图片大小 */
    background-repeat: no-repeat !important; /* 不重复 */
    background-position: center !important; /* 位于中心 */
    margin: 5px 0 !important;
    height: 25px !important; /* 设置高度为25px */
    min-height: 25px !important; /* 最小高度也为25px */
    border: 0 !important;
    opacity: 0.8 !important;
    display: block !important;
    visibility: visible !important;
}

body.bubblechat .welcomePanel {
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    background-color: rgb(25 41 27 / 70%);
}

.select2-container .select2-selection--multiple, .select2-container .select2-selection--single {
    background-color: rgb(0 0 0 / 0%);
}

.select_chat_block[highlight] {
    background-color: rgb(79 99 78 / 30%);
}

.reverse_proxy_warning:not(small) {
    background-color: rgb(84 105 93 / 49%);
    color: rgb(115 133 112 / 90%);
}

/* --- 通用输入框提示词自定义 ，源自墨千尾，感谢老师/(ㄒoㄒ)/~~--- */

#send_textarea::placeholder {
    color: transparent !important;
    text-shadow: none !important; /* 移除可能存在的文字阴影 */
}

#nonQRFormItems:has(#send_textarea:placeholder-shown)::before {
    content: "听泉流，一窗秋"; /*☁️自定义文本*/
    position: absolute; /* 使用绝对定位，让它能自由移动 */
    top: 50%; 
    left: 50%;
    transform: translate(-50%, -50%); /* 精确居中 */

    font-style: oblique; /* 字体样式：可以改为 normal (正常) 或 oblique (倾斜) */
    font-size: 0.5em;  
    pointer-events: none; 
    z-index: 10; /* 确保它显示在最上层 */

    white-space: nowrap; 
