/*ğŸ›‘åŠ è½½ç¾åŒ–@å¢¨åƒå°¾*/
#preloader {
  position: fixed;
  margin: 0;
  padding: 0;
  top: 0;
  left: 0;
  z-index: 999999;
  width: 100vw;
  height: 100vh;
  background: transparent;   /* é€æ˜èƒŒæ™¯ */
}

#load-spinner {
  --spinner-size: 100px;
  --animation-duration-2x: 3s;
  position: absolute;
  top: calc(50% - var(--spinner-size) / 2);
  left: calc(50% - var(--spinner-size) / 2);
  width: var(--spinner-size);
  height: var(--spinner-size);
  animation: scaleAndStay var(--animation-duration-2x) infinite;
}

#load-spinner::before {
  content: "";
  position: absolute;
  inset: 0;
  background: url(https://iili.io/fjeXYQa.png) center/contain no-repeat;
  animation: upDown 1.2s ease-in-out infinite; /* åªä¸Šä¸‹åŠ¨ */
}

/* ä¸Šä¸‹ 10 px å¾ªç¯ï¼ˆä¼ªå…ƒç´ ï¼‰ */
@keyframes upDown {
  0%,100% { transform: translateY(0); }
  50%     { transform: translateY(-10px); }
}



