    100% { transform: translateY(0); opacity: 1; } 
}
@keyframes paperSlideOut { 
    0% { transform: translateX(0); opacity: 1; } 
    100% { transform: translateX(20px); opacity: 0; } 
}

div.toast { animation: paperSlideIn 0.4s cubic-bezier(0.2, 0.8, 0.2, 1) forwards; }
div.toast.toast-exit { animation: paperSlideOut 0.3s ease-in forwards; }

