/* 21. åŠ è½½åŠ¨ç”» */
:root {
    /* ğŸ“ åœ¨è¿™é‡Œè‡ªå®šä¹‰ä½ çš„åŠ è½½æ–‡å­— */
    --loading-text: "é›ªã‚’å¾…ã¤"; 
}

#load-spinner {
    position: fixed !important; 
    top: 50% !important; 
    left: 50% !important;
    transform: translate(-50%, -50%);

    width: auto !important;
    height: auto !important;
    background: none !important;
    animation: none !important;
    margin: 0 !important;

    font-family: "NaikaiFont", "Meiryo", sans-serif !important;
    font-size: 24px !important; /* ğŸ“ å­—ä½“å¤§å° */
    font-weight: bold !important;

    color: #87CEFA !important; /* ğŸ“ å­—ä½“é¢œè‰² */

    /* ğŸ“ æ–‡å­—å…‰æ™•/æè¾¹é¢œè‰²å’Œå¤§å° */
    text-shadow: 
        0 0 2px rgba(255, 255, 255, 0.9),
        0 0 8px rgba(255, 255, 255, 0.7) !important;
}

#load-spinner::before { 
    content: var(--loading-text) !important;
    display: inline-block;

    animation: text-bounce 1.6s ease-in-out infinite;
}

#load-spinner::after {
    content: '...';
    display: inline-block;
    width: 2.2ch;
    text-align: left;
    overflow: hidden;
    vertical-align: bottom;

    animation: ellipsis-animation 1.6s steps(4, end) infinite;
}

@keyframes text-bounce {
    0%, 100% {
        transform: translateY(0) scale(1);
    }
    50% {
        transform: translateY(-8px) scale(1.03); /* ğŸ“ å‘ä¸Šè·³åŠ¨é«˜åº¦å’Œæ”¾å¤§æ¯”ä¾‹ */
    }
}

@keyframes ellipsis-animation {
    0%  { width: 0; }
    30% { width: 0.7ch; }
    60% { width: 1.45ch; }
    90%, 100% { width: 2.2ch; }
}


#spinner-overlay {
    background-color: rgba(240, 248, 255, 0.7) !important; /* ğŸ“ èƒŒæ™¯é®ç½©é¢œè‰²å’Œé€æ˜åº¦ */
    backdrop-filter: blur(5px) !important;
