/* ============================================================
   SillyTavern 云雾灰白主题
   ============================================================ */

/* ================== 1. 字体定义 ================== */

@font-face {
  font-family: 'ZhuqueFangsong';
  src: url("https://sharkpan.xyz/f/JkxLTg/%E6%B1%87%E6%96%87%E6%98%8E%E6%9C%9D%E4%BD%93.otf") format('truetype');
  font-weight: 200;
  font-style: normal;
}

/* ================== 2. CSS 变量 ================== */

:root {
  --scroll-area-offset: 14px;
  --avatar-height-double: calc(var(--avatar-base-height) * 2.1);
  --avatar-height-large: calc(var(--avatar-base-height) * var(--big-avatar-height-factor));
  --text-token-layout: contents;
}

* {
  --black30a: rgb(233 233 229 / 30%);
  --black70a: rgb(233 233 229 / %);
  --black50a: rgb(233 233 229 / 50%);
  --grey30: #F9F9F7;
}

/* ================== 3. 全局重置 ================== */

* {
  border: none !important;
  box-shadow: none !important;
}

body {
  font-family: 'ZhuqueFangsong', serif;
  line-height: 1.5;
  font-weight: 300;
  background: #F9F9F7;
}

::selection {
  background: #E1E1DC;
}

::-moz-selection {
  background: #E1E1DC;
}

/* ================== 4. 滚动条 ================== */

::-webkit-scrollbar {
  width: 3px;
  background: #F9F9F7;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(to bottom, #F2F2EF 0%, #9A9A94 100%);
  border-radius: 5px;
}

/* ================== 5. 表单控件 ================== */

/* 输入框 */
textarea,
input[type="text"] {
  font-family: 'ZhuqueFangsong', serif;
  font-size: 16px;
  font-weight: 300;
  line-height: 1.6;
  background-color: #F2F2EF;
  border-radius: 10px;
  padding: 4px;
  outline: none;
  transition: all 0.3s ease;
}

.text_pole {
  border-radius: 6px;
  padding: 8px 12px;
}

/* 复选框 */
input[type="checkbox"] {
  width: 14px;
  height: 14px;
  border-radius: 30%;
  appearance: none;
  -webkit-appearance: none;
  background: #E1E1DC;
  cursor: pointer;
  outline: none;
  position: relative;
  transition: background 0.16s;
  vertical-align: middle;
}

input[type="checkbox"] {
  background: #E1E1DC;
}

input[type="checkbox"]:hover {
  background: #9A9A94;
}

input[type="checkbox"]:checked {
  background: #9A9A94;
}

input[type="checkbox"]:checked:hover {
  background: #9A9A94;
}

input[type="checkbox"]::after {
  transition: border-color 0.16s;
}

/* 下拉框 */
select {
  background: #F2F2EF !important;
  border-radius: 8px !important;
  box-shadow: none !important;
  padding: 3px 28px 3px 10px !important;
  font-size: 14px !important;
  min-height: 24px !important;
  appearance: none !important;
  transition: background 0.18s;
  outline: none;
  cursor: pointer;
  vertical-align: middle;
}

select::-ms-expand {
  display: none;
}

select::after {
  content: '';
  position: absolute;
  right: 14px;
  top: 50%;
  width: 0;
  height: 0;
  pointer-events: none;
  border-left: 5px solid transparent;
  border-right: 5px solid transparent;
  border-top: 6px solid #9A9A94;
  transform: translateY(-50%);
}

/* 滑动条 */
input[type="range"]::-webkit-slider-runnable-track {
  height: 5px;
  background: #E1E1DC;
  border-radius: 4px;
}

input[type="range"]::-webkit-slider-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 14px;
  height: 14px;
  border-radius: 50%;
  background: #9A9A94;
  cursor: pointer;
  transition: background 0.18s;
  margin-top: -4px;
  position: relative;
  z-index: 2;
}

input[type="range"]:hover::-webkit-slider-thumb {
  background: #C2C2BD;
}

input[type="range"]::-moz-range-thumb {
  width: 14px;
  height: 14px;
  border-radius: 50%;
  background: #9A9A94;
  cursor: pointer;
  transition: background 0.18s;
  position: relative;
  z-index: 2;
}

input[type="range"]:hover::-moz-range-thumb {
  background: #C2C2BD;
}

input[type="range"]::-ms-thumb {
  width: 14px;
  height: 14px;
  border-radius: 50%;
  background: #9A9A94;
  cursor: pointer;
  transition: background 0.18s;
  position: relative;
  z-index: 2;
}

input[type="range"]:hover::-ms-thumb {
  background: #C2C2BD;
}

/* ================== 6. 消息区域 ================== */

.mes {
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 10px 0 20px 0;
  margin-bottom: 10px !important;
}

.mes_block {
  margin-top: -1px !important;
  margin-bottom: 45px !important;
  padding: 12px 15px !important;
}

.mes_text {
  margin: 0;
  padding: 0;
  line-height: 1.5;
}

.mes_block .mes_text {
  text-align: justify !important;
  padding-right: 5px !important;
}

/* ================== 7. 头像 ================== */

.mesAvatarWrapper {
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: 12px;
  padding: 12px 15px !important;
  margin-top: 100px;
  margin-bottom: 0 !important;
  width: 100%;
  background-color: transparent !important;
  border-radius: 8px;
}

.mesAvatarWrapper .avatar img,
.avatar img {
  width: 56px;
  height: 56px;
  object-fit: cover;
  border-radius: 50%;
  box-shadow: none;
  filter: none;
  background: transparent;
}

/* ================== 8. 信息条 ================== */

.mesIDDisplay,
.mes_timer,
.tokenCounterDisplay {
  display: var(--text-token-layout);
  color: #9A9A94;
  font-size: 12px;
  opacity: 0.75;
  margin: 2px 4px;
  font-family: 'ZhuqueFangsong', serif;
}

.mes_info_bar {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 8px;
  font-size: 12px;
  color: #9A9A94;
  font-family: 'ZhuqueFangsong', serif;
  opacity: 0.75;
  margin-bottom: 6px;
}

/* ================== 9. 代码块 ================== */

code {
  background-color: #F2F2EF;
  color: #18181A;
  font-family: 'ZhuqueFangsong', serif;
  font-size: 14px;
  border-radius: 5px;
  padding: 3px 7px;
  box-shadow: none;
}

code span.comment {
  color: #9A9A94;
  font-style: italic;
}

pre {
  background-color: #F2F2EF;
  color: #18181A;
  border-radius: 10px;
  padding: 14px 18px;
  margin: 16px 0;
  font-family: 'ZhuqueFangsong', serif;
  font-size: 13px;
  line-height: 1.65;
  position: relative;
  overflow-x: auto;
  box-shadow: none;
  transition: box-shadow 0.4s ease, transform 0.3s ease;
}

pre:hover {
  transform: translateY(-1px);
}

pre::before {
  content: "❇️";
  position: absolute;
  top: 8px;
  left: 12px;
  font-size: 14px;
  color: #9A9A94;
}

pre code {
  background-color: transparent !important;
  color: inherit !important;
  font-family: 'ZhuqueFangsong', serif;
  font-size: 13px;
  line-height: 1.65;
  display: block;
  white-space: pre-wrap;
  word-break: break-word;
  padding-left: 14px;
  position: relative;
  animation: codeFadeIn 0.4s ease;
}

@keyframes codeFadeIn {
  0% {
    opacity: 0;
    transform: translateY(4px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

/* ================== 10. 推理/思考区块 ================== */

.mes_reasoning_header {
  background: linear-gradient(90deg, #FFF 30%, #F9F9F7 100%);
  border-radius: 10px;
  padding: 1px 26px;
  margin: 14px auto;
  box-shadow: 0 4px 16px rgba(154, 154, 148, 0.08);
  display: flex;
  align-items: center;
  justify-content: center;
  backdrop-filter: blur(7px);
  position: relative;
  max-width: 92%;
  width: fit-content;
}

.mes_reasoning_header_title {
  font-size: 12px;
  font-weight: bold;
  padding: 0 14px;
  white-space: nowrap;
  overflow: visible;
  text-overflow: unset;
  text-align: center;
  letter-spacing: 0.04em;
  text-shadow: none;
}

.mes_reasoning_header_title::before,
.mes_reasoning_header_title::after {
  content: "☁️";
  display: inline-block;
  margin: 0 5px;
  opacity: 0.18;
  filter: none;
}

.mes_reasoning_arrow {
  font-size: 13px;
  color: #9A9A94;
  cursor: pointer;
  transition: transform 0.3s ease, color 0.3s ease;
  margin-left: 10px;
  margin-right: 4px;
  vertical-align: middle;
}

/* ================== 11. 抽屉与按钮 ================== */

.drawer-icon {
  width: 22px;
  height: 22px;
  font-size: 18px;
  line-height: 26px;
  color: #9A9A94;
  background: none !important;
  text-shadow: none !important;
  -webkit-background-clip: unset;
  -webkit-text-fill-color: unset;
  display: inline-flex;
  justify-content: center;
  align-items: center;
  padding: 0;
  margin: 6px 6px 3px 3px;
  border-radius: 6px;
  user-select: none;
  flex-shrink: 0;
  transition: background-color 0.2s ease;
}

.drawer-icon:hover {
  background-color: #F2F2EF;
  cursor: pointer;
}

.drawer-icon.left {
  margin-left: 0;
  margin-right: auto;
}

.drawer-icon.right {
  margin-left: auto;
  margin-right: 0;
}

.drawer-icon.top {
  align-self: flex-start;
}

.drawer-icon.bottom {
  align-self: flex-end;
}

#options_button,
#extensionsMenuButton,
#send_but {
  font-size: 18px;
  background-size: 60% 60%;
  background-position: center;
  background-repeat: no-repeat;
  color: #9A9A94;
}

/* ================== 12. 底栏 QR 按键 ================== */

#qr--bar {
  height: 0.1px;
}

#send_form:hover #qr--bar {
  height: 40px;
  transition: all 0.5s ease;
}

/* ================== 13. 收藏滑动 (HotSwap) ================== */

#HotSwapWrapper {
  overflow: hidden;
}

#HotSwapWrapper > div {
  overflow-x: auto;
  overflow-y: hidden;
  flex-wrap: nowrap;
  height: calc(var(--avatar-base-height) + var(--scroll-width) + var(--scroll-area-offset));
  max-height: var(--avatar-height-double);
}

body.big-avatars #HotSwapWrapper > div {
  height: calc(var(--avatar-height-large) + var(--scroll-width) + var(--scroll-area-offset));
  max-height: calc(var(--avatar-height-large) * 2.1);
}

#HotSwapWrapper > div::-webkit-scrollbar-thumb {
  background-color: var(--ui-color-sec);
}

/* ================== 14. 杂项 ================== */

.regex-script-label {
  font-family: monospace;
  background: #F2F2EF;
  padding: 4px 8px;
  border-radius: 4px;
}

/* ================== 15. 移动端适配 ================== */
@media screen and (max-width: 720px) {
  #chat {
    display: flex !important;
    flex-direction: column !important;
    align-items: center !important;
    padding: 0 !important;
  }

  .mes {
    display: flex !important;
    flex-direction: column !important;
    align-items: center !important;
    width: 96vw !important;
    max-width: 96vw !important;
    margin: 10px auto !important;  
    padding: 0 !important;
  }

  .mes_block {
    width: 100% !important;
    padding: 12px 15px !important;
  }

  .mesAvatarWrapper {
    width: 100% !important;
    padding: 12px 15px !important;
  }
}
