custom_css:
q {
  background: #D8D3EE;        /* 极浅莫兰迪紫底色 */
  color: #222;                /* 深灰黑字 */
  border-radius: 4px;         /* 偏方形圆角 */
  padding: 1px 8px;
  font-style: normal;
  font-weight: 500;
  text-decoration: none;
  margin: 0 2px;
  box-shadow: none;
  transition: background 0.16s;
}
q:before, q:after {
  content: '“';
  color: #B7AFC7;             /* 很淡的莫兰迪紫引号 */
  margin: 0 1.5px;
  font-size: 1.06em;
  vertical-align: top;
}
q:after {
  content: '”';
}

/* ================== 聊天消息区块整体间距 ================== */
.mes_block {
  margin-bottom: 45px !important;
}

/* ================== 移动端宽度适配 ================== */
@media screen and (max-width: 720px) {
  :root {
    --mes-width: 100vw;
    --mes-block-width: 100vw;
  }
  .mes, .mes_block {
    width: 100vw !important;
    max-width: 99vw !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    box-sizing: border-box !important;
  }
  .mes_block {
    padding: 0 !important;
  }
}

/* ================== 聊天头像图片 ================== */
.mesAvatarWrapper .avatar img {
  width: 56px;
  height: 56px;
  object-fit: cover;
  border-radius: 50%;                  /* 圆形头像 */
  border: 1px solid #B7AFC7;           /* 莫兰迪深紫描边 #B7AFC7 */
  box-shadow: none;                    /* 去除阴影 */
  filter: none;
}

/* ================== 底栏QR按键隐藏 ================== */
#send_form:hover #qr--bar {
    height: 40px;
    transition: all 0.5s ease;
}
#qr--bar {
    height: 0.1px;
}

/* ================== 代码注释文本颜色 ================== */
code span.comment {
    color: #B7AFC7;                  /* 莫兰迪紫色偏灰 #B7AFC7 */
    font-style: italic;
}

/* ================== 主体字体、行距、背景 ================== */
body {
    font-family: 'ZhuqueFangsong', serif;
    line-height: 1.5;
    font-weight: 300;
    background: #fff;                 /* 纯白背景 #fff */
}

/* ================== 嵌入字体 ================== */
@font-face {
    font-family: 'ZhuqueFangsong';
    src: url("https://sharkpan.xyz/f/JkxLTg/%E6%B1%87%E6%96%87%E6%98%8E%E6%9C%9D%E4%BD%93.otf") format('truetype');
    font-weight: 200;
    font-style: normal;
}

/* ================== 编辑输入框 ================== */
textarea, input[type="text"] {
    font-family: 'ZhuqueFangsong', serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-color: #D6C5E7;      /* 莫兰迪紫高亮 #D6C5E7 */
    border-radius: 10px;
    padding: 4px;
    box-shadow: 3px 3px 5px rgba(183,175,199,0.09); /* 莫兰迪紫阴影 rgba(183,175,199,0.09) */
    outline: none;
    transition: all 0.3s ease;
}

/* ================== 全局色彩变量莫兰迪低饱和紫 ================== */
*{
  --black30a: rgb(229 226 241 / 30%);  /* 莫兰迪紫主色 #E5E2F1 */
  --black50a: rgb(229 226 241 / 50%);  /* 莫兰迪紫主色 #E5E2F1 */
  --black70a: rgb(229 226 241 / %);  /* 莫兰迪紫主色 #E5E2F1 */
  --white20a: rgb(229 226 241 / 20%);  /* 莫兰迪紫主色 #E5E2F1 */
  --white30a: rgb(229 226 241 / 30%);  /* 莫兰迪紫主色 #E5E2F1 */
  --white50a: rgb(229 226 241 / 50%);  /* 莫兰迪紫主色 #E5E2F1 */
  --grey30: #F4F3FA;                   /* 更淡莫兰迪紫 #F4F3FA */
  --grey30a: rgb(244 243 250 / 30%);   /* 更淡莫兰迪紫 #F4F3FA */
  --grey7070a: rgb(183 175 199 / 70%); /* 莫兰迪深紫 #B7AFC7 */
}

/* ================== 消息区整体居中 ================== */
.mes {
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 10px 0 20px 0;
}

/* ================== 聊天气泡区块 ================== */
.mes_block {
  max-width: 90%;
  width: 100%;
  margin: 0 auto;
  text-align: left;
}

/* ================== 头像容器（整体下移） ================== */
.mesAvatarWrapper {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: 100px;
  margin-bottom: 10px;
}

/* ================== 信息类（楼层/计时/Token等） ================== */
.mesIDDisplay,
.mes_timer,
.tokenCounterDisplay {
  display: inline-block;
  color: #B7AFC7;                  /* 莫兰迪深紫 #B7AFC7 */
  font-size: 12px;
  opacity: 0.75;
  margin: 2px 4px;
  font-family: 'ZhuqueFangsong', serif;
}

/* ================== 信息条 ================== */
.mes_info_bar {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 8px;
  font-size: 12px;
  color: #B7AFC7;                  /* 莫兰迪深紫 #B7AFC7 */
  font-family: 'ZhuqueFangsong', serif;
  opacity: 0.75;
  margin-bottom: 6px;
}

/* ================== 移动端消息与头像布局 ================== */
@media screen and (max-width: 768px) {
  .mesAvatarWrapper {
    margin-top: 0px;
    margin-bottom: 8px;
    width: auto;
  }
  .mes_block {
    max-width: 95%;
  }
}

:root {
  --text-token-layout: contents;
}

.mesIDDisplay,
.mes_timer,
.tokenCounterDisplay {
  display: var(--text-token-layout);
  color: #B7AFC7;                  /* 莫兰迪深紫 #B7AFC7 */
}

/* ================== 消息整体间距 ================== */
.mes {
  margin-bottom: 6px;
}

/* ================== 消息文本 ================== */
.mes_text {
  margin: 0;
  padding: 0;
  line-height: 1.5;
}


/* ================== 正则脚本标签 ================== */
.regex-script-label {
  font-family: monospace;
  background: #F4F3FA;             /* 更淡莫兰迪紫底色 #F4F3FA */
  padding: 4px 8px;
  border-radius: 4px;
  color: #18181A;                  /* 黑色文本 #18181A */
  border: 1px solid #B7AFC7;       /* 莫兰迪深紫描边 #B7AFC7 */
}

/* ================== 输入栏 ================== */
.text_pole {
  border: 1px solid #B7AFC7;       /* 莫兰迪深紫描边 #B7AFC7 */
  border-radius: 6px;
  padding: 8px 12px;
  font-family: 'ZhuqueFangsong', serif;
  color: #18181A;                  /* 黑色文本 #18181A */
}

/* ================== 抽屉按钮图标（导航栏等） ================== */
.drawer-icon {
  width: 22px;
  height: 22px;
  font-size: 20px;
  line-height: 26px;
  color: #B7AFC7;                  /* 莫兰迪紫点缀 #B7AFC7 */
  background: none !important;
  text-shadow: none !important;
  -webkit-background-clip: unset;
  -webkit-text-fill-color: unset;
  display: inline-flex;
  justify-content: center;
  align-items: center;
  padding: 0;
  margin: 6px 6px 3px 3px;
  border-radius: 6px;
  user-select: none;
  flex-shrink: 0;
  transition: background-color 0.2s ease;
}

/* ================== 抽屉按钮悬停效果 ================== */
.drawer-icon:hover {
  background-color: #F4F3FA;       /* 更淡莫兰迪紫悬停 #F4F3FA */
  cursor: pointer;
}

/* ================== 抽屉按钮对齐 ================== */
.drawer-icon.left {
  margin-left: 0;
  margin-right: auto;
}
.drawer-icon.right {
  margin-left: auto;
  margin-right: 0;
}
.drawer-icon.top {
  align-self: flex-start;
}
.drawer-icon.bottom {
  align-self: flex-end;
}

/* ================== 代码块样式 ================== */
code {
  background-color: #F4F3FA;       /* 更淡莫兰迪紫代码块底色 #F4F3FA */
  color: #18181A;                  /* 黑色文本 #18181A */
  font-family: 'ZhuqueFangsong', serif;
  font-size: 14px;
  border: 1px solid #B7AFC7;       /* 莫兰迪深紫描边 #B7AFC7 */
  border-radius: 5px;
  padding: 3px 7px;
  box-shadow: none;
}

/* ================== 头像容器（横向+描边） ================== */
.mesAvatarWrapper {
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: 12px;
  padding-top: 10px;
  padding-right: 16px;
  padding-bottom: 16px;
  padding-left: 16px;
  width: 100%;
  background-color: transparent !important;
  border: 1px solid #B7AFC7;       /* 莫兰迪深紫描边 #B7AFC7 */
  border-radius: 8px;
  box-shadow: none;
  margin: 12px 0;
}

/* ================== 头像图片描边 ================== */
.avatar img {
  width: 56px;
  height: 56px;
  object-fit: cover;
  border-radius: 50%;
  border: 1px solid #B7AFC7;       /* 莫兰迪深紫描边 #B7AFC7 */
  background: transparent;
  box-shadow: none !important;
}

/* ================== 消息文本区块（对齐） ================== */
.mes_block .mes_text {
  text-align: justify !important;
  padding-right: 5px !important;
}

/* ================== 代码块整体样式 ================== */
pre {
  background-color: #F4F3FA;       /* 更淡莫兰迪紫 #F4F3FA */
  color: #18181A;                  /* 黑色文本 #18181A */
  border: 1px solid #B7AFC7;       /* 莫兰迪深紫描边 #B7AFC7 */
  border-radius: 10px;
  padding: 14px 18px;
  margin: 16px 0;
  font-family: 'ZhuqueFangsong', serif;
  font-size: 13px;
  line-height: 1.65;
  position: relative;
  overflow-x: auto;
  box-shadow:
    inset 0 0 6px #D6C5E7,       /* 莫兰迪紫高亮内阴影 #D6C5E7 */
    0 0 10px #F4F3FA;            /* 更淡莫兰迪紫外阴影 #F4F3FA */
  transition: box-shadow 0.4s ease, transform 0.3s ease;
}

pre:hover {
  box-shadow:
    inset 0 0 8px #D6C5E7,       /* 莫兰迪紫高亮内阴影 #D6C5E7 */
    0 0 14px #B7AFC7;            /* 莫兰迪深紫外阴影 #B7AFC7 */
  transform: translateY(-1px);
}

pre code {
  background-color: transparent !important;
  color: inherit !important;
  font-family: 'Courier New', monospace;
  font-size: 13px;
  line-height: 1.65;
  display: block;
  white-space: pre-wrap;
  word-break: break-word;
  padding-left: 14px;
  border-left: 2px solid #B7AFC7;   /* 莫兰迪深紫分隔线 #B7AFC7 */
  position: relative;
  animation: codeFadeIn 0.4s ease;
}

pre::before {
  content: "❇️";
  position: absolute;
  top: 8px;
  left: 12px;
  font-size: 14px;
  color: #B7AFC7;                    /* 莫兰迪深紫角标 #B7AFC7 */
}

@keyframes codeFadeIn {
  0% { opacity: 0; transform: translateY(4px); }
  100% { opacity: 1; transform: translateY(0); }
}

pre code {
  font-family: 'ZhuqueFangsong', serif;
  background-color: #F4F3FA;         /* 更淡莫兰迪紫代码块 #F4F3FA */
}

/* ================== 操作按钮颜色 ================== */
#options_button,
#extensionsMenuButton,
#send_but {
  font-size: 18px;
  background-size: 60% 60%;
  background-position: center;
  background-repeat: no-repeat;
  color: #B7AFC7;                   /* 莫兰迪紫图标色 #B7AFC7 */
}

/* ================== 固定间距区块 ================== */
.mes {
  margin-bottom: 10px !important;
}
.mesAvatarWrapper {
  padding: 12px 15px !important;
  margin-bottom: 0 !important;
}
.mes_block {
  margin-top: -1px !important;
  padding: 12px 15px !important;
}

/* ================== 极简莫兰迪紫复选框 ================== */
input[type="checkbox"] {
  width: 14px;
  height: 14px;
  border-radius: 30%;
  appearance: none;
  -webkit-appearance: none;
  background: #D2CCE6;            /* 莫兰迪紫复选框底色 #D2CCE6 */
  border: 1.2px solid #B7AFC7;    /* 莫兰迪深紫描边 #B7AFC7 */
  cursor: pointer;
  outline: none;
  position: relative;
  transition: border-color 0.16s;
  vertical-align: middle;
}
input[type="checkbox"]:hover {
  border-color: #A395BD;          /* 莫兰迪紫点缀悬停 #A395BD */
}
input[type="checkbox"]:checked {
  background: #B7AFC7;            /* 莫兰迪深紫选中 #B7AFC7 */
  border-color: #A395BD;          /* 莫兰迪紫点缀描边 #A395BD */
}
input[type="checkbox"]:checked::after {
  content: '';
  position: absolute;
  left: 3px;
  top: 0px;
  width: 6px;
  height: 11px;
  border-right: 2px solid #A395BD; /* 莫兰迪紫对勾 #A395BD */
  border-bottom: 2px solid #A395BD;/* 莫兰迪紫对勾 #A395BD */
  border-radius: 1px;
  transform: rotate(35deg);
  box-sizing: border-box;
  pointer-events: none;
}
input[type="checkbox"]::after {
  transition: border-color 0.16s;
}

/* ================== 极简莫兰迪紫圆角下拉框 ================== */
select {
    background: #D6C5E7 !important;          /* 莫兰迪紫高亮下拉底色 #D6C5E7 */
    border-radius: 8px !important;
    box-shadow: none !important;
padding: 3px 28px 3px 10px !important;
    color: #18181A !important;
    font-size: 14px !important;
    min-height: 24px !important;
    appearance: none !important;
    transition: border-color 0.18s;
    outline: none;
    cursor: pointer;
    vertical-align: middle;
    /* 可添加下方自定义箭头，方法见注释 */
}

/* ================== 下拉框悬停/聚焦高亮莫兰迪紫描边 ================== */

/* ================== 自定义极简箭头（适配莫兰迪紫） ================== */
select::-ms-expand { display: none; }           /* IE隐藏原生箭头 */
select::after {
    content: '';
    position: absolute;
    right: 14px;
    top: 50%;
    width: 0;
    height: 0;
    pointer-events: none;
    border-left: 5px solid transparent;
    border-right: 5px solid transparent;
    border-top: 6px solid #B7AFC7;             /* 莫兰迪紫三角箭头 #B7AFC7 */
    transform: translateY(-50%);
}

/* ================== 极简莫兰迪紫滚动条 ================== */
::-webkit-scrollbar {
  width: 3px;
  background: #F4F3FA;                    /* 更淡莫兰迪紫滚动条底色 #F4F3FA */
}
::-webkit-scrollbar-thumb {
  background: linear-gradient(
    to bottom,
    #D6C5E7 0%,                            /* 莫兰迪紫高亮滚动条 #D6C5E7 */
    #B7AFC7 100%                           /* 莫兰迪深紫滚动条 #B7AFC7 */
  );
  border-radius: 5px;
}

/* ================== 选中文本莫兰迪紫高亮 ================== */
::selection {
  background: #D6C5E7;                    /* 莫兰迪紫高亮 #D6C5E7 */
  color: #18181A;                         /* 黑色 #18181A */
}
::-moz-selection {
  background: #D6C5E7;                    /* 莫兰迪紫高亮 #D6C5E7 */
  color: #18181A;                         /* 黑色 #18181A */
}

/* ================== 滑动条轨道（莫兰迪紫纯色） ================== */
input[type="range"]::-webkit-slider-runnable-track {
  height: 5px;
  background: #E5E2F1;                   /* 纯色轨道：莫兰迪紫 */
  border-radius: 4px;
}

/* ================== 滑块 thumb（莫兰迪紫圆点） ================== */
input[type="range"]::-webkit-slider-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 14px;
  height: 14px;
  border-radius: 50%;
  background: #D6C5E7;                    /* 莫兰迪紫高亮 #D6C5E7 */
  border: 2px solid #D6C5E7;              /* 莫兰迪紫高亮描边 */
  box-shadow: none;
  cursor: pointer;
  transition: background 0.18s;
  margin-top: -4px;
  position: relative;
  z-index: 2;
}
input[type="range"]:hover::-webkit-slider-thumb {
  background: #A395BD;                    /* 莫兰迪紫点缀悬停 #A395BD */
}
input[type="range"]::-moz-range-thumb {
  width: 14px;
  height: 14px;
  border-radius: 50%;
  background: #B7AFC7;                    /* 莫兰迪深紫 #B7AFC7 */
  border: 2px solid #FFF;                 /* 白描边 #FFF */
  box-shadow: none;
  cursor: pointer;
  transition: background 0.18s;
  position: relative;
  z-index: 2;
}
input[type="range"]:hover::-moz-range-thumb {
  background: #A395BD;                    /* 莫兰迪紫点缀悬停 #A395BD */
}
input[type="range"]::-ms-thumb {
  width: 14px;
  height: 14px;
  border-radius: 50%;
  background: #B7AFC7;                    /* 莫兰迪深紫 #B7AFC7 */
  border: 2px solid #FFF;                 /* 白描边 #FFF */
  box-shadow: none;
  cursor: pointer;
  transition: background 0.18s;
  position: relative;
  z-index: 2;
}
input[type="range"]:hover::-ms-thumb {
  background: #A395BD;                    /* 莫兰迪紫点缀悬停 #A395BD */
}

/* ================== 滑动条主样式重置 ================== */
input[type="range"] {
  -webkit-appearance: none;
  appearance: none;
  width: 100%;
  margin: 0;
  background: transparent;
  box-shadow: none;
  outline: none;
  height: 28px;
  cursor: pointer;
  padding: 0;
}

/* ================== 强制去除所有控件阴影和边框 ================== */
* {
  border: none !important;
  box-shadow: none !important;
}

/* ===== 全局极简（防止无关阴影） ===== */
* {
  border: none !important;
  box-shadow: none !important;
}

.mes_reasoning_header {
  background: linear-gradient(90deg, #FFF 30%, #E5E2F1 100%); /* 顶部背景白到莫兰迪紫渐变 */
  border: 1.5px solid #E5E2F1;     /* 边框莫兰迪紫 */
  border-radius: 10px;
  padding: 6px 26px;
  margin: 14px auto;
  color: #18181A;
  box-shadow: 0 4px 16px rgba(229,226,241,0.08);
  display: flex;
  align-items: center;
  justify-content: center;
  backdrop-filter: blur(7px);
  position: relative;
  max-width: 92%;
  width: fit-content;
}
.mes_reasoning_header_title::before,
.mes_reasoning_header_title::after {
  content: "☀️";
  display: inline-block;
  margin: 0 5px;
  opacity: 0.18;
  filter: none;
}
.mes_reasoning_header_title {
  font-size: 12px;
  font-weight: bold;
  color: #18181A;
  padding: 0 14px;
  white-space: nowrap;
  overflow: visible;
  text-overflow: unset;
  text-align: center;
  letter-spacing: 0.04em;
  text-shadow: none;
}
.mes_reasoning_arrow {
  font-size: 13px;
  color: #E5E2F1; /* 小箭头莫兰迪紫 */
  cursor: pointer;
  transition: transform 0.3s ease, color 0.3s ease;
  margin-left: 10px;
  margin-right: 4px;
  vertical-align: middle;
}
