custom_css:
/*==================== 主题：小狗嘎嘎青花 ====================*/    
/* 主色：#FAF5EB 米白 | 强调 #0A2D3C | 字体 #222 */
q {
  text-decoration: underline dashed 2.5px #88A3BC;
  text-underline-offset: 4px;
  font-style: normal;
  color: #38506B;
  padding: 1px 6px;
  border-radius: 5px;
}

.regex-script-label {    
    max-width: 94%;    
}    
.mes_block {    
  margin-bottom: 45px !important;    
}    
@media screen and (max-width: 720px) {    
  :root {    
    --mes-width: 100vw;    
    --mes-block-width: 100vw;    
  }    
  .mes, .mes_block {    
    width: 100vw !important;    
    max-width: 99vw !important;    
    margin-left: 0 !important;    
    margin-right: 0 !important;    
    box-sizing: border-box !important;    
  }    
  .mes_block {    
    padding: 0 !important;    
  }    
}    
.mesAvatarWrapper .avatar img {    
  width: 56px;    
  height: 56px;    
  object-fit: cover;    
  border-radius: 50%;    
  border: 1.5px solid #0A2D3C;      /* 深蓝描边 */    
  box-shadow: none;    
  filter: none;    
}    
/* 底栏QR按键隐藏 */    
#send_form:hover #qr--bar {    
    height: 40px;    
    transition: all 0.5s ease;    
}    
#qr--bar {    
    height: 0.1px;    
}    
code span.comment {    
    color: #90877A;    
    font-style: italic;    
}    
body {    
    font-family: 'ZhuqueFangsong', serif;    
    line-height: 1.5;    
    font-weight: 300;    
    background: #FAF5EB;                /* 米白背景 */    
    color: #222;                        /* 深灰文字 */    
}    
@font-face {    
    font-family: 'ZhuqueFangsong';    
    src: url("http://sharkpan.xyz/f/24j6iQ/%E4%B8%AD%E6%96%87%20-%20Huiwen.otf") format('truetype');    
    font-weight: 200;    
    font-style: normal;    
}    
@font-face {    
  font-family: 'Huiwen';    
  src: url("http://sharkpan.xyz/f/24j6iQ/%E4%B8%AD%E6%96%87%20-%20Huiwen.otf") format('opentype');    
  font-weight: 400;    
  font-style: normal;    
}    

/* 强制输入框使用该字体 */    
textarea, input[type="text"] {    
  font-family: 'Huiwen', 'ZhuqueFangsong', serif !important;    
  font-size: 16px;    
  font-weight: 400;    
  line-height: 1.6;    
  color: #0A2D3C; /* 深蓝主色文字 */    
  background-color: #F3ECE1;    
  border: 1px solid #0A2D3C;    
  border-radius: 5px;    
  padding: 10px;    
  box-shadow: 3px 3px 5px rgba(10,45,60, 0.08);    
  outline: none;    
  transition: all 0.3s ease;    
}    

*{    
  --black30a: rgb(10 45 60 / 30%);    
  --black50a: rgb(10 45 60 / 50%);    
  --black70a: rgb(10 45 60 / %);    
  --white20a: rgb(250 245 235 / 20%);    
  --white30a: rgb(250 245 235 / 30%);    
  --white50a: rgb(250 245 235 / 50%);    
  --grey30: rgb(243 236 225);    
  --grey30a: rgb(243 236 225 / 30%);    
  --grey7070a: rgb(243 236 225 / 70%);    
}    
/* 聊天消息容器整体居中布局 */    
.mes {    
  display: flex;    
  flex-direction: column;    
  align-items: center;    
  margin: 10px 0 20px 0;    
}    
/* 头像容器：整体下移（关键） */    
.mesAvatarWrapper {    
  display: flex;    
  flex-direction: column;    
  align-items: center;    
  justify-content: center;    
  margin-top: 100px;    
  margin-bottom: 10px;    
}    
/* 信息类（楼层、计时、token）设置为 inline，颜色继承 */    
.mesIDDisplay,    
.mes_timer,    
.tokenCounterDisplay {    
  display: inline-block;    
  color: #5C6973; /* 深蓝灰 */    
  font-size: 12px;    
  opacity: 0.85;    
  margin: 2px 4px;    
  font-family: 'ZhuqueFangsong', serif;    
}    
/* 信息条（放在头像上或下都行） */    
.mes_info_bar {    
  display: flex;    
  justify-content: center;    
  align-items: center;    
  gap: 8px;    
  font-size: 12px;    
  color: #5C6973;    
  font-family: 'ZhuqueFangsong', serif;    
  opacity: 0.85;    
  margin-bottom: 6px;    
}    
/* 移动端保持一致布局 */    
@media screen and (max-width: 768px) {    
  .mesAvatarWrapper {    
    margin-top: 0px;    
    margin-bottom: 8px;    
    width: auto;    
  }    
  .mes_block {    
    max-width: 95%;    
  }    
}    
:root {    
  --text-token-layout: contents;    
}    
.mesIDDisplay,    
.mes_timer,    
.tokenCounterDisplay {    
  display: var(--text-token-layout);    
  color: #5C6973;    
}    
/* 消息整体间距压缩 */    
.mes {    
  margin-bottom: 6px;    
}    
/* 消息文本紧凑 */    
.mes_text {    
  margin: 0;    
  padding: 0;    
  line-height: 1.5;    
}    
/* 思维链容器 */    
.mes_reasoning {    
  border-left: 3px solid #0A2D3C;    
  padding: 10px;    
  font-family: 'ZhuqueFangsong', serif;    
}    

/* 正则脚本标签：黑蓝高质感风格 */    
.regex-script-label {    
  font-family: 'Fira Mono', 'Menlo', 'monospace';    
  background: linear-gradient(102deg, #28425B 80%, #0A2D3C 100%);    
  color: #FAF5EB;    
  padding: 5px 12px 5px 12px;    
  border-radius: 6px;    
  border: 1.2px solid #0A2D3C;    
  box-shadow: 0 2px 8px rgba(10,45,60,0.10) inset, 0 1px 1.5px rgba(250,245,235,0.09);    
  letter-spacing: 0.04em;    
  font-size: 13px;    
  font-weight: 500;    
  margin: 2px 0;    
  transition: box-shadow 0.18s, border 0.18s;    
}    
.regex-script-label:hover {    
  border-color: #FAF5EB;    
  box-shadow: 0 4px 18px rgba(10,45,60,0.18) inset, 0 2px 8px rgba(250,245,235,0.10);    
  color: #0A2D3C;    
  background: linear-gradient(102deg, #FAF5EB 80%, #E1D5C9 100%);    
  cursor: pointer;    
}    

/* 输入框 */    
.text_pole {    
  background-color: #F3ECE1;    
  border: 1px solid #0A2D3C;    
  border-radius: 6px;    
  padding: 8px 12px;    
  font-family: 'ZhuqueFangsong', serif;    
  color: #0A2D3C;
}    
.drawer-icon {    
  width: 28px;    
  height: 28px;    
  font-size: 24px;    
  line-height: 28px;    
  color: #0A2D3C;         /* 深蓝主色图标 */    
  background: none !important;    
  text-shadow: none !important;    
  -webkit-background-clip: unset;    
  -webkit-text-fill-color: unset;    
  display: inline-flex;    
  justify-content: center;    
  align-items: center;    
  padding: 0;    
  margin: 6px 6px 3px 3px;    
  border-radius: 6px;    
  user-select: none;    
  flex-shrink: 0;    
  transition: background-color 0.2s ease;    
}    

nav, .nav-bar, .drawer, .top-nav, .header-bar {    
  background-color: #F3ECE1;  /* 米白卡片背景 */    
  box-shadow: none;    
  border-bottom: 1px solid #0A2D3C;    
}    
/* 悬停效果 */    
.drawer-icon:hover {    
  background-color: #0A2D3C;    
  color: #164F68;
  cursor: pointer;    
}    
/* 水平对齐控制 */    
.drawer-icon.left {    
  margin-left: 0;    
  margin-right: auto;    
}    
.drawer-icon.right {    
  margin-left: auto;    
  margin-right: 0;    
}    
/* 垂直对齐控制 */    
.drawer-icon.top {    
  align-self: flex-start;    
}    
.drawer-icon.bottom {    
  align-self: flex-end;    
}    
code {    
  background-color: #F3ECE1;    
  color: #0A2D3C;    
  font-family: 'ZhuqueFangsong', serif;    
  font-size: 14px;    
  border: 1px solid #0A2D3C;    
  border-radius: 5px;    
  padding: 3px 7px;    
  box-shadow: none;    
}    
/* ===== 头像容器（柔白极简风 + 扩大底部内边距） ===== */    
.mesAvatarWrapper {    
  display: flex;    
  flex-direction: row;    
  align-items: center;    
  gap: 12px;    
  padding-top: 10px;    
  padding-right: 16px;    
  padding-bottom: 16px;    
  padding-left: 16px;    
  width: 100%;    
  background-color: transparent !important;    
  border: 1.5px solid #0A2D3C;    
  border-radius: 8px;    
  box-shadow: none;    
  margin: 12px 0;    
}    
/* ===== 头像图像（圆形 + 极简描边） ===== */    
.avatar img {    
  width: 56px;    
  height: 56px;    
  object-fit: cover;    
  border-radius: 50%;    
  border: 1.5px solid #0A2D3C;    
  background: transparent;    
  box-shadow: none !important;    
}    

/* 深色思维链头部容器 */    
.mes_reasoning_header {    
  background: #0A2D3C;     /* 深蓝主色 */    
  border: 1.5px solid #0A2D3C;    
  border-radius: 10px;    
  padding: 8px 26px;    
  margin: 14px auto;    
  color: #FAF5EB;    
  box-shadow: 0 4px 16px rgba(10,45,60,0.08);    
  display: flex;    
  align-items: center;    
  justify-content: center;    
  backdrop-filter: blur(7px);    
  position: relative;    
  max-width: 92%;    
  width: fit-content;    
}    

/* ☀️ emoji作为左右角标 */    
.mes_reasoning_header_title::before,    
.mes_reasoning_header_title::after {    
  content: "☀️";    
  display: inline-block;    
  margin: 0 5px;    
  opacity: 0.75;    
  filter: drop-shadow(0 0 2px rgba(10,45,60, 0.12));    
}    

/* 标题文字本身 */    
.mes_reasoning_header_title {    
  font-size: 13px;    
  font-weight: bold;    
  color: #FAF5EB;    
  padding: 0 14px;    
  white-space: nowrap;    
  overflow: visible;    
  text-overflow: unset;    
  text-align: center;    
  letter-spacing: 0.04em;    
  text-shadow: 0 1px 6px rgba(10,45,60,0.12), 0 0.5px 2px rgba(250,245,235,0.09);    
}    

/* 箭头样式简化 */    
.mes_reasoning_arrow {    
  font-size: 13px;    
  color: #FAF5EB;    
  cursor: pointer;    
  transition: transform 0.3s ease, color 0.3s ease;    
  margin-left: 10px;    
  margin-right: 4px;    
  vertical-align: middle;    
}    
.mes_reasoning_arrow:hover {    
  color: #0A2D3C;    
  background: #FAF5EB;
  transform: scale(1.17);    
}    
.mes_block .mes_text {    
  text-align: justify !important;    
  padding-right: 5px !important;    
}    

pre {    
  background-color: #F3ECE1;    
  color: #0A2D3C;    
  border: 1px solid #0A2D3C;    
  border-radius: 10px;    
  padding: 14px 18px;    
  margin: 16px 0;    
  font-family: 'ZhuqueFangsong', serif;    
  font-size: 13px;    
  line-height: 1.65;    
  position: relative;    
  overflow-x: auto;    
  box-shadow: inset 0 0 6px rgba(10,45,60,0.09), 0 0 10px rgba(10,45,60,0.10);    
  transition: box-shadow 0.4s ease, transform 0.3s ease;    
}    
pre:hover {    
  box-shadow: inset 0 0 8px rgba(10,45,60,0.16), 0 0 14px rgba(10,45,60,0.16);    
  transform: translateY(-1px);    
}    
pre code {    
  background-color: transparent !important;    
  color: inherit !important;    
  font-family: 'Courier New', monospace;    
  font-size: 13px;    
  line-height: 1.65;    
  display: block;    
  white-space: pre-wrap;    
  word-break: break-word;    
  padding-left: 14px;    
  border-left: 2px solid #0A2D3C;    
  position: relative;    
  animation: codeFadeIn 0.4s ease;    
}    
pre::before {    
  content: "❈";    
  position: absolute;    
  top: 8px;    
  left: 12px;    
  font-size: 14px;    
  color: rgba(10,45,60,0.14);    
}    
@keyframes codeFadeIn {    
  0% { opacity: 0; transform: translateY(4px); }    
  100% { opacity: 1; transform: translateY(0); }    
}    
pre code {    
  font-family: 'ZhuqueFangsong', serif;    
  background-color: #F3ECE1;    
}    
#options_button,    
#extensionsMenuButton,    
#send_but {    
  font-size: 18px;    
  background-size: 60% 60%;    
  background-position: center;    
  background-repeat: no-repeat;    
  color: #0A2D3C;  /* 深蓝图标 */    
}    
/* ===== 固定间距系统 ===== */    
.mes {    
  margin-bottom: 10px !important;    
}    
.mesAvatarWrapper {    
  padding: 12px 15px !important;    
  margin-bottom: 0 !important;    
}    
.mes_block {    
  margin-top: -1px !important;    
  padding: 12px 15px !important;    
}    
.mes_block {    
  margin-bottom: 45px !important;    
}    
/* 滑块、滚动条等控件适配浅米蓝风格 */    
::-webkit-scrollbar {    
  width: 3px;    
  background: #FAF5EB
