custom_css:
/*
 * SillyTavern Custom UI Theme：抹茶ฅ^•ﻌ•^ฅbyleviathan
 * 感谢：络络老师的思维链美化
 * 二改请标明出处，禁止二传
 */

@import url("https://fontsapi.zeoseven.com/309/main/result.css");

body {
    font-family: "KingHwaOldSong";
    font-weight: normal;
}

/* ================================== */
/* 全局与核心变量          */
/* ================================== */
:root {
    --main-font: 'NotoSans', sans-serif;
    --text-main: #4a634e; 
    --text-light: #6b8a70; 
    --text-on-accent: #f0f3f0; 
    --bg-main: #f4f6f3; 
    --bg-chat: #eef1ec; 
    --panel-bg: rgba(225, 232, 226, 0.75); 
    --blur-strength: 12px; 
    --user-bubble-bg: #d8e2d9; 
    --bot-bubble-bg: #ffffff; 
    --accent-primary: #a5c3a9; 
    --accent-secondary: #c9dbc9; 
    --accent-hover: #b5d0b9; 
    --border-color: #d1ddd3; 
    --shadow-soft: 0 4px 12px rgba(92, 115, 96, 0.1);
    --radius-l: 20px; 
    --radius-m: 16px; 
    --radius-s: 12px; 
    --text-shadow-color: rgba(200, 210, 200, 0.3); 
}

/* ================================== */
/* 基础元素美化            */
/* ================================== */
/* --- 聊天区域 --- */

body.bubblechat .mes {
    padding: 10px;
    border-radius: 10px;
    background-color: var(--SmartThemeBotMesBlurTintColor);
    margin-bottom: 5px;
    border: none; 
}
#chat {
    border: none !important;
    backdrop-filter: none; 
    background-color: none;
}

/* --- 滚动条 --- */
::-webkit-scrollbar {
    width: 8px;
    height: 8px;
}
::-webkit-scrollbar-track {
    background: transparent;
}
::-webkit-scrollbar-thumb {
    background-color: var(--accent-secondary);
    border-radius: 10px;
    border: 2px solid var(--bg-chat);
}
::-webkit-scrollbar-thumb:hover {
    background-color: var(--accent-primary);
}


h1, h2, h3, h4, h5, b, strong {
    color: var(--text-main);
}

hr {
    border: none;
    height: 1px;
    background-image: linear-gradient(to right, transparent, var(--border-color), transparent);
    margin: 15px 0;
}

/* ================================== */
/* 面板、抽屉与弹出窗口美化      */
/* ================================== */

/* --- 通用面板/抽屉样式 --- */
.drawer-content, .right_menu, #character_popup, #WorldInfo, #options .options-content {
    background-color: var(--panel-bg);
    backdrop-filter: blur(var(--blur-strength));
    -webkit-backdrop-filter: blur(var(--blur-strength));
    border: none;
    border-radius: var(--radius-l);
    box-shadow: var(--shadow-soft);
    transition: all 0.3s ease;
}

#right-nav-panel, #left-nav-panel {
    border-radius: var(--radius-l) 0 0 var(--radius-l);
}
#right-nav-panel {
    border: none; 
    box-shadow: none;
    border-radius: 10px;
    overflow: hidden;
    flex-flow: column;
    min-width: 100px;
}

#left-nav-panel {
    border-radius: 0 var(--radius-l) var(--radius-l) 0;
}

/* --- 头部高亮 --- */
.standoutHeader, .inline-drawer-header {
    background-color: rgba(165, 195, 169, 0.2);
    padding: 8px 12px;
    border-radius: var(--radius-m);
    margin-bottom: 10px;
    color: var(--text-main);
    font-weight: bold;
}

/* ================================== */
/* 聊天界面美化         */
/* ================================== */


.mes {
    position: relative;
    padding: 0;
    margin-top: 15px;
    margin-bottom: 30px; 


    border-radius: var(--radius-l);
    box-shadow: var(--shadow-soft);
    border: 1px solid rgba(255, 255, 255, 0.4);
    backdrop-filter: blur(var(--blur-strength));
    -webkit-backdrop-filter: blur(var(--blur-strength));
}


.mes .mes_block {
    position: relative;
    background: none;
    padding: 0;
    box-shadow: none;
    border-radius: 0;
}


.mes::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 75px;
    background-color: rgba(255, 255, 255, 0.7); 
    backdrop-filter: blur(var(--blur-strength));
    -webkit-backdrop-filter: blur(var(--blur-strength));
    border-radius: var(--radius-m);
    box-shadow: var(--shadow-soft);
    z-index: 0;
}


.mes .avatar {
    position: absolute;
    top: 5px; 
    left: 20px;
    width: 65px !important;
    height: 65px !important;
    z-index: 2;
}

.mes .avatar img {
    width: 100%;
    height: 100%;
    border-radius: 50%;

    box-shadow: 0 6px 18px rgba(92, 115, 96, 0.25);
    border: 3px solid #fff;
}


.mes .mes_timer,
.mes .tokenCounterDisplay,
.mes .mesIDDisplay {
    position: absolute;
    top: 7px;
    background-color: rgba(224, 240, 227, 0.8); 
    color: var(--text-light);
    padding: 4px 12px;
    border-radius: 16px;
    font-size: 11px;
    font-weight: bold;
    box-shadow: 0 2px 4px rgba(92, 115, 96, 0.1);
    transition: all 0.2s ease;
    z-index: 1;
}
.mes .mes_timer:hover,
.mes .tokenCounterDisplay:hover,
.mes .mesIDDisplay:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 8px rgba(92, 115, 96, 0.15);
}



.mes:not(.is_user) .mes_timer { left: 100px; }
.mes:not(.is_user) .tokenCounterDisplay { left: 170px; } 
.mes:not(.is_user) .mesIDDisplay { left: 240px; }     



.mes.is_user .mes_timer { right: 100px; left: auto; }
.mes.is_user .tokenCounterDisplay { right: 170px; left: auto; }
.mes.is_user .mesIDDisplay { right: 240px; left: auto; }



.mes .ch_name {
    position: absolute;
    top: 25px;
    left: 60px;
    right: 15px;
    height: 30px;
    line-height: 30px;
    padding: 0 15px;
    color: var(--text-main);
    font-weight: bold;
    font-size: 13px;
    background: linear-gradient(to right, #d8e2d9, rgba(216, 226, 217, 0));
    z-index: 1;
}


.mes.is_user .ch_name {
    background: linear-gradient(to left, #d8e2d9, rgba(216, 226, 217, 0));
    text-align: right;
}



/* 重新定义消息文本块的样式 */
.mes .mes_text {
    margin-top: 85px;

    padding: 20px;
    min-height: 40px; 
    line-height: 1.7;
    font-size: 14px;
    padding-left: 10px !important;
    padding-right: 10px !important;


    border-radius: 0;
    box-shadow: none;
    border: none;
    backdrop-filter: none;
    background-color: transparent; 
}


#send_textarea {
    background-color: #fff;
    border: none;
    border-radius: var(--radius-m);
    padding: 0;
    color: var(--text-main);
}
#send_textarea::placeholder {
    color: var(--text-light);
}

#send_but, #mes_impersonate, #mes_continue, #options_button {
    color: var(--accent-primary);
    font-size: 24px;
    transition: all 0.2s ease;
}
#send_but:hover {
    transform: scale(1.1);
    color: var(--text-main);
}

/* ================================== */
/* 交互元素美化 (按钮/表单)    */
/* ================================== */

/* --- 通用按钮 --- */
.menu_button {
    background-image: linear-gradient(145deg, var(--accent-primary), var(--accent-secondary));
    color: var(--text-on-accent);
    border: none;
    border-radius: var(--radius-m);
    padding: 5px 12px;
    box-shadow: 0 2px 5px rgba(165, 195, 169, 0.3);
    font-weight: bold;
    transition: all 0.2s ease;
}
.menu_button:hover {
    background-image: linear-gradient(145deg, var(--accent-hover), var(--accent-primary));
    box-shadow: 0 4px 8px rgba(165, 195, 169, 0.4);
    transform: translateY(-2px);
}
.menu_button:active {
    transform: translateY(0);
}

/* --- API 连接按钮 --- */
.api_button {
    background-image: linear-gradient(145deg, #8dcbb5, #a5d3b8);
}
.api_button:hover {
    background-image: linear-gradient(145deg, #99d2c0, #b1e0ca);
}


/* --- 输入框/选择框 --- */
.text_pole, select, input[type="text"], input[type="search"], input[type="number"], textarea {
    background-color: rgba(255, 255, 255, 0.8);
    border: 1px solid var(--border-color);
    border-radius: var(--radius-s);
    padding: 8px 12px;
    color: var(--text-main);
    transition: border-color 0.2s, box-shadow 0.2s;
}
.text_pole:focus, select:focus, input:focus, textarea:focus {
    border-color: var(--accent-primary);
    box-shadow: 0 0 0 3px rgba(165, 195, 169, 0.3);
}

/* --- 复选框 --- */
input[type="checkbox"] {
    appearance: none;
    -webkit-appearance: none;
    width: 20px;
    height: 20px;
    border-radius: 6px;
    border: 2px solid var(--border-color);
    background-color: #fff;
    cursor: pointer;
    vertical-align: middle;
    margin-right: 8px;
    position: relative;
    transition: all 0.2s ease;
}
input[type="checkbox"]:checked {
    background-color: var(--accent-primary);
    border-color: var(--accent-primary);
}
input[type="checkbox"]:checked::after {
    content: '✔';
    color: var(--text-on-accent);
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    font-size: 14px;
}

/* --- 滑块 --- */
.neo-range-slider {
    -webkit-appearance: none;
    appearance: none;
    width: 100%;
    height: 6px;
    background: var(--border-color);
    border-radius: 5px;
    outline: none;
}
.neo-range-slider::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    width: 20px;
    height: 20px;
    background: #fff;
    border: 3px solid var(--accent-primary);
    border-radius: 50%;
    cursor: pointer;
    box-shadow: var(--shadow-soft);
}
.neo-range-slider::-moz-range-thumb {
    width: 20px;
    height: 20px;
    background: #fff;
    border: 3px solid var(--accent-primary);
    border-radius: 50%;
    cursor: pointer;
}

/* ================================== */
/* 特定区域美化            */
/* ================================== */

/* --- 角色列表 --- */
/*#rm_print_characters_block .character_select {
    background-color: #f4f6f3;
    border-radius: var(--radius-m);
    margin-bottom: 10px;
    box-shadow: var(--shadow-soft);
    border: none;
    transition: all 0.2s ease;
}
#rm_print_characters_block .character_select:hover {
    border-color: var(--accent-primary);
    transform: translateY(-2px);
}
#rm_print_characters_block .character_select .avatar img {
    border-radius: var(--radius-m);
}
#rm_print_characters_block .character_select .ch_name {
    font-weight: bold;
}*/

/* --- 世界信息 (Lorebook) --- */
.world_entry {
    background-color: rgba(255, 255, 255, 0.7);
    border-radius: var(--radius-m);
    padding: none;
    margin-bottom: 10px;
    border: none;
}
.world_entry textarea[name="comment"] {
    font-weight: bold;
    font-size: 1em;
    background: transparent;
    border: none;
}


/* --- 顶部栏图标 --- */
#top-bar .drawer-icon {
    color: var(--text-main);
    background: white;
    padding: 10px;
    border-radius: 50%;
    transition: all 0.2s ease;
}

#top-bar .drawer-icon:hover {
    transform: scale(1.1);
    color: var(--accent-primary);
}


/* --- Waifu/Visual Novel 模式优化 --- */
body.waifuMode #chat {
    background-image: linear-gradient(to top, rgba(238, 241, 236, 1) 0%, rgba(238, 241, 236, 0.7) 30%, rgba(238, 241, 236, 0) 100%);
    background-color: transparent !important;
}

body.waifuMode #form_sheld {
    background-color: rgba(225, 232, 226, 0.6);
}


.wi-card-entry {
    border: none;
    padding: 0 0px;
    margin-bottom: 1px;
}

/* ================================== */
/* 特定标题与扩展菜单美化  */
/* ================================== */

#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4,
.standoutHeader {

    background-image: linear-gradient(100deg, rgba(199, 219, 201, 0.5) 0%, rgba(225, 232, 226, 0.3) 100%);
    background-size: auto; 
    background-position: auto; 
    background-repeat: no-repeat; 


    border: 1px solid rgba(255, 255, 255, 0.6);
    border-radius: var(--radius-m); 

    padding: 8px 12px;
    margin-bottom: 10px;

    color: var(--text-main);
    text-shadow: 1px 1px 2px rgba(255, 255, 255, 0.7);

    transition: all 250ms ease-in-out;
}


#extensions_settings .inline-drawer-toggle.inline-drawer-header:hover,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header:hover,
#user-settings-block h4:hover,
.standoutHeader:hover {
    transform: translateY(-1px);
    box-shadow: var(--shadow-soft);
    border-color: #fff;
}

#send_form {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    width: 100%;
    margin: 0 auto 0 auto;
    border: none; 
    border-radius: 0 0 10px 10px;
    background-color: var(--SmartThemeBlurTintColor);
    backdrop-filter: blur(var(--SmartThemeBlurStrength));
}

#send_textarea {
    background-color: #fff;
    border: none;
    border-radius: var(--radius-m);
    padding: 10px;
    color: var(--text-main);
}

#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt {
    align-items: center;
    padding: 0.5em;
    border: 2px solid var(--accent-primary);
    border-radius: 15px;
}

#completion_prompt_manager .completion_prompt_manager_header {
    color: var(--text-main);
}
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name, #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
    color: var(--text-light);
}

/* ================================== */
/* 思维链/思考框  */
/* ================================== */

/* 思考框主容器 */
details.mes_reasoning_details {
  width: 90%;
  margin: 20px auto;
  background: linear-gradient(135deg, rgba(238, 245, 239, 0.7) 0%, rgba(228, 240, 230, 0.6) 100%);
  backdrop-filter: blur(calc(var(--blur-strength) - 2px)); 
  -webkit-backdrop-filter: blur(calc(var(--blur-strength) - 2px));
  border-radius: var(--radius-l);
  border: 1px solid rgba(200, 220, 205, 0.5);
  box-shadow: 0 3px 10px rgba(92, 115, 96, 0.08);
  transition: all 0.25s cubic-bezier(0.215, 0.61, 0.355, 1);
  overflow: hidden;
  will-change: transform, box-shadow;
  padding: 0 !important;
}


details.mes_reasoning_details:hover,
details.mes_reasoning_details:focus-within,
details.mes_reasoning_details.hover { /* 兼容hover类 */
    border-color: rgba(180, 200, 185, 0.7);
    box-shadow: 0 5px 15px rgba(92, 115, 96, 0.1);
}

details.mes_reasoning_details.open,
details.mes_reasoning_details.open-force { /* 兼容 open 和 open-force 类 */
    box-shadow: 0 7px 22px rgba(92, 115, 96, 0.12);
    transform: translateY(-1px);
}

/* 标题栏 */
details.mes_reasoning_details > summary.mes_reasoning_summary {
  padding: 12px 16px;
  background: rgba(180, 205, 185, 0.1); 
  color: var(--text-main);
  cursor: pointer;
  list-style: none;
  position: relative;
  outline: none;
  width: 100%;
  box-sizing: border-box;
  display: flex;
  justify-content: space-between;
  align-items: center;
  border-left: none !important;
  transition: background-color 0.2s ease-in-out;
  user-select: none;
  overflow: hidden;
}

/* 标题栏交互 */
details.mes_reasoning_details > summary.mes_reasoning_summary:hover {
  background: rgba(180, 205, 185, 0.2) !important;
}
details.mes_reasoning_details > summary.mes_reasoning_summary:active {
  background: rgba(180, 205, 185, 0.3) !important;
  transform: none;
}

/* 隐藏默认箭头 */
details.mes_reasoning_details > summary.mes_reasoning_summary::-webkit-details-marker,
details.mes_reasoning_details > summary.mes_reasoning_summary::marker {
  display: none;
}

/* 展开 */
details.mes_reasoning_details.open > summary.mes_reasoning_summary,
details.mes_reasoning_details.open-force > summary.mes_reasoning_summary {
  border-radius: var(--radius-l) var(--radius-l) 0 0;
}

/* Flex容器 */
details.mes_reasoning_details .flex-container {
  display: flex;
  align-items: center;
  background: transparent !important;
}

/* 标题文本区块 */
details.mes_reasoning_details .mes_reasoning_header_block {
  flex: 1;
  background: transparent !important;
  border: none !important;
  box-shadow: none !important;
  overflow: hidden;
  min-width: 0;
}

/* 标题容器 */
details.mes_reasoning_details .mes_reasoning_header {
  background: transparent !important;
  border: none !important;
  box-shadow: none !important;
  padding: 0 !important;
  margin: 0 !important;
  position: relative;
  display: flex;
  align-items: center;
  width: 100%;
}

/* 图标动画 */
details.mes_reasoning_details.open .thinking-icon,
details.mes_reasoning_details.open-force .thinking-icon {
  transform: scale(1.05);
}

/* 标题文字  */
details.mes_reasoning_details .mes_reasoning_header_title {
  font-weight: 500;
  background: transparent !important;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  padding-right: 10px;
  line-height: 1.5;
  display: inline-flex;
  align-items: center;
  color: var(--text-light); 
  padding: 13px 16px !important;
  width: 100%;
}


/* 下拉箭头 */
details.mes_reasoning_details .mes_reasoning_arrow {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  background: transparent !important;
  margin-left: 5px;
  line-height: 1;
  padding-top: 2px;
  color: rgba(130, 150, 135, 0.7); 
}


details.mes_reasoning_details .mes_reasoning_actions {
  display: inline-flex;
  align-items: center;
  opacity: 0.6; 
  transition: opacity 0.2s ease;
  background: transparent !important;
  margin-left: auto;
  flex-shrink: 0;
  margin-top: 0 !important;
  padding-right: 13px;
}

details.mes_reasoning_details .mes_reasoning_actions:hover {
  opacity: 0.9;
}

/* 按钮样式  */
details.mes_reasoning_details .mes_button,
details.mes_reasoning_details .menu_button {
  color: var(--text-light); 
  margin-left: 10px;
  cursor: pointer;
  transition: all 0.2s ease;
  background: transparent !important;
  flex-shrink: 0;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  line-height: 1;
}

details.mes_reasoning_details .mes_button:hover,
details.mes_reasoning_details .menu_button:hover {
  color: var(--accent-primary);
  transform: scale(1.1);
}

/* 内容区域  */
details.mes_reasoning_details .mes_reasoning {
  max-height: 300px;
  overflow-y: auto;
  padding: 16px;
  color: var(--text-main);
  line-height: 1.6;
  border-left: none !important;
  background: transparent; 
  width: 100%;
  box-sizing: border-box;
  transform-origin: top;
  will-change: opacity, transform, max-height;
}

/* 展开动画 */
details.mes_reasoning_details.open .mes_reasoning,
details.mes_reasoning_details.open-force .mes_reasoning {
  animation: mes_reasoning_smooth_expand 0.38s cubic-bezier(0.33, 1, 0.68, 1) forwards;
}

@keyframes mes_reasoning_smooth_expand {
  0% { opacity: 0; transform: translateY(-8px) scale(0.98); }
  40% { opacity: 0.8; }
  100% { opacity: 1; transform: translateY(0) scale(1); }
}

/* 列表样式 */
details.mes_reasoning_details .mes_reasoning ol,
details.mes_reasoning_details .mes_reasoning ul {
  padding-left: 20px;
  margin: 5px 0;
}
details.mes_reasoning_details .mes_reasoning li {
  margin-bottom: 8px;
}
details.mes_reasoning_details .mes_reasoning li:last-child {
  margin-bottom: 0;
}
details.mes_reasoning_details .mes_reasoning p {
  margin: 5px 0;
}

/* 滚动条 - 更柔和的颜色 */
details.mes_reasoning_details .mes_reasoning::-webkit-scrollbar {
  width: 6px; 
  background-color: transparent;
}
details.mes_reasoning_details .mes_reasoning::-webkit-scrollbar-thumb {
  background-color: rgba(180, 205, 185, 0.5); 
  border-radius: 3px;
  border: 1px solid transparent;
  transition: background-color 0.2s ease;
}
details.mes_reasoning_details .mes_reasoning::-webkit-scrollbar-thumb:hover {
  background-color: rgba(160, 185, 165, 0.7);
}
details.mes_reasoning_details .mes_reasoning::-webkit-scrollbar-track {
  background-color: transparent;
  margin: 3px 0;
}
details.mes_reasoning_details .mes_reasoning {
  scrollbar-width: thin;
  scrollbar-color: rgba(180, 205, 185, 0.5) transparent;
}

/* --- 动画效果)--- */

/* 思考中状态的流光效果  */
details.mes_reasoning_details.thinking > summary.mes_reasoning_summary::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 150%; 
  height: 100%;
  background: linear-gradient(
    90deg,
    transparent 0%,
    rgba(165, 195, 169, 0.03) 20%,
    rgba(165, 195, 169, 0.08) 30%,
    rgba(165, 195, 169, 0.15) 40%,
    rgba(165, 195, 169, 0.1) 50%,
    rgba(165, 195, 169, 0.08) 60%,
    rgba(165, 195, 169, 0.03) 70%,
    transparent 90%
  );
  animation: mes_reasoning_shimmer_refined 2s cubic-bezier(0.4, 0.0, 0.2, 1) infinite; 
  transform: translateX(-100%);
  pointer-events: none;
}
@keyframes mes_reasoning_shimmer_refined {
  0% { transform: translateX(-100%); opacity: 0.6; }
  50% { opacity: 0.9; }
  100% { transform: translateX(50%); opacity: 0.6; }
}

/* 思考完成时的动画效果  */
details.mes_reasoning_details.done > summary.mes_reasoning_summary {
  position: relative;
  overflow: hidden;
}
details.mes_reasoning_details.done > summary.mes_reasoning_summary::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(165, 195, 169, 0.15); 
  border-radius: inherit;
  opacity: 0;
  transform: scaleX(0);
  transform-origin: left;
  animation: mes_reasoning_expand_fade 0.5s ease-out forwards;
  pointer-events: none;
  z-index: 1;
}
@keyframes mes_reasoning_expand_fade {
  0% { transform: scaleX(0); opacity: 0; }
  60% { transform: scaleX(1); opacity: 0.7; }
  100% { transform: scaleX(1); opacity: 0; }
}

/* 引用文本 */
details.mes_reasoning_details .mes_reasoning q {
  font-style: italic;
  color: rgba(100, 120, 105, 0.8); 
}

/* 强制移除背景和边框 */
details.mes_reasoning_details .mes_reasoning_summary *,
details.mes_reasoning_details .mes_reasoning_header_block *,
details.mes_reasoning_details .mes_reasoning_header * {
  background: transparent !important;
  border: none !important;
  box-shadow: none !important;
}

/* 图标对齐 */
details.mes_reasoning_details .fa-solid {
  line-height: 1;
  display: inline-flex;
  align-items: center;
  justify-content: center;
}
/* --- 思维链组件对齐修正 --- */
details.mes_reasoning_details {
    margin-top: 80px !important;
}
details.mes_reasoning_details {
    margin-bottom: -80px !important;
}


/* ================================== */
/* 标题 H1, H2, H3  */
/* ================================== */

/* --- h1: 主要标题 --- */
h1 {
    position: relative;
    color: var(--text-main);
    padding-bottom: 15px; 
    margin-bottom: 25px;
    text-align: center; 
    font-size: 2.2em; 
    text-shadow: 1px 1px 3px rgba(165, 195, 169, 0.2);
}

h1::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 80px; 
    height: 4px;
    border-radius: 2px;
    background-image: linear-gradient(to right, var(--accent-secondary), var(--accent-primary));
    box-shadow: 0 2px 5px rgba(165, 195, 169, 0.4);
    transition: all 0.3s cubic-bezier(0.25, 0.8, 0.25, 1);
}


h1:hover::after {
    width: 120px;
    box-shadow: 0 3px 8px rgba(165, 195, 169, 0.6);
}


/* --- h2: 次级标题 --- */
h2 {
    position: relative;
    color: var(--text-main);
    padding: 10px 20px 10px 25px; 
    margin-bottom: 20px;
    font-size: 1.8em;

    background-color: rgba(224, 240, 227, 0.3);
    border-radius: var(--radius-s);
    border: 1px solid rgba(255, 255, 255, 0.5);
    transition: all 0.3s ease;
}


h2::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 10px;
    transform: translateY(-50%);
    width: 5px;
    height: 60%;
    border-radius: 2.5px;
    background-color: var(--accent-primary);
    transition: all 0.3s ease-in-out;
}


h2:hover {
    transform: translateY(-2px);
    background-color: rgba(224, 240, 227, 0.5);
    box-shadow: var(--shadow-soft);
}

h2:hover::before {
    height: 80%;
    background-color: var(--text-main);
}


/* --- h3: 三级标题 --- */

h3 {
    position: relative;
    color: var(--text-main);
    padding-left: 25px; 
    margin-bottom: 15px;
    font-size: 1.4em;
    transition: color 0.3s ease;
}

h3::before {
    content: '❖'; 
    position: absolute;
    top: 50%;
    left: 0;
    transform: translateY(-50%);
    color: var(--accent-secondary);
    font-size: 0.8em; /* 图标大小 */
    transition: all 0.3s ease;
}


h3:hover {
    color: var(--text-main); 
}

h3:hover::before {
    color: var(--accent-primary);
    transform: translateY(-50%) rotate(180deg);
}

/* ================================== */
/*文本元素美化  */
/* ================================== */

/* --- 引用块 --- */
.mes_text blockquote {
    display: block;
    margin: 10px 5px; 
    padding: 10px 10px; 
    position: relative;

    /* 核心样式 */
    background-color: rgba(255, 255, 255, 0.45); 
    backdrop-filter: blur(8px); 
    -webkit-backdrop-filter: blur(8px);
    border-radius: var(--radius-l); 
    border: 1px solid rgba(255, 255, 255, 0.6); 
    box-shadow: var(--shadow-soft); 

    /* 左侧装饰线 */
    border-left: 5px solid var(--accent-secondary);

    color: var(--text-main); 
    font-style: italic; 
    transition: all 0.3s ease;
}


.mes_text blockquote:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 16px rgba(92, 115, 96, 0.15);
    border-color: rgba(255, 255, 255, 0.8);
}


.mes_text blockquote p:first-child {
    margin-top: 0;
}
.mes_text blockquote p:last-child {
    margin-bottom: 0;
}


/* --- 行内引用  --- */
.mes_text q { 
 margin: 0 2px;
 padding: 1px 5px; 


 background-color: rgba(225, 232, 226, 0.3); 
 backdrop-filter: blur(4px);
 -webkit-backdrop-filter: blur(4px);
 border-radius: var(--radius-s); 
 border: 1px solid rgba(255, 255, 255, 0.3); 

 color: var(--text-light); 
 font-style: italic;


 quotes: none;
}
.mes_text q::before,
.mes_text q::after {
 content: none;
}

/* --- 强调文本  --- */
.mes_text strong {
    font-weight: 600; 
    color: var(--text-main);
    text-shadow: 1px 1px 3px var(--text-shadow-color);
}


/* --- 删除文本--- */
.mes_text del {
    color: var(--text-light); 
    text-decoration: none; 
    position: relative;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="100%" height="100%"><text x="0" y="12" style="font-size:14px; font-family: sans-serif; fill: rgba(165, 195, 169, 0.8);">~~~~~~~~~~~~~~~~~~~~</text></svg>');
    background-repeat: repeat-x;
    background-position: bottom;
    padding-bottom: 1px;
}


/* --- 代码块容器 (pre) --- */
.mes_text pre {
    position: relative;
    margin: 20px 0; 
    border-radius: var(--radius-m); 
    box-shadow: var(--shadow-soft); 
    border: 1px solid rgba(255, 255, 255, 0.7); 
    overflow: hidden; 


    background-color: rgba(244, 246, 243, 0.6); 
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);

    padding: 45px 0 20px 0; 
}

/* --- 代码块的“伪窗口”标题栏 --- */
.mes_text pre::before {
    content: '● ● ●'; 
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 35px; 

    background-color: rgba(225, 232, 226, 0.5); 
    border-bottom: 1px solid rgba(255, 255, 255, 0.7); 


    display: flex;
    align-items: center;
    padding-left: 15px;
    font-size: 14px;
    letter-spacing: 5px; 
    color: rgba(74, 99, 78, 0.35); 
    box-sizing: border-box; 
}


/* --- <code> 元素在 <pre> 内的样式 --- */
.mes_text pre code {

    font-family: 'Fira Code', 'JetBrains Mono', Consolas, Monaco, 'Ubuntu Mono', monospace;
    font-size: 14px;
    line-height: 1.7; 
    color: var(--text-main);

    display: block;
    overflow-x: auto;

    background: none;
    border: none;

    padding: 0 20px;
}

/* --- 代码块内部的滚动条样式 --- */
.mes_text pre code::-webkit-scrollbar {
    height: 8px; 
    width: 8px;
}
.mes_text pre code::-webkit-scrollbar-track {
    background: transparent;
}
.mes_text pre code::-webkit-scrollbar-thumb {
    background-color: rgba(165, 195, 169, 0.5); 
    border-radius: 10px;
    border: 2px solid transparent;
    background-clip: content-box;
}
.mes_text pre code::-webkit-scrollbar-thumb:hover {
    background-color: rgba(165, 195, 169, 0.8);
}

.mes_text :not(pre) > code {
    font-family: 'Fira Code', 'JetBrains Mono', Consolas, Monaco, 'Ubuntu Mono', monospace;
    background-color: rgba(216, 226, 217, 0.6);
    color: var(--text-main);
    border-radius: var(--radius-s);
    font-size: 0.9em;


    display: inline-block; 
    padding: 3px 8px;
    margin: 0 2px;
    position: relative;
    top: -1px; 


    border: 1px solid;
    border-color: rgba(255, 255, 255, 0.25) rgba(0, 0, 0, 0.06) rgba(0, 0, 0, 0.08) rgba(255, 255, 255, 0.25); 


    text-shadow: 0 1px 1px rgba(255, 255, 255, 0.6); 
    box-shadow: 0 1px 2px rgba(92, 115, 96, 0.1); 
    transition: all 0.2s ease-out; 
}

