/*
 * =================================================================
 *  2. é¡¶éƒ¨å¯¼èˆªæ  (Y2Kæ™´ç©ºè‹æ‰“é£æ ¼)
 * =================================================================
*/

/* --- 2.1 å¯¼èˆªæ å¾®è°ƒæ§åˆ¶ä¸­å¿ƒ --- */
:root {
    /* -- ğŸ® å¯¼èˆªæ ä¸»ä½“ -- */
    --y2k-bar-height: 35px;          /* å¯¼èˆªæ çš„é«˜åº¦ */
    --y2k-bar-radius: 25px;          /* å¯¼èˆªæ çš„åœ†è§’å¤§å° */
    --y2k-bar-top: 1px;              /* å¯¼èˆªæ è·ç¦»å±å¹•é¡¶éƒ¨çš„è·ç¦» */

    /* -- ğŸ® æ ¸å¿ƒé…è‰² -- */
    --y2k-main-blue: #87CEFA;       /* ä¸»è‰²è°ƒï¼šæ˜äº®çš„å¤©è“è‰² */
    --y2k-sky-blue: #B0E0E6;        /* è¾…åŠ©è‰²ï¼šæŸ”å’Œçš„ç²‰è“è‰² */
    --y2k-highlight: #FFFFFF;        /* é«˜å…‰è‰²ï¼šå¹²å‡€çš„ç™½è‰² */
    --y2k-shadow-color: rgba(65, 105, 225, 0.3); /* é˜´å½±è‰²ï¼šæ¸…æ¾ˆçš„å®è“è‰² */

    /* -- ğŸ® å›¾æ ‡ -- */
    --y2k-icon-size: 25px;           /* å›¾æ ‡çš„å¤§å° */
    --y2k-icon-vertical-align: 8px;  /* å›¾æ ‡åœ¨å¯¼èˆªæ å†…çš„ä¸Šä¸‹ä½ç½® (0pxä¸ºå±…ä¸­, æ­£æ•°ä¸‹ç§») */
    /* ğŸ‘¾ å¯¼èˆªæ å›¾æ ‡ */
    --y2k-icon-normal: url('https://i.postimg.cc/Dz0ZRHM1/IMG-8472.png');
    --y2k-icon-hover: url('https://i.postimg.cc/Dz0ZRHM1/IMG-8472.png');

    /* -- ğŸ® åº•éƒ¨è£…é¥°é“¾ -- */
    /* ğŸ‘¾ è£…é¥°é“¾å›¾ç‰‡ */
    --y2k-chain-url: url('https://i.postimg.cc/zXfkc3PG/IMG-8355.png');
    --y2k-chain-height: 50px;           /* è£…é¥°é“¾çš„é«˜åº¦ */
    --y2k-chain-bottom-offset: -47px;   /* è£…é¥°é“¾è·ç¦»å¯¼èˆªæ åº•éƒ¨çš„å‚ç›´åç§» */
}

/* --- 2.2 å¯¼èˆªæ æ ¸å¿ƒä»£ç  --- */
#top-bar {
    position: fixed;
    top: var(--y2k-bar-top);
    left: 0px;
    right: 15px;
    height: var(--y2k-bar-height) !important;
    padding: 0 15px !important;
    display: flex;
    align-items: center;
    z-index: 1000;
    background: linear-gradient(180deg, var(--y2k-sky-blue), var(--y2k-main-blue)) !important;
    border-radius: var(--y2k-bar-radius);
    border: 2px solid var(--y2k-highlight);
    box-shadow: 
        inset 0 2px 5px rgba(255, 255, 255, 0.7),
        0 5px 15px var(--y2k-shadow-color);
    transition: all 0.25s ease-out;
    border-image: none !important;
    backdrop-filter: blur(5px);
}
#top-bar:hover {
    transform: translateY(-3px);
    box-shadow: 
        inset 0 2px 5px rgba(255, 255, 255, 0.7),
        0 8px 20px var(--y2k-shadow-color),
        0 0 15px var(--y2k-sky-blue);
}
#top-bar::after {
    content: '';
    position: absolute;
    width: 100%;
    height: var(--y2k-chain-height);
    bottom: var(--y2k-chain-bottom-offset);
    top: auto;
    left: 0;
    background-image: var(--y2k-chain-url);
    background-repeat: repeat-x;
    background-position: center center;
    background-size: auto 100%;
    pointer-events: none;
    z-index: -1;
    filter: drop-shadow(0 2px 5px var(--y2k-shadow-color));
}
.drawer-icon.fa-fw {
    background-image: var(--y2k-icon-normal);
    width: var(--y2k-icon-size) !important;
    height: var(--y2k-icon-size) !important;
    background-size: contain;
    background-position: center;
    background-repeat: no-repeat;
    transition: transform 0.2s ease;
    border: none !important;
    border-radius: 0 !important;
    margin: 0 4px !important;
    transform: translateY(var(--y2k-icon-vertical-align));
    filter: drop-shadow(1px 1px 2px rgba(0,0,0,0.2));
}
.drawer-icon.fa-fw:hover,
.drawer-icon.fa-fw.openIcon {
    background-image: var(--y2k-icon-hover);
    transform: translateY(var(--y2k-icon-vertical-align)) scale(1.1);
}
.drawer-icon.fa-fw::before {
    display: none !important;
}

/* --- 2.3 å†…å®¹åŒºåŸŸé—´è·ä¿®æ­£ --- */
#chat {
