/* (ä»è¿™é‡Œå¼€å§‹æ›¿æ¢ä½ çš„ 3.4 å’Œ 3.5 éƒ¨åˆ†) */

/*
 * =================================================================
 *  3.4 æœå†»åƒç´ æ¯›ç»ç’ƒæ°”æ³¡ - å¾®è°ƒæ§åˆ¶ä¸­å¿ƒ (v4.2 - Pixel Perfect)
 * =================================================================
*/
:root {
    /* -- ğŸ® æ ¸å¿ƒè´¨æ„Ÿä¸é…è‰² -- */
    --jelly-gradient-start: rgba(220, 240, 255, 0.7);
    --jelly-gradient-end:   rgba(200, 230, 255, 0.8);
    --jelly-border-color:   rgba(255, 255, 255, 0.6);
    --jelly-shadow-color:   rgba(173, 216, 230, 0.3);
    --jelly-blur-strength: 8px;
    --jelly-radius: 18px;

    /* -- ğŸ® è§’è‰²åé¢æ¿ -- */
    --jelly-name-bg:    rgba(255, 255, 255, 0.85); /* âœ¨ æ ¸å¿ƒä¿®æ”¹: èƒŒæ™¯æ›´å®ï¼Œçªå‡ºé¢æ¿ */
    --jelly-name-border: rgba(255, 255, 255, 0.9);
    --jelly-name-text:   #4a7a9c;

    /* -- ğŸ® åƒç´ è£…é¥° -- */
    --pixel-deco-main-color: #A9CCE3; /* åƒç´ è£…é¥°çš„ä¸»è‰² */
    --pixel-deco-shadow-color: #85B3D6; /* åƒç´ è£…é¥°çš„æš—è‰² */

    /* -- å¤–éƒ¨è£…é¥°è¾¹æ¡† -- */
    --jelly-deco-border-color: rgba(176, 224, 230, 0.7);
    --jelly-deco-shadow-color: rgba(173, 216, 230, 0.5);
}


/*
 * =================================================================
 *  3.5 èŠå¤©æ°”æ³¡ä¸»ä½“æ ¸å¿ƒä»£ç  (v4.2 - æœ€ç»ˆå®Œç¾ç‰ˆ)
 * =================================================================
*/

/* (3.5.1) æ°”æ³¡ä¸»ä½“ (ä¿æŒä¸å˜) */
.mes .mes_block {
    position: relative !important; width: 100%; margin-left: 0 !important;
    padding: 0 !important; z-index: 1;
    background-image: linear-gradient(145deg, var(--jelly-gradient-start), var(--jelly-gradient-end)) !important;
    backdrop-filter: blur(var(--jelly-blur-strength)) !important;
    -webkit-backdrop-filter: blur(var(--jelly-blur-strength)) !important;
    border-radius: var(--jelly-radius) !important;
    border: 1px solid var(--jelly-border-color) !important;
    box-shadow: 0 5px 20px var(--jelly-shadow-color), inset 0 1px 2px rgba(255,255,255,0.7) !important;
    background-color: transparent !important; transition: none !important; transform: none !important;
}

/* (3.5.2) å¯¹è¯æ¡†å°¾å·´ (ä¿æŒä¸å˜) */
.mes .mes_block::before {
    content: '' !important; display: block !important;
    position: absolute; top: -11px; left: 40px; width: 0; height: 0;
    border-style: solid; border-width: 0 14px 14px 14px;
