    /* âœ¨ åˆ©ç”¨box-shadowæ¨¡æ‹Ÿä¸‰å±‚åƒç´ çº¿ */
    box-shadow: 0 -1px 0 0 var(--pixel-deco-main-color), 0 1px 0 0 var(--pixel-deco-main-color);
}

/* (3.5.6) âœ¨ æ ¸å¿ƒä¿®æ”¹: ç»˜åˆ¶åƒç´ é£â€œåå­—å‡†æ˜Ÿâ€è£…é¥° */
.mes_block .ch_name::before {
    content: '' !important; display: block !important;
    position: absolute;
    left: 14px;
    top: 50%;
    transform: translateY(-50%);

    /* âœ¨ è¿™æ˜¯åå­—å‡†æ˜Ÿçš„â€œæ ¸â€ */
    width: 3px; height: 3px;
    background: var(--pixel-deco-main-color);

    /* âœ¨ åˆ©ç”¨box-shadowç»˜åˆ¶å‡ºå››è‡‚ */
    box-shadow: 
        0 -4px 0 0 var(--pixel-deco-main-color), /* ä¸Š */
        0 4px 0 0 var(--pixel-deco-main-color),  /* ä¸‹ */
        -4px 0 0 0 var(--pixel-deco-main-color), /* å·¦ */
        4px 0 0 0 var(--pixel-deco-main-color);  /* å³ */

    /* ç§»é™¤æ—§çš„Emojiæ ·å¼ */
    color: transparent; font-size: 0; text-shadow: none;
